					}
				}
			},
			 'extension': {
				 'valueTiming': ''
			},
			 'code': {
				 'id': 10124
			},
			 'performer': {
				 'reference': {
					 'value': 'Practitioner/ordering-provider-1'
				}
			},
			 'subject': {
				 'reference': {
					 'value': 'Patient'
				}
			},
			 'effectiveElementDefinition': {
				 'extension': {
					 'url': 'http://hl7.org/fhir/v3/NullFlavor/NI'
				}
			},
			 'id': {
				 'value': 'ORC-common-order-1'
			},
			 'status': {
				 'value': 'final'
			}
		}
	}
};	


	var code    = item.code;
	var display = item.display; 

	report.resource.DiagnosticReport.id.value = index + '';
	report.resource.DiagnosticReport.request.reference.value = "DiagnosticOrder/" + index + '';

	tmp['Bundle']['entry'].push( report );	
//	logger.error( "Test: " + code + " - " + display );

	order.resource.DiagnosticOrder.item.code.coding.code.value = code;
	order.resource.DiagnosticOrder.item.code.coding.display.value = display						 
	order.resource.DiagnosticOrder.id.value = index;

	tmp['Bundle']['entry'].push( order );		

	index++;
}

