com.mirth.connect.plugins.javascriptstep.script:
// Set initial getResults SOAP query // This stays the same for all requests
var getResultsSoap = <soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:res="http://medplus.com/results"> <soapenv:Header/> <soapenv:Body> <res:getResults> <res:RetrieveResultsRequest> <requestParameters> <parameterName>maxMessages</parameterName> <parameterValue>900</parameterValue> </requestParameters> <resultServiceType>HL7</resultServiceType> </res:RetrieveResultsRequest> </res:getResults> </soapenv:Body> </soapenv:Envelope>;
getResultsSoap.*::Body.*::getResults.*::RetrieveResultsRequest.requestParameters.parameterValue = $cfg("maxLRIMessages");

try {

	var ackORU = [];
	var isMore = false;
	var controlIdList = [];	
	var requestId;

	// Placeholder for doPost() response SOAP, namespaces are preserved
	var soapResp;	

	do {

		var httpCode = doPost(getResultsSoap, $cfg("getLRIResultsURL"));

		if (httpCode == 200) {		

			// Mar 28 2017: To add to Custom Metadata
			channelMap.put("GetResultsResponse", soapResp.toString());

			// The HTML error response with <!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.0 Draft//EN"> is possible.
			// This causes E4X serialization problem
			if (soapResp.indexOf("!DOCTYPE") > -1) {			
				var error = soapResp.substring(stringResponse.indexOf("<TITLE>"), stringResponse.indexOf("</TITLE>") + 8);
				throw ("LRI GetResults: Destination - HTTP ERROR: " + error);

			} else {

				soapResp = new XML(soapResp.toString());				
				if (soapResp.*::Body.*::getResultsResponse.*::Results) {
					// Extracts isMore indicator (whether or not more results are available from the DEX to be returned)
					isMore = (soapResp.*::Body.*::getResultsResponse.*::Results.isMore.toString().toLowerCase() == "true");
					// Extracts the request ID to identify the results to be acknowledged
					requestId = soapResp.*::Body.*::getResultsResponse.*::Results.requestId.toString() || "";
				}
			}

			// Mar 27 2017 - Quest getResultsResponse always has isMore = "true", which cause an infinite loop, Issue #18
			// FIX: maxMessages set to 900, isMore set to false
			isMore = false;						

			processResults(soapResp);

		} else if (httpCode >= 300 && httpCode < 400) {
			// 3xx Redirection
			throw ("LRI GetResults: Destination - HTTP " + getHttpCodeDescription(httpCode));			

		} else if (httpCode >= 400 && httpCode < 500) {
			// 4xx Client Error
			// TODO: Attempt to resend the same message and then throw the exception
			throw ("LRI GetResults: Destination - HTTP " + getHttpCodeDescription(httpCode));

		} else if (httpCode >= 500) {
			// 5xx Server Error
			throw ("LRI GetResults: Destination - HTTP " + getHttpCodeDescription(httpCode));			
		}

	} while (isMore);

	// Check if ackORU Array is not empty
	if (ackORU.length > 0) {

		var httpCode = doPost(getAckSoap(), $cfg("getLRIResultsURL"));

		if (httpCode == 200) {
			// Ignore DEX ACK on ACK response
			// TODO: May need to handle those as well to verify isMore parameter

			// Mar 28 2017: To add to Custom Metadata
			channelMap.put("AcknowledgeResultsResponse", soapResp.toString());

		} else {
			throw ("LRI GetResults: Destination (ACK) - HTTP " + getHttpCodeDescription(httpCode));
		}
	}	

} catch(ex) {
	// TODO: Better logging if failed
	if (soapResp) {
		logger.error(ex + "  getResultsResponse: " + soapResp);
		throw (ex + "  getResultsResponse: " + soapResp);
	} else {
		logger.error(ex);
		throw (ex);		
	}

} finally {
	controlIdList.length = 0;
	ackORU.length = 0;
}

/**
* Submits the HTTP POST request method 
* @param msgSoap - SOAP message to be submitted in the HTTP body
* @param url - the URL HTTP POST to be submitted to
*/
function doPost(msgSoap, url) {

	var httpClient = globalChannelMap.get("httpClientClass");

	if (httpClient == null) return 0;

	if (httpClient) {

		httpClient.setURL(url);
		httpClient.setSOAPRequest(msgSoap);
		httpCode = httpClient.doPost();

		// Preserve namespaces
		soapResp = httpClient.getSOAPResponse(true);

		return httpCode;

	} else {
		return 0;
	}

}





/**
* Extracts, decodes and routes ORU_R01 messages
* @param soapResp - getResultsResponse SOAP message from DEX
*/
function processResults(soapResp) {

	if (soapResp.*::Body.*::getResultsResponse.*::Results.toString().length > 0) {
		for each (seg in soapResp.*::Body.*::getResultsResponse.*::Results.children()) {			
			if (seg.name().toString() == "results") {

				// Route the HL7v2 message to ORU_R01 processing channel
				var hl7v251ORU = new java.lang.String(FileUtil.decode(seg.HL7Message['message'].toString()));

				var ORUProcessingChannelId = globalMap.get("ORUProcessingChannelId");
				if (ORUProcessingChannelId) {
					router.routeMessageByChannelId(ORUProcessingChannelId, hl7v251ORU);
				} else {
					logger.error("LRI GetResults: ORU_Processing channel ID is not found. Message cannot be routed.");
				}

				// Extract MSH.10 [Message Control ID] of the actual ORU_R01 message
				var xmlORU = new XML(SerializerFactory.getSerializer('HL7V2').toXML(hl7v251ORU));				
				var msgCtrlID = xmlORU['MSH']['MSH.10']['MSH.10.1'].toString();
				controlIdList.push(msgCtrlID);

				// Create HL7v2 ACK message to acknowledge this ORU_R01
				var hl7v2Ack = "MSH|^~\&||||STL|||ACK|||2.5.1|||AL|NE|||||LRI_NG_RN_Profile^^2.16.840.1.113883.9.20^ISO" + '\n' +
				"MSA|CA|";
				var xmlAck = new XML(SerializerFactory.getSerializer('HL7V2').toXML(hl7v2Ack));

				xmlAck['MSH']['MSH.3']['MSH.3.1'] = $cfg("application");
				xmlAck['MSH']['MSH.4']['MSH.4.1'] = $cfg("account");
				xmlAck['MSH']['MSH.6']['MSH.6.1'] = "STL";

				// Time Zone is not used (otherwise use "yyyyMMddHHmmssZ")
				var currentDate = DateUtil.getCurrentDate("yyyyMMddHHmmss");
				xmlAck['MSH']['MSH.7']['MSH.7.1'] = DateUtil.getCurrentDate("yyyyMMddHHmmss"); // or "yyyyMMddHHmmssZ"
				xmlAck['MSH']['MSH.10']['MSH.10.1'] = currentDate + UUIDGenerator.getUUID().toString().substring(0,6);
				xmlAck['MSH']['MSH.11']['MSH.11.1'] = $("processingID");

				// The message control ID returned in MSH.10 (Message Control ID) from the result message.
				xmlAck['MSA']['MSA.2']['MSA.2.1'] = msgCtrlID;

				// Save ACK response for acknoledgement
				ackORU.push(SerializerFactory.getSerializer('HL7V2').fromXML(xmlAck));					

			}
		}
	} 	
}


/**
* Constructs ACK SOAP message using global variables
*/
function getAckSoap() {

	var ackSoap = <soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:res="http://medplus.com/results">
	   <soapenv:Header/>
	   <soapenv:Body>
	      <res:acknowledgeResults>
	         <res:RetrieveResultsAcknowledge/>
	      </res:acknowledgeResults>
	   </soapenv:Body>
	</soapenv:Envelope>;

	for (var i =0; i < controlIdList.length; i++) {
		var ackMessage = <ackMessages><controlId></controlId><message></message></ackMessages>;
		ackMessage.controlId = controlIdList[i];

		// Encode HL7v2 ACK message
		var bytes = new java.lang.String(ackORU[i].toString()).getBytes();
		ackMessage['message'] = FileUtil.encode(bytes).replaceAll('\r\n|\r|\n', '');

		ackSoap.*::Body.*::acknowledgeResults.*::RetrieveResultsAcknowledge.appendChild(ackMessage);
	}

	ackSoap.*::Body.*::acknowledgeResults.*::RetrieveResultsAcknowledge.requestId = requestId;
	ackSoap.*::Body.*::acknowledgeResults.*::RetrieveResultsAcknowledge.resultServiceType = "hl7";

	// Mar 28 2017: To add to Custom Metadata
	channelMap.put("AcknowledgeResults", ackSoap.toString());

	return ackSoap;
}

