com.mirth.connect.plugins.javascriptstep.script:
// Different HL7v2.x.x versions will be processed differently
var version = msg['MSH']['MSH.12']['VID.1'].toString();

channelMap.put("MessageControlID", msg['MSH']['MSH.10'].toString());

if ("2.5.1" == version) {
	destinationSet.removeAllExcept(["ORU_R01_2.5.1","FHIR_submit"]);

} else if ("2.3.1" == version || "2.3" == version) {
	destinationSet.removeAllExcept(["ORU_R01_2.3.x","FHIR_submit"]);

} else {
	logger.error("ORU Processing: unknown ORU_R01 message version (MSH.12) - " + version);
	destinationSet.removeAllExcept(["FHIR_submit"]);
}

com.mirth.connect.plugins.javascriptrule.script:
// Continue processing only if the incoming message is ORU_R01
if("ORU" == msg['MSH']['MSH.9']['MSG.1'].toString() && "R01" == msg['MSH']['MSH.9']['MSG.2'].toString()) {
	return true;
} else {
	logger.debug("ORU Processing: non-ORU message is rejected " + msg.toString());
	return false;
}

