var arrDiagnosticReport = [];
var arrOrderingProvider = [];
var arrProviderID = [];

/*
* (SN) Practitioner resource for Performing Organization or Medical Doctor is not mapped (see comments below)
*
*/ 

for each (var observation in msg['ORU_R01.PATIENT_RESULT'].children()) {

	var resDiagnosticReport = <DiagnosticReport>
							<id value=""/>
							<text>
								<status value="generated"/>
								<xhtml:div xmlns:xhtml="http://www.w3.org/1999/xhtml"/>
							</text>
						</DiagnosticReport>;

	// Process only ORC-OBR-OBX group
	if (observation['ORC']['ORC.1'].toString() != "RE") { continue; }

	resDiagnosticReport.id.@value = observation['ORC']['ORC.3']['EI.1'].toString(); // Filler Order ID

	// Process Observation Request (OBR) level NTE segments
	// (SN) Known deficiency - "p" element namespace is not bound, must be "xhtml:p" 
	// March 14 2017: Issue #3 "Comments appear twice" fixed 
	resDiagnosticReport.text.*::div.* = new XMLList();
	var observationComment = false;	

	// Extract ORC-OBR-OBX group related comments only
	var orderObservationGroup = new XML(observation.toString());

	// March 14: This returns all children and grandchildren NTE segments of the this object
	for each (var nte in orderObservationGroup.descendants("NTE")) { 

		if ("NTE" == nte.name().toString()) {

			observationComment = true;

			// March 14, 2017: Comment source depends on the group and appended as a prefix 
			var prefix = "";
			if (nte.parent().name().toString().indexOf(".ORDER_OBSERVATION") > -1) {
				prefix = "OBR";
			} else if (nte.parent().name().toString().indexOf(".OBSERVATION") > -1) {
				prefix = "OBX";
			}

			// Source of Comment - Optional
			var commentSources = ["L","P","O"];
			var source = nte['NTE.2'].toString();

			// Comments contained in the segment - Required
			var comment = nte['NTE.3'].toString();

			if (commentSources.indexOf(source) > -1) {	
				resDiagnosticReport.text.*::div.* += new XML('<p class="' + prefix + '-' + source + '">' + stripNonValidXMLChars(comment) +'</p>');
			} else {
				resDiagnosticReport.text.*::div.* += new XML('<p class="' + prefix + '">' + stripNonValidXMLChars(comment) +'</p>');	
			}
		}
	}

	if (!observationComment) delete resDiagnosticReport.text;

	// Process single OBR segment
	resDiagnosticReport.appendChild(new XML("<contained>" + getDiagnosticOrder(observation) + "</contained>"));

	// Process Ordering Provider
	// (SN) Add only if such Ordering Provider is not in the list already
	var provID =  observation['ORC']['ORC.12']['XCN.1'].toString();
	if (arrProviderID.indexOf(provID) < 0) {
		arrOrderingProvider.push(getOrderingProvider(observation));
		arrProviderID.push(provID);
	}

	// Process multiple OBX segments
	for each (var obs in observation['ORU_R01.OBSERVATION'].children()) {
		if ("OBX" == obs.name().toString()) {

			// OBX segments nested under DiagnosticReport as contained resources
			resDiagnosticReport.appendChild(new XML("<contained>" + getObservationResource(obs) + "</contained>"));

			// March 14 2017: Process Performing Organization: OBX.23, OBX.24, OBX.25
			var performingOrganization = new XML( getPerformingProvider(obs) );
			if (performingOrganization.contained.Organization) {
				var performID = performingOrganization.contained.Organization.identifier.value.@value.toString();
				if (arrProviderID.indexOf(performID) < 0) {
					arrOrderingProvider.push(performingOrganization.toString());
					arrProviderID.push(performID);
				}
			}
		}	
	}	

	// Placer Order Number - If populated, this field contains the placer applications order number.
	if (observation['ORC']['ORC.2']['EI.1'].toString()) {
		var identifier1 = <identifier>
						<type id="99QDI"/>
						<system value=""/>
						<value value=""/>
					</identifier>;
		identifier1.value.@value = observation['ORC']['ORC.2']['EI.1'].toString();
		identifier1.system.@value = observation['ORC']['ORC.2']['EI.2'].toString();
		resDiagnosticReport.appendChild(identifier1);
	}

	// Filler Order Number - The order number associated with the fillers application. 
	if (observation['ORC']['ORC.3']['EI.1'].toString()) {
		var identifier2 = <identifier>
						<type id="99QDI"/>
						<system value=""/>
						<value value=""/>
					</identifier>;
		identifier2.value.@value = observation['ORC']['ORC.3']['EI.1'].toString();
		identifier2.system.@value = observation['ORC']['ORC.3']['EI.2'].toString();
		resDiagnosticReport.appendChild(identifier2);		
	}

	// Order Status - Indicates whether the result is partial or final.
	// March 25 2017: updated to pass API validation (see Terminology Mapping spreadsheet)
	var statusCode = observation['ORC']['ORC.5'].toString();
	if (statusCode) {
		var statusXML = <status value="final">
						<extension url="http://hl7.org/fhir/ValueSet">
							<valueCodeableConcept>
								<coding>
									<system value="http://hl7.org/fhir/ValueSet/v2-0038"/>
									<code value=""/>
									<display value=""/>
								</coding>
							</valueCodeableConcept>
						</extension>
					</status>;
		statusXML.extension.valueCodeableConcept.coding.code.@value = statusCode;
		statusXML.extension.valueCodeableConcept.coding.display.@value = getHL7v2CodeDescription("0038", statusCode);
		resDiagnosticReport.appendChild(statusXML);
	}

	resDiagnosticReport.code.@id = observation['ORC']['ORC.3']['EI.1'].toString();

	// Hard-coded, required by FHIR
	resDiagnosticReport.appendChild(new XML('<subject><reference value="Patient"/></subject>'));

	// Observation Date/Time
	resDiagnosticReport.effectiveDateTime.@value = convertHL7DateTime(observation['OBR']['OBR.7']['TS.1'].toString());
	resDiagnosticReport.issued.@value = convertHL7DateTime(observation['OBR']['OBR.7']['TS.1'].toString());

	var performer = <performer>
					<reference value="Practitioner"/>
				</performer>;
	var orderingProviderID = observation['ORC']['ORC.12']['XCN.1'].toString();
	if (orderingProviderID) {
		performer.reference.@value = "Practitioner/" + observation['ORC']['ORC.12']['XCN.1'].toString();
	} else {
		performer.reference.@value = "Practitioner";
	}
	resDiagnosticReport.appendChild(performer);

	// Reference to speciments this report is based on
	for each (var specimen in observation['ORU_R01.SPECIMEN'].children()) {
		var id = "Specimen/spm-specimen-" + specimen['SPM.2']['EIP.1']['EI.1'].toString() + "-" + specimen['SPM.2']['EIP.2']['EI.1'].toString();
		var specimenReference = <specimen>
								<reference value=""/>
							</specimen>;
		specimenReference.reference.@value = id;
		resDiagnosticReport.appendChild(specimenReference);		
	}

	// Reference to observation this report is based on
	for each (var obs in observation['ORU_R01.OBSERVATION'].children()) {
		if ("OBX" == obs.name().toString() && obs['OBX.3']['CE.1'].toString() != "") {
			var id = "Observation/obx-observation-result-" + obs['OBX.3']['CE.1'].toString();
			var resultReference = <result>
									<reference value=""/>
								</result>;
			resultReference.reference.@value = id;
			resDiagnosticReport.appendChild(resultReference);	
		}	
	}

	logger.error( "Diagnostic Report\n:" +  resDiagnosticReport.toString() );

	// ~~ STEPS: 
	// 1. Query for DiagnosticOrder (mostly for the resource ID)
	// 2. Pull array of Observations for each Order/Report
	// 3. Put Observations into Diagnostic Report
	// 4. Assemble array of Attachments
	// 5. Populate Conclusion
	// 4. Insert Diagnostic Report
	//
	// NOTE: Decide if we really need specimen, performer

	arrDiagnosticReport.push(resDiagnosticReport.toString());
}

// Store DiagnosticReport resource to be processed by FHIR_Submit destination
channelMap.put("DiagnosticReport", arrDiagnosticReport);

// Store Practitioner resource to be processed by FHIR_Submit destination
channelMap.put("Practitioner", arrOrderingProvider);

/**
	Creates the DiagnosticOrder resourse with OBR segment mappings

	@param {String} obs - original ORU_R01.ORDER_OBSERVATION XML string
	@return {String} return DiagnosticOrder XML string
*/
function getDiagnosticOrder(obs) {

/*
(SN) Following fields are not mapped in this version:
* OBR.26 [Parent Result] - Optional - If OBR.11 (Specimen Action Code) is populated, then this field is populated with the data in the parent OBX.03 (Observation Identifier) subfield.
* OBR.29 [Parent] - Optional - This field is populated if OBR.11 (Specimen Action Code) is populated.
* OBR.50 [Parent Universal Service Identifier] - Optional - The universal service identifier of the parent order. This field is populated if OBR.11 (Specimen Action Code) is populated.
*/
	var diagnosticOrder = <DiagnosticOrder>
					<id value="obr-observation-request"/>
					<extension url="http://hl7.org/fhir/StructureDefinition/diagnosticorder-precondition">
						<valueDateTime value=""/>
					</extension>
					<subject id="Patient"/>
					<event>
						<status value="completed">
							<extension url="http://hl7.org/fhir/ValueSet">
								<valueCodeableConcept>
									<coding>
										<system value="http://hl7.org/fhir/ValueSet/v2-0123"/>
										<code value=""/>
										<display value=""/>
									</coding>
								</valueCodeableConcept>
							</extension>
						</status>
						<dateTime value=""/>
						<actor>
							<reference value="Practitioner/ordering-provider"/>
						</actor>
					</event>
					<item>
						<code>
							<coding>
								<system value=""/>
								<code value=""/>
								<display value=""/>
							</coding>
						</code>
						<specimen>
							<display value="G"/>
						</specimen>
					</item>
				</DiagnosticOrder>;

	diagnosticOrder.extension.valueDateTime.@value = convertHL7DateTime(obs['OBR']['OBR.7']['TS.1'].toString());

	var resultHandling = obs['OBR']['OBR.49'].toString();
	if (resultHandling) {
		diagnosticOrder.supportingInformation.display.@value = obs['OBR']['OBR.49'].toString();
	} else {
		delete diagnosticOrder.supportingInformation;
	}

	// Result Status - Indicates whether the result is partial or final.
	// March 25 2017: updated to pass API validation (see Terminology Mapping spreadsheet)	
	var statusCode = obs['OBR']['OBR.25'].toString();
	if (statusCode) {
		 diagnosticOrder.event.status.extension.valueCodeableConcept.coding.code.@value = statusCode;
		 diagnosticOrder.event.status.extension.valueCodeableConcept.coding.display.@value = getHL7v2CodeDescription("0123", statusCode);
	}

	//  The date/time the results were reported or status changed
	diagnosticOrder.event.dateTime.@value = convertHL7DateTime(obs['OBR']['OBR.22']['TS.1'].toString());

	// Quest samples contain local and LOINC codes
	diagnosticOrder.item.code.coding.system.@value = obs['OBR']['OBR.4']['CE.3'].toString();
	diagnosticOrder.item.code.coding.code.@value = obs['OBR']['OBR.4']['CE.1'].toString();
	diagnosticOrder.item.code.coding.display.@value = obs['OBR']['OBR.4']['CE.2'].toString();

	// Specimen Action Code
	var specimenActionCode = obs['OBR']['OBR.11'].toString();
	if (specimenActionCode) {
		diagnosticOrder.item.specimen.display.@value = specimenActionCode;
	} else {
		delete diagnosticOrder.item.specimen;
	}

	return diagnosticOrder.toString();
}


/**
	Creates the Observation resourse with a single OBX segment mappings

	@param {String} obs - original ORU_R01.OBSERVATION XML string
	@return {String} return Observation XML string
*/
function getObservationResource(obs) {

	var obsResource = <Observation>
					<id value="OBX-observation-result"/>					
					<identifier id=""/>
					<status value="final">
						<extension url="http://hl7.org/fhir/ValueSet">
							<valueCodeableConcept>
								<coding>
									<system value="http://hl7.org/fhir/ValueSet/v2-0085"/>
									<code value="F"/>
									<display value="Final results; can only be changed with a corrected result"/>
								</coding>
							</valueCodeableConcept>
						</extension>
					</status>
					<code>
						<extension url="http://www.questdiagnostics.com">
							<valueCoding>
								<system value=""/>
								<code value=""/>
								<display value=""/>
							</valueCoding>
						</extension>
						<coding>
							<system value="http://loinc.org"/>
							<code value=""/>
							<display value=""/>
						</coding>
					</code>
					<effectiveDateTime value=""/>
					<issued value=""/>
					<performer>
						<reference value="Practitioner/obx-performing-organization"/>
					</performer>
					<valueString value=""/>
					<interpretation>
						<text value=""/>
					</interpretation>
					<referenceRange>
						<meaning>
							<coding>
								<system value=""/>
								<code value=""/>
								<display value=""/>
							</coding>
						</meaning>
						<text value=""/>
					</referenceRange>
				</Observation>;

	obsResource.id.@value="OBX-observation-result-" + obs['OBX.3']['CE.1'].toString();

	obsResource.identifier.@id = obs['OBX.1'].toString();

	// Result Status - the status of the result value
	// March 25 2017: updated to pass API validation (see Terminology Mapping spreadsheet)	
	var statusCode = obs['OBX.11'].toString();
	if (statusCode) {
		 obsResource.status.extension.valueCodeableConcept.coding.code.@value = statusCode;
		 obsResource.status.extension.valueCodeableConcept.coding.display.@value = getHL7v2CodeDescription("0085", statusCode);
	}



	if (obs['OBX.3']['CE.6'].toString() == "LN") {
		// Care360 own coding
		obsResource.code.extension.valueCoding.system.@value = obs['OBX.3']['CE.3'].toString();
		obsResource.code.extension.valueCoding.code.@value = obs['OBX.3']['CE.1'].toString();
		obsResource.code.extension.valueCoding.display.@value = obs['OBX.3']['CE.2'].toString();

		// LOINC coding
		obsResource.code.coding.system.@value = "http://loinc.org";
		obsResource.code.coding.code.@value = obs['OBX.3']['CE.4'].toString();
		obsResource.code.coding.display.@value = obs['OBX.3']['CE.5'].toString();		
	} else {
		delete obsResource.code.extension;
		obsResource.code.coding.system.@value = obs['OBX.3']['CE.3'].toString();
		obsResource.code.coding.code.@value = obs['OBX.3']['CE.1'].toString();
		obsResource.code.coding.display.@value = obs['OBX.3']['CE.2'].toString();			
	}

	var dateTimeOfObservation = obs['OBX.14']['TS.1'].toString();
	if (dateTimeOfObservation) {
		obsResource.effectiveDateTime.@value = convertHL7DateTime(dateTimeOfObservation);
	} else {
		delete obsResource.effectiveDateTime;
	}

	var dateTimeOfAnalysis = obs['OBX.19']['TS.1'].toString();
	if (dateTimeOfAnalysis) {
		obsResource.issued.@value = convertHL7DateTime(dateTimeOfAnalysis);
	} else {
		delete obsResource.issued;
	}

	// March 15: Issues #13: OBX.5 [Observation Value] using SN (Structured Numeric) data type
	var observationValue = "";
	if (obs['OBX.5']['SN.3']) {
		for each (var sn in obs['OBX.5'].children()) {
			if (sn.toString()) observationValue += sn.toString(); 
		}
	} else {
		observationValue = obs['OBX.5'].toString();
	}

	if (observationValue) {
		obsResource.valueString.@value = observationValue;
	} else {
		delete obsResource.valueString;
	}

	var abnormalFlags = obs['OBX.8'].toString();
	if (abnormalFlags) {
		obsResource.interpretation.text.@value = abnormalFlags;
	} else {
		delete obsResource.interpretation;
	}

	var unitsOfMeasure = obs['OBX.6']['CE.1'].toString();
	var meaningPresent = true;
	if (unitsOfMeasure) {
		obsResource.referenceRange.meaning.coding.code.@value = unitsOfMeasure;
		if (obs['OBX.6']['CE.3'].toString() != "") {
			obsResource.referenceRange.meaning.coding.system.@value = obs['OBX.6']['CE.3'].toString();
		} else {
			delete obsResource.referenceRange.meaning.coding.system;
		}
		if (obs['OBX.6']['CE.2'].toString() != "") {
			obsResource.referenceRange.meaning.coding.display.@value = obs['OBX.6']['CE.2'].toString();
		} else {
			delete obsResource.referenceRange.meaning.coding.display;
		}
	} else {
		delete obsResource.referenceRange.meaning;
		meaningPresent = false;
	}

	var referencesRange = obs['OBX.7'].toString();
	var rangePresent = true;
	if (referencesRange) {
		obsResource.referenceRange.text.@value = referencesRange;
	} else {
		delete obsResource.referenceRange.text;
		rangePresent = false;
	}

	if (!meaningPresent && !rangePresent) delete obsResource.referenceRange;

	// Reference to performing organization
	obsResource.performer.reference.@value="Practitioner/obx-performing-organization-" + obs['OBX.23']['XON.10'].toString();

	return obsResource.toString();

}

/**
	Creates the Practitioner resourse for Ordering Provider with ORC segment mappings

	@param {String} obs - original ORU_R01.ORDER_OBSERVATION XML string
	@return {String} return Practitioner XML string
*/
function getOrderingProvider(obs) {

	// Mar 16 update: /Practitioner/practitionerRole/organization is changed to 
	// /Practitioner/practitionerRole/managingOrganization - tooling issue on the "left side"
	// Note: this breaks STU3 1.4.0 XML Schema validation

	var resPractitioner = <Practitioner>
						<id value="ordering-provider"/>
						<contained>
							<Organization>
								<id value="orc21-ordering-facility-1"/>
								<identifier>
									<value value=""/>
								</identifier>
								<name value=""/>
								<telecom>
									<system value="phone"/>
									<value value=""/>
									<use value="work"/>
								</telecom>
								<address>
									<line value=""/>
									<city value=""/>
									<state value=""/>
									<postalCode value=""/>
								</address>
							</Organization>
						</contained>
						<identifier>
							<system value="NPI"/>
							<value value=""/>
						</identifier>
						<name/>
						<practitionerRole>
							<managingOrganization>
								<reference value="Organization/orc21-ordering-facility-1"/>
							</managingOrganization>
						</practitionerRole>
					</Practitioner>;

	// Practitioner's own ID
	resPractitioner.identifier.value.@value = obs['ORC']['ORC.12']['XCN.1'].toString();
	resPractitioner.identifier.system.@value = obs['ORC']['ORC.12']['XCN.13'].toString();

	// Organization this Practitioner belongs to
	resPractitioner.contained.Organization.identifier.value.@value = obs['ORC']['ORC.21']['XON.10'].toString();
	resPractitioner.contained.Organization.name.@value = obs['ORC']['ORC.21']['XON.1'].toString();

	// Organization telecom
	var telecom = "(" + obs['ORC']['ORC.23']['XTN.6'].toString() + ") " + convertPhone(obs['ORC']['ORC.23']['XTN.7'].toString());
	if (telecom.length > 4) {
		resPractitioner.contained.Organization.telecom.value.@value = telecom;
	} else delete resPractitioner.contained.Organization.telecom;

	// Organization address
	var addressInUse = false;
	var line = obs['ORC']['ORC.22']['XAD.1']['SAD.1'].toString();
	if (line) {
		addressInUse = true;
		resPractitioner.contained.Organization.address.line.@value = line;
	}

	var city = obs['ORC']['ORC.22']['XAD.3'].toString();
	if (city) {
		addressInUse = true;		
		resPractitioner.contained.Organization.address.city.@value = city;
	}

	var state = obs['ORC']['ORC.22']['XAD.4'].toString();
	if (state) {
		addressInUse = true;		
		resPractitioner.contained.Organization.address.state.@value = state;
	}	

	var postalCode = obs['ORC']['ORC.22']['XAD.5'].toString();
	if (postalCode) {
		addressInUse = true;		
		resPractitioner.contained.Organization.address.postalCode.@value = postalCode;
	}

	if (!addressInUse) delete resPractitioner.contained.Organization.address;

	// Practitioner name
	var familyName = obs['ORC']['ORC.12']['XCN.2']['FN.1'].toString();
	if (familyName) {
		resPractitioner.name.* = new XMLList();
		resPractitioner.name.* += new XML('<family value="' + familyName +'"/>');
		if (obs['ORC']['ORC.12']['XCN.3'].toString())
			resPractitioner.name.* += new XML('<given value="' + obs['ORC']['ORC.12']['XCN.3'].toString() +'"/>');
		if (obs['ORC']['ORC.12']['XCN.4'].toString())
			resPractitioner.name.* += new XML('<given value="' + obs['ORC']['ORC.12']['XCN.4'].toString() +'"/>');	
	} else {
		delete resPractitioner.name;
	}

	return resPractitioner.toString();	
}

/**
	Creates the Practitioner resourse for OBX.23 [Performing Organization Name],
	OBX.24 [Performing Organization Address] and OBX.25[Performing Organization Medical Director] for each OBX segment

	@param {String} obs - original ORU_R01.ORDER_OBSERVATION XML string
	@return {String} return Practitioner XML string
*/
function getPerformingProvider(obs) {

	var resPractitioner = <Practitioner>
						<id value="obx-performing-organization"/>
						<contained>
							<Organization>
								<identifier>
									<value value=""/>
									<assigner id="CLIA"/>
								</identifier>
								<type>
									<text value="FI"/>
								</type>
								<name value=""/>
								<address>
									<line value=""/>
									<city value=""/>
									<state value=""/>
									<postalCode value=""/>
								</address>
							</Organization>
						</contained>
						<identifier>
							<type>
								<text value="NPI"/>
							</type>
							<value value=""/>
							<assigner id="EN"/>
						</identifier>
						<name/>
					</Practitioner>;

	// Performing Organization Name
	resPractitioner.id.@value = "obx-performing-organization-" + obs['OBX.23']['XON.10'].toString();
	resPractitioner.contained.Organization.identifier.value.@value = obs['OBX.23']['XON.10'].toString();
	resPractitioner.contained.Organization.name.@value = obs['OBX.23']['XON.1'].toString();


	// Performing Organization Address
	var addressInUse = false;
	var line = obs['OBX.24']['XAD.1']['SAD.1'].toString();
	if (line) {
		addressInUse = true;
		resPractitioner.contained.Organization.address.line.@value = line;
	}

	var city = obs['OBX.24']['XAD.3'].toString();
	if (city) {
		addressInUse = true;		
		resPractitioner.contained.Organization.address.city.@value = city;
	}

	var state = obs['OBX.24']['XAD.4'].toString();
	if (state) {
		addressInUse = true;		
		resPractitioner.contained.Organization.address.state.@value = state;
	}	

	var postalCode = obs['OBX.24']['XAD.5'].toString();
	if (postalCode) {
		addressInUse = true;		
		resPractitioner.contained.Organization.address.postalCode.@value = postalCode;
	}

	if (!addressInUse) delete resPractitioner.contained.Organization.address;


	// Medical Director
	resPractitioner.identifier.value.@value = obs['OBX.25']['XCN.1'].toString();
	resPractitioner.identifier.assigner.@id = obs['OBX.25']['XCN.14']['HD.1'].toString()

	// Medical Director Name
	var familyName = obs['OBX.25']['XCN.2']['FN.1'].toString();
	if (familyName) {
		resPractitioner.name.* = new XMLList();
		resPractitioner.name.* += new XML('<family value="' + familyName +'"/>');
		if (obs['OBX.25']['XCN.3'].toString())
			resPractitioner.name.* += new XML('<given value="' + obs['OBX.25']['XCN.3'].toString() +'"/>');
		if (obs['OBX.25']['XCN.4'].toString())
			resPractitioner.name.* += new XML('<given value="' + obs['OBX.25']['XCN.4'].toString() +'"/>');
		if (obs['OBX.25']['XCN.5'].toString())
			resPractitioner.name.* += new XML('<suffix value="' + obs['OBX.25']['XCN.5'].toString() +'"/>');				
	} else {
		delete resPractitioner.name;
	}


	return resPractitioner.toString();		
}

var resBundle = <Bundle xmlns="http://hl7.org/fhir" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance">
		<type value="message"/>
	</Bundle>;

// Patient resource
