resBinary.content.@value = FileUtil.encode(bytes).replaceAll('\r\n|\r|\n', '');

// Store to be processed by the FHIR_submit destination
globalChannelMap.put("Binary", resBinary.toString());


return message;

com.mirth.connect.plugins.javascriptstep.script:
// Different HL7v2.x.x versions will be processed differently
var version = msg['MSH']['MSH.12']['VID.1'].toString();

channelMap.put("MessageControlID", msg['MSH']['MSH.10'].toString());

if ("2.5.1" == version) {
	destinationSet.removeAllExcept(["ORU_R01_2.5.1","FHIR_submit"]);

} else if ("2.3.1" == version || "2.3" == version) {
	destinationSet.removeAllExcept(["ORU_R01_2.3.x","FHIR_submit"]);

} else {
	logger.error("ORU Processing: unknown ORU_R01 message version (MSH.12) - " + version);
	destinationSet.removeAllExcept(["FHIR_submit"]);
}

var bytes = new java.lang.String(SerializerFactory.getSerializer('HL7V2').fromXML(msg) ).getBytes();
channelMap.put( "HL7Base64", FileUtil.encode(bytes).replaceAll('\r\n|\r|\n', '') );

com.mirth.connect.plugins.javascriptrule.script:
// Continue processing only if the incoming message is ORU_R01
if("ORU" == msg['MSH']['MSH.9']['MSG.1'].toString() && "R01" == msg['MSH']['MSH.9']['MSG.2'].toString()) {
	return true;
} else {
	logger.error("ORU Processing: non-ORU message is rejected " + msg.toString());
	return false;
}

com.mirth.connect.plugins.javascriptstep.script:
// Mar 16: <value value=""/> removed from identifier


var resPatient = <Patient>
			<id value="PID-patient-identification"/>
			<text>
				<status value="generated"/>
				<xhtml:div xmlns:xhtml="http://www.w3.org/1999/xhtml"/>
			</text>			
			<identifier>
				<use value="official"/>				
			</identifier>
			<name>
				<use value="official"/>
				<family value=""/>
			</name>
				<telecom>
					<system value="phone"/>
					<value value=""/>
					<use value="home"/>
				</telecom>
			<gender value=""/>
			<birthDate value=""/>
			<address>
				<use value="home"/>
			</address>
		</Patient>;


// Process NTE segments
// (SN) Known deficiency - "p" element namespace is not bound, must be "xhtml:p"  
resPatient.text.*::div.* = new XMLList();
var 	patientComment = false;
for each (var nte in msg['ORU_R01.PATIENT_RESULT']['ORU_R01.PATIENT'].children()) {
	if ("NTE" == nte.name().toString()) {

		patientComment = true;

		// Source of Comment - Optional
		var commentSources = ["L","P","O"];
		var source = nte['NTE.2'].toString();

		// Comments that are contained in the segment - Required
		var comment = nte['NTE.3'].toString();

		if (commentSources.indexOf(source) > -1) {	
			resPatient.text.*::div.* += new XML('<p class="' + source + '">' + stripNonValidXMLChars(comment) +'</p>');
		} else {
			resPatient.text.*::div.* += new XML('<p>' + stripNonValidXMLChars(comment) +'</p>');	
		}
	}
}

if (!patientComment) delete resPatient.text;

// Patient Quest360 identifier list (repeatable)
// (SN)  Only first PID.3.1 [ID Number] is mapped in this version
var identifierTypeCode = msg['ORU_R01.PATIENT_RESULT']['ORU_R01.PATIENT']['PID']['PID.3']['CX.5'].toString();
if (identifierTypeCode) {
	resPatient.identifier.type.coding.code.@value = identifierTypeCode;
}

//======================= ~~ RKW IMPORTANT: This is where the patient SAFE ID is found
// Quest identifier - Required

var patientID = msg['PID']['PID.3']['PID.3.1'].toString();
//var patientID = msg['ORU_R01.PATIENT_RESULT']['ORU_R01.PATIENT']['PID']['PID.3']['CX.1'].toString();
resPatient.identifier.(use.@value == "official").value.@value = patientID;


