var arrDiagnosticReport = [];
var arrOrderingProvider = [];
var arrProviderID = [];

/*
* (SN) Practitioner resource for Performing Organization or Medical Doctor is not mapped (see comments below)
*
*/ 

for each (var observation in msg['ORU_R01.PATIENT_RESULT'].children()) {

	// Ignore "comment" results
	if( observation['OBX']['OBX.3']['CE.1'].toString() == "86006556" )
	{
		continue; 
	}

	var resDiagnosticReport = <DiagnosticReport>
							<id value=""/>
							<text>
								<status value="generated"/>
								<xhtml:div xmlns:xhtml="http://www.w3.org/1999/xhtml"/>
							</text>
						</DiagnosticReport>;

	// Process only ORC-OBR-OBX group
	if (observation['ORC']['ORC.1'].toString() != "RE") { continue; }

	resDiagnosticReport.id.@value = observation['ORC']['ORC.3']['EI.1'].toString();

	// Process Observation Request (OBR) level NTE segments
	// (SN) Known deficiency - "p" element namespace is not bound, must be "xhtml:p" 
	// March 14 2017: Issue #3 "Comments appear twice" fixed 
	resDiagnosticReport.text.*::div.* = new XMLList();
	var observationComment = false;	

	// Extract ORC-OBR-OBX group related comments only
	var orderObservationGroup = new XML(observation.toString());

	// March 14: This returns all children and grandchildren NTE segments of the this object
	for each (var nte in orderObservationGroup.descendants("NTE")) { 

		if ("NTE" == nte.name().toString()) {

			observationComment = true;

			// March 14, 2017: Comment source depends on the group and appended as a prefix 
			var prefix = "";
			if (nte.parent().name().toString().indexOf(".ORDER_OBSERVATION") > -1) {
				prefix = "OBR";
			} else if (nte.parent().name().toString().indexOf(".OBSERVATION") > -1) {
				prefix = "OBX";
			}

			// Source of Comment - Optional
			var commentSources = ["L","P","O"];
			var source = nte['NTE.2'].toString();

			// Comments contained in the segment - Required
			var comment = nte['NTE.3'].toString();

			if (commentSources.indexOf(source) > -1) {	
				resDiagnosticReport.text.*::div.* += new XML('<p class="' + prefix + '-' + source + '">' + stripNonValidXMLChars(comment) +'</p>');
			} else {
				resDiagnosticReport.text.*::div.* += new XML('<p class="' + prefix + '">' + stripNonValidXMLChars(comment) +'</p>');	
			}
		}
	}

	if (!observationComment) delete resDiagnosticReport.text;

	// Process single OBR segment
//	resDiagnosticReport.appendChild(new XML("<contained>" + getDiagnosticOrder(observation) + "</contained>"));

/*
	// Process Ordering Provider
	// (SN) Add only if such Ordering Provider is not in the list already
	var provID =  observation['ORC']['ORC.12']['XCN.1'].toString();
	if (arrProviderID.indexOf(provID) < 0) {
		arrOrderingProvider.push(getOrderingProvider(observation));
		arrProviderID.push(provID);
	}

	// Process multiple OBX segments
	for each (var obs in observation['ORU_R01.OBSERVATION'].children()) {
		if ("OBX" == obs.name().toString()) {

			// OBX segments nested under DiagnosticReport as contained resources
			resDiagnosticReport.appendChild(new XML("<contained>" + getObservationResource(obs) + "</contained>"));
		}	
	}	
*/
	// Placer Order Number - If populated, this field contains the placer applications order number.
	if (observation['ORC']['ORC.2']['EI.1'].toString()) {
		var identifier1 = <identifier>
						<system value=""/>
						<value value=""/>
					</identifier>;
		identifier1.value.@value = observation['ORC']['ORC.2']['EI.1'].toString();
//		identifier1.system.@value = observation['ORC']['ORC.2']['EI.2'].toString();
		identifier1.system.@value = $('sparkRawURL') + "/" + "90046003"
		resDiagnosticReport.appendChild(identifier1);
	}

	// Filler Order Number - The order number associated with the fillers application. 
	if (observation['ORC']['ORC.3']['EI.1'].toString()) {
		var identifier2 = <identifier>
						<system value=""/>
						<value value=""/>
					</identifier>;
		identifier2.value.@value = observation['ORC']['ORC.3']['EI.1'].toString();
		identifier2.system.@value = $('sparkRawURL') + "/Observation/" +  observation['ORC']['ORC.3']['EI.2'].toString();
		resDiagnosticReport.appendChild(identifier2);		
	}

	var identifier3 = <identifier>
					<system value=""/>
					<value value=""/>
				</identifier>;
		identifier3.value.@value = observation['OBR']['OBR.4']['CE.1'].toString();
		identifier3.system.@value = "QUEST";
		resDiagnosticReport.appendChild(identifier3);		


	// Order Status - Indicates whether the result is partial or final.
	// March 25 2017: updated to pass API validation (see Terminology Mapping spreadsheet)
	var statusCode = observation['ORC']['ORC.5'].toString();
	if (statusCode) {
		var statusXML = <status value="final">
					</status>;

		resDiagnosticReport.appendChild(statusXML);
	}

	resDiagnosticReport.code.@id = observation['ORC']['ORC.3']['EI.1'].toString();

	// Hard-coded, required by FHIR
	resDiagnosticReport.appendChild(new XML('<subject><reference value="Patient"/></subject>'));

	// Observation Date/Time
	resDiagnosticReport.effectiveDateTime.@value = convertHL7DateTime(observation['OBR']['OBR.7']['TS.1'].toString());
	resDiagnosticReport.issued.@value = convertHL7DateTime(observation['OBR']['OBR.7']['TS.1'].toString());

	// Reference to observation this report is based on
	for each (var obs in observation['ORU_R01.OBSERVATION'].children()) {
		if ("OBX" == obs.name().toString() && obs['OBX.3']['CE.1'].toString() != "") {
			var id = "Observation/#" + obs['OBX.3']['CE.1'].toString();
			var resultReference = <result>
									<reference value=""/>
								</result>;
			resultReference.reference.@value = id;
			resDiagnosticReport.appendChild(resultReference);	
		}	
	}


//	logger.error( "Diagnostic Report\n:" +  resDiagnosticReport.toString() );

	// ~~ STEPS: 
	// 1. Add test code to ID
	// 2. Pull array of Observations for each Order/Report
	// 3. Put Observations into Diagnostic Report
	// 5. Populate Conclusion

	importPackage(Packages.java.util);
	importPackage(Packages.ca.uhn.fhir.context);
	importPackage(Packages.ca.uhn.fhir.model.dstu3.resource);
	importPackage(Packages.ca.uhn.fhir.rest.api);
	importPackage(Packages.ca.uhn.fhir.rest.client);

	importPackage(Packages.ca.uhn.fhir.model.dstu.composite); // RD added 6/1/2017

	var ctx = channelMap.get( "ctx" );
	logger.info ('ctx is ' + ctx);
	var client = ctx.newRestfulGenericClient( configurationMap.get('sparkRawURL') );

	var originatingCode = observation['OBR']['OBR.50']['CWE.1'].toString();

	// The parent test is itself, but a reflex has a different parent test
	if( originatingCode == null || originatingCode == "" ) 
	{
		originatingCode = observation['OBR']['OBR.4']['CE.1'].toString();
	}
// debug 
	logger.info ('resDiagnosticReport is ' + resDiagnosticReport);

// end

	var report = ctx.newXmlParser().parseResource( resDiagnosticReport.toString() );
	logger.info ('report is ' + report);


	var Reference = new ResourceReferenceDt();

	var patientReference = new ResourceReferenceDt();
	report.setSubject(new ResourceReferenceDt(  $('sparkRawURL') + "/Patient/" + $('PatientResourceID') ));	

//	var requests = new ArrayList();
//	requests.add( new ResourceReferenceDt(  $('sparkRawURL') + "/ClinicalOrder/" + originatingCode ) );	
//	report.setRequest(requests);

	var theAttachments = new ArrayList();

	var attachment = new AttachmentDt();
	attachment.setContentType("application/pdf");
	attachment.setData( new java.lang.String( $('PDFBase64') ).getBytes() );
	theAttachments.add(attachment);

	var attachment2 = new AttachmentDt();
	attachment2.setContentType("text/hl7");
	attachment2.setData( new java.lang.String( $('HL7Base64') ).getBytes() );
	theAttachments.add(attachment2);

	report.setPresentedForm(theAttachments );	

	// Contained Observations

     var resourceList = new ArrayList();

	var containedDt = new ContainedDt();

	for each (var obs in observation['ORU_R01.OBSERVATION'].children()) {
		if ("OBX" == obs.name().toString()) 
		{
			// ~~ Make sure to exclude OBX observations containing PDF
//			var obxObservation = ctx.newXmlParser().parseResource( getObservationResource(obs).toString() );	
			var obxObservation = getObservationResourcePopulated( obs );


			resourceList.add(obxObservation);

		}	
	}

	containedDt.setContainedResources(resourceList);
	report.setContained(containedDt);		
	report.setContained(containedDt);	

	// Conclusion
	var abnormalFlags = observation['OBX.8'].toString();
	if (abnormalFlags) {
		report.setConclusion("ABNORMAL");
	} else {
		report.setConclusion("NORMAL");
	}

	var outcome = client.create()
		.resource(report)
		.execute();	

	logger.error( outcome.getResource().toString() );

//	arrDiagnosticReport.push(resDiagnosticReport.toString());
}

