/**
	Populates MSG segment with data from the MessageHeader resource
	@param {Object} obj - MessageHeader object resource
	@return - nothing, uses global HL7v2 message object
*/
function processMessageHeader(obj) {

	if (obj.resource.MessageHeader.source) {

		// The name of the sending application
		var sendingApplication = ((obj.resource.MessageHeader.source.name) ? obj.resource.MessageHeader.source.name.value.toString() : "");
		if (sendingApplication) oml['MSH']['MSH.3'] = sendingApplication;

		// Primary or secondary account number(s) defined for the placer.
		var sendingFacility = ((obj.resource.MessageHeader.source.endpoint) ? obj.resource.MessageHeader.source.endpoint.value.toString() : "");
		if (sendingFacility) oml['MSH']['MSH.4'] = sendingFacility;

	} 

	if (obj.resource.MessageHeader.destination) {

		// If provided must be hard-coded to "PSC" for PSC hold orders. If the <name> element is not provided or value is other than "PSC" the order is considered to be a basic lab order.
		var receivingApplication = ((obj.resource.MessageHeader.destination.name) ? obj.resource.MessageHeader.destination.name.value.toString() : "");
		if (receivingApplication) oml['MSH']['MSH.5'] = receivingApplication;

		// The receiving facility. The three-letter ID of the facility that performs the test.
		var receivingFacility = ((obj.resource.MessageHeader.destination.endpoint) ? obj.resource.MessageHeader.destination.endpoint.value.toString() : "");
		if (receivingFacility) oml['MSH']['MSH.6'] = receivingFacility;			
	}

	return;
}

/**
	Populates PID segment with data from the Patient resource
	@param {Object} obj - Patient object resource
	@return - nothing, uses global HL7v2 message object
*/
function processPatient(obj) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");
	var hasAddress = false;
	var hasPhone = false;

	var comments = [];

	// Iterate over all patient identifiers
	obj.resource.Patient.identifier.forEach(function(item){

		// PID.3 [Patient Identifier List]
		if (item.use.value.toString() == "official") {
			var id = item.value.value.toString();
			if (id) 
				oml['PID']['PID.3']['PID.3.1'] = id;

			var type = ((item.type && item.type.coding) ? item.type.coding.code.value.toString() : "");
			if (type)
				oml['PID']['PID.3']['PID.3.5'] = type;

			var assigner = ((item.assigner) ? item.assigner.display.value.toString() : "");
			if (assigner)
				oml['PID']['PID.3']['PID.3.4'] = assigner;								

		// PID.2 [Patient ID]	
		} else if (item.use.value.toString() == "secondary") {

			var id = item.value.value.toString();
			if (id) 
				oml['PID']['PID.2']['PID.2.1'] = id;

			var type = ((item.type && item.type.coding) ? item.type.coding.code.value.toString() : "");
			if (type)
				oml['PID']['PID.2']['PID.2.5'] = type;			

		}

	});


	// Parse patient's name with DEX name length limitation applied
	var nameParts = processPersonName(obj.resource.Patient.name, true);
	oml['PID']['PID.5']['PID.5.1'] = nameParts.family;
	if (nameParts.given1) oml['PID']['PID.5']['PID.5.2'] = nameParts.given1;
	if (nameParts.given2) oml['PID']['PID.5']['PID.5.3'] = nameParts.given2;


	// Patient's DOB
	if (obj.resource.Patient.birthDate) {
		var dob = obj.resource.Patient.birthDate.value.toString();
		oml['PID']['PID.7'] = dob.replace(/-/g,"");
	} else {
		error.push("resource.Patient.birthDate for PID.7[DateTime of Birth] is required");
	}

	// Administrative Gender 
	if (obj.resource.Patient.gender) {
		var gender = obj.resource.Patient.gender.value;
		oml['PID']['PID.8'] = convertTable("0001", gender);
	} else {
		error.push("resource.Patient.gender for PID.8[Administrative Sex] is required");		
	}




	// Patient Address
	if (obj.resource.Patient.address) {

		var street = ((obj.resource.Patient.address.line) ? obj.resource.Patient.address.line.value.toString() : "");
		if (street)
			oml['PID']['PID.11']['PID.11.1'] = street;

		var city = ((obj.resource.Patient.address.city) ? obj.resource.Patient.address.city.value.toString() : "");
		if (city)
			oml['PID']['PID.11']['PID.11.3'] = city;	

		var state = ((obj.resource.Patient.address.state) ? obj.resource.Patient.address.state.value.toString() : "");
		if (state)
			oml['PID']['PID.11']['PID.11.4'] = state;

		var postal = ((obj.resource.Patient.address.postalCode) ? obj.resource.Patient.address.postalCode.value.toString() : "");
		if (postal)
			oml['PID']['PID.11']['PID.11.5'] = postal;

		var country = ((obj.resource.Patient.address.country) ? obj.resource.Patient.address.country.value.toString() : "");
		if (country)
			oml['PID']['PID.11']['PID.11.6'] = country;

		// There might be a better definition what is required "patients address".
		hasAddress = (street || city || state || postal);	
	}

	// Patient Phone Number (Home)	
	var phone = ((obj.resource.Patient.telecom) ? obj.resource.Patient.telecom.value.value.toString() : "");
	if (phone) {
		hasPhone = true;
		oml['PID']['PID.13'] = convertPhoneXTN(phone);
	}


	// Validate conditional fields
	if (coverage == "P" || coverage == "T") {
		if (!hasAddress) error.push("resource.Patient.address for PID.11[Patient Address] is required if IN1.47[Coverage Type] is P or T");
		if (!hasPhone) error.push("resource.Patient.telecom for PID.13[Phone Number-Home] is required if IN1.47[Coverage Type] is P or T");
	}


	// Iterate over all possible comment entries in /text/div/p  (#CS20 certification test case)
	if (obj.resource.Patient.text && obj.resource.Patient.text.div) {	

		var internalCommentsCount = 0; //  only 5 NTE segments of 60 characters each
		var reportCommentsCount = 0; // only 2 NTE segments of 60 characters each
		var sourceOfComment = ["L","P","O", ""]; // Optional source of comments

		if (obj.resource.Patient.text.div.p.length && obj.resource.Patient.text.div.p.length > 0) {
			obj.resource.Patient.text.div.p.forEach(function(item){
				// For internal (I) or report (R) comments restriction applies
				if ((item.class.toString() == "I" && internalCommentsCount++ < 5) || 
					(item.class.toString() == "R" && reportCommentsCount++ < 2)) {
						comments.push(getNTESegment(item, countNTE++));
				} else if (sourceOfComment.indexOf(item.class.toString()) > -1) {
					// If the comment is not internal (I) or report (R) then accept
						comments.push(getNTESegment(item, countNTE++));
				} 		
			});	
		} else if (obj.resource.Patient.text.div.p) {
			// A single comment from any acceptable source
			sourceOfComment = ["I","R","L","P","O", ""];
			if (sourceOfComment.indexOf(obj.resource.Patient.text.div.p.class.toString()) > -1)
				comments.push(getNTESegment(obj.resource.Patient.text.div.p, countNTE++));
		}

		for (var i = comments.length - 1; i >= 0; i--) {
			var nte = comments[i];
			if (nte.toString() != "")
				oml['PID'] += new XML(nte.toString());
		}

		comments.length = 0;
	}

	return;
}

/**
	Populates IN1 [Insurance] segments with data from the primary and secondary Coverage resources	
	@param {Object} obj - Coverage object resource
	@return - nothing, uses global HL7v2 message object
*/
function processCoverage(obj) {

	var nameParts = {family: "", given1: "", given2: ""};

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");
	var isContainedPerson = hasPersonAddress = false;
	var isContainedOrganization = hasOrganizationAddress = false;

	var segIns = <IN1>
				<IN1.1>
					<IN1.1.1>1</IN1.1.1>
				</IN1.1>
				<IN1.2/><IN1.3/><IN1.4/><IN1.5/><IN1.6/><IN1.7/><IN1.8/><IN1.9/>
				<IN1.10/><IN1.11/><IN1.12/><IN1.13/><IN1.14/><IN1.15/><IN1.16/><IN1.17/><IN1.18/><IN1.19/>
				<IN1.20/><IN1.21/><IN1.22/><IN1.23/><IN1.24/><IN1.25/><IN1.26/><IN1.27/><IN1.28/><IN1.29/>
				<IN1.30/><IN1.31/><IN1.32/><IN1.33/><IN1.34/><IN1.35/><IN1.36/><IN1.37/><IN1.38/><IN1.39/>
				<IN1.40/><IN1.41/><IN1.42/><IN1.43/><IN1.44/><IN1.45/><IN1.46/><IN1.47/>
			</IN1>;

	// Insurance Set ID
	var coverageType = 1; 
	var setID = obj.resource.Coverage.id.value.toString();
	if (setID.indexOf("2") > -1) {
		segIns['IN1.1'] = "2";
		coverageType = 2; // secondary - "IN1-insurance-2"
	} else {
		segIns['IN1.1'] = "1";
		coverageType = 1; // primary - "IN1-insurance-1"		
	}


	// Insurance Plan ID
	var planID = obj.resource.Coverage.identifier.value.value.toString();
	if (planID)
		segIns['IN1.2'] = planID;


	if (obj.resource.Coverage.contained.length && obj.resource.Coverage.contained.length > 0) {

		// Loop through contained resources
		obj.resource.Coverage.contained.forEach(function(item){

			// Process contained insurance company
			processInsuranceOrganization(segIns, item);

			if (item.Person != null && item.Person != undefined) {

				isContainedPerson = true;

				// Process insured person name with DEX name length limitation applied
				nameParts = processPersonName(item.Person.name, true);

				// Insured's Address
				if (item.Person.address) {
					var street = item.Person.address.line.value.toString();
					if (street)
						segIns['IN1.19']['IN1.19.1'] = street;

					var city = ((item.Person.address.city) ? item.Person.address.city.value.toString() : "");
					if (city)
						segIns['IN1.19']['IN1.19.3'] = city;	

					var state = ((item.Person.address.state) ? item.Person.address.state.value.toString() : "");
					if (state)
						segIns['IN1.19']['IN1.19.4'] = state;

					var postal = ((item.Person.address.postalCode) ? item.Person.address.postalCode.value.toString() : "");
					if (postal)
						segIns['IN1.19']['IN1.19.5'] = postal;

					var country = ((item.Person.address.country) ? item.Person.address.country.value.toString() : "");
					if (country)
						segIns['IN1.19']['IN1.19.6'] = country;

					// There might be a better definition what is required "insureds Address".
					hasPersonAddress = (street || city || state || postal);	
				}
			}
		});

	} else {
		// When only contained  insurance company is present
		processInsuranceOrganization(segIns, obj.resource.Coverage.contained);
	}

	// Insured's Group Employer Name 
	if (obj.resource.Coverage.extension) {
		var emplName = ((obj.resource.Coverage.extension.valueIdentifier) ? obj.resource.Coverage.extension.valueIdentifier.value.value.toString() : "");
		if (emplName)
			segIns['IN1.11'] = emplName;
	}


	// Group Number
	var groupNumber = ((obj.resource.Coverage.group) ? obj.resource.Coverage.group.value.toString() : "");
	if (groupNumber)
		segIns['IN1.8'] = groupNumber;





	// Name Of Insured
	// (SN) March 11, 2017: Fix to downgrade to DSTU3 Coverage resource. Previously checked only planholderReference.
/*	
	var insuredRef;
	if (obj.resource.Coverage.planholderReference) {
		// STU3
		insuredRef = obj.resource.Coverage.planholderReference.reference.value.toString();
	} else if (obj.resource.Coverage.subscriberReference) {
		// DSTU3
		insuredRef = obj.resource.Coverage.subscriberReference.reference.value.toString();
	}
*/

	// Name Of Insured
	// (SN) March 13 2017: Disabled
	//var insuredRef = obj.resource.Coverage.planholderReference.reference.value.toString();

	var insuredRef;
	if (obj.resource.Coverage.planholderReference) {
		// STU3
		insuredRef = obj.resource.Coverage.planholderReference.reference.value.toString();
	} else if (obj.resource.Coverage.subscriberReference) {
		// DSTU3
		insuredRef = obj.resource.Coverage.subscriberReference.reference.value.toString();
	}

	if (insuredRef == "Patient/PID-patient-identification") {
		// Same as the PID segment
		segIns['IN1.16']['IN1.16.1'] = oml['PID']['PID.5']['PID.5.1'].toString();
		if (oml['PID']['PID.5']['PID.5.2'].toString())
			segIns['IN1.16']['IN1.16.2'] = oml['PID']['PID.5']['PID.5.2'].toString();
		if (oml['PID']['PID.5']['PID.5.3'].toString())
			segIns['IN1.16']['IN1.16.3'] = oml['PID']['PID.5']['PID.5.3'].toString();

	} else if (insuredRef == "Person/insured-person") {
		// Taken from the contained Person resource
		segIns['IN1.16']['IN1.16.1'] = nameParts.family;
		if (nameParts.given1.length > 0) segIns['IN1.16']['IN1.16.2'] = nameParts.given1;
		if (nameParts.given2.length > 0) segIns['IN1.16']['IN1.16.3'] = nameParts.given2;		
	}

	// Insured's Relationship To Patient
	if ( obj.resource.Coverage.relationship) {
		var relationship = ((obj.resource.Coverage.relationship.code) ? obj.resource.Coverage.relationship.code.value.toString() : "");
		if (relationship)
			segIns['IN1.17']['IN1.17.1'] = relationship;

		var relationshipDesc = ((obj.resource.Coverage.relationship.display) ? obj.resource.Coverage.relationship.display.value.toString() : "");
		if (relationshipDesc)
			segIns['IN1.17']['IN1.17.2'] = relationshipDesc;
	}

	// Company Plan Code
	var planCode = (( obj.resource.Coverage.subPlan) ? obj.resource.Coverage.subPlan.value.toString() : "");
	if (planCode)
		segIns['IN1.35'] = planCode;

	// Policy Number
	var policyNumber = ((obj.resource.Coverage.plan) ? obj.resource.Coverage.plan.value.toString() : "");
	if (policyNumber)
		segIns['IN1.36'] = policyNumber;

	// Coverage Type
	segIns['IN1.47'] = channelMap.get("CoverageType");


	if (coverage == "T") {
		if (segIns['IN1.4'].toString() == "") error.push("resource.Coverage.contained.Organization.name for IN1.4[Insurance Company Name] is required if IN1.47[Coverage Type] is T");
		if (segIns['IN1.8'].toString() == "") error.push("resource.Coverage.group for IN1.8[Group Number] is required if IN1.47[Coverage Type] is T");
		if (segIns['IN1.16']['IN1.16.1'].toString() == "") error.push("resource.Coverage.contained.Person.name for IN1.16[Name Of Insured] is required if IN1.47[Coverage Type] is T");
		if (segIns['IN1.17']['IN1.17.1'].toString() == "") error.push("resource.Coverage.relationship.code for IN1.17[Insured Relationship To Patient] is required if IN1.47[Coverage Type] is T");
		if (isContainedPerson && !hasPersonAddress) error.push("resource.Coverage.contained.Person.address for IN1.19[Insured Address] is required if IN1.47[Coverage Type] is T");
		if (segIns['IN1.36'].toString() == "") error.push("resource.Coverage.plan for IN1.36[Policy Number] is required if IN1.47[Coverage Type] is T");		
	}


	// Store the segment to be later compiled into a message
	coverageMap.put(coverageType, segIns.toString());

	return;
}


/**
	Populates IN1.3 [Insurance Company ID] field with data from the contained Organization resource
	@param {Object} obj - Organization object resource
	@return - nothing, uses global HL7v2 message object
*/
function processInsuranceOrganization(segIns, item) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");
	var hasAddress = false;

	if (item.Organization != null && item.Organization != undefined) {

		isContainedOrganization = true;

		// Insurance Company ID
		var companyID = ((item.Organization.identifier) ? item.Organization.identifier.value.value.toString() : "");
		if (companyID) 
			segIns['IN1.3'] = companyID;

		// Insurance Company Name
		var companyName = ((item.Organization.name) ? item.Organization.name.value.toString() : "");
		if (companyName)
			segIns['IN1.4'] = companyName;

		// Insurance Company Address
		if (item.Organization.address) {
			var street = ((item.Organization.address.line) ? item.Organization.address.line.value.toString() : "");
			if (street)
				segIns['IN1.5']['IN1.5.1'] = street;

			var city = ((item.Organization.address.city) ? item.Organization.address.city.value.toString() : "");
			if (city)
				segIns['IN1.5']['IN1.5.3'] = city;

			var state = ((item.Organization.address.state) ? item.Organization.address.state.value.toString() : "");
			if (state)
				segIns['IN1.5']['IN1.5.4'] = state;

			var postal = ((item.Organization.address.postalCode) ? item.Organization.address.postalCode.value.toString() : "");
			if (postal)
				segIns['IN1.5']['IN1.5.5'] = postal;

			var country = ((item.Organization.address.country) ? item.Organization.address.country.value.toString() : "");
			if (country)
				segIns['IN1.5']['IN1.5.6'] = country;

			// There might be a better definition what is required "Insurance Company Address"
			hasOrganizationAddress = (street || city || state || postal);						
		}		

		// Insurance Co Phone Number
		var phone = ((item.Organization.telecom) ? item.Organization.telecom.value.value.toString() : "");
		if (phone) {
			hasPhone = true;
			segIns['IN1.7'] = convertPhoneXTN(phone);
		}
	}

	// Validate conditional fields
	if (coverage == "T") {		
		if (isContainedOrganization && !hasOrganizationAddress) error.push("resource.Coverage.contained.Organization.address for IN1.5[Insurance Company Address] is required if IN1.47[Coverage Type] is T");
	}


	return;
}

/**
	Creates and populates GT1 [Guarantor] segment from the Person resource
	@param {Object} obj - Coverage object resource
	@return - nothing, uses global HL7v2 message object
*/
function processGuarantor(obj) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");
	var hasAddress = false;
	var hasPhone = false;

	var gt1 = <GT1>
				<GT1.1><GT1.1.1>1</GT1.1.1></GT1.1>
				<GT1.2/>
				<GT1.3><GT1.3.1/><GT1.3.2/></GT1.3>
			</GT1>;

	// Parse guarantor's name with DEX name length limitation applied
	var nameParts = processPersonName(obj.resource.Person.name, true);
	gt1['GT1.3']['GT1.3.1'] = nameParts.family;
	if (nameParts.given1.length > 0) gt1['GT1.3']['GT1.3.2'] = nameParts.given1;
	if (nameParts.given2.length > 0) gt1['GT1.3']['GT1.3.3'] = nameParts.given2;	


	// Guarantor Address
	if (obj.resource.Person.address) {
		var street = ((obj.resource.Person.address.line) ? obj.resource.Person.address.line.value.toString() : "");
		if (street)
			gt1['GT1.5']['GT1.5.1'] = street;

		var city = ((obj.resource.Person.address.city) ? obj.resource.Person.address.city.value.toString() : "");
		if (city)
			gt1['GT1.5']['GT1.5.3'] = city;	

		var state = ((obj.resource.Person.address.state) ? obj.resource.Person.address.state.value.toString() : "");
		if (state)
			gt1['GT1.5']['GT1.5.4'] = state;

		var postal = ((obj.resource.Person.address.postalCode) ? obj.resource.Person.address.postalCode.value.toString() : "");
		if (postal)
			gt1['GT1.5']['GT1.5.5'] = postal;

		var country = ((obj.resource.Person.address.country) ? obj.resource.Person.address.country.value.toString() : "");
		if (country)
			gt1['GT1.5']['GT1.5.6'] = country;

		// There might be a better definition what is required "guarantor address".
		hasAddress = (street || city || state || postal);			
	}


	// Guarantor Phon Number - Home
	var phone = ((obj.resource.Person.telecom) ? obj.resource.Person.telecom.value.value.toString() : "");
	if (phone) {
		hasPhone = true;
		gt1['GT1.6'] = convertPhoneXTN(phone);
	}


	if (coverage == "P" || coverage == "T") {
		if (gt1['GT1.3']['GT1.3.1'].toString() == "") error.push("resource.Person.name for GT1.3[Guarantor Name] is required if IN1.47[Coverage Type] is P or T");
		if (!hasAddress) error.push("resource.Person.address for GT1.5[Guarantor Address] is required if IN1.47[Coverage Type] is P or T");
		if (!hasPhone) error.push("resource.Person.telecom for GT1.6[Guarantor Phone Number-Home] is required if IN1.47[Coverage Type] is P or T");		
	}

	// Store the segment to be later compiled into a message
	guarantorMap.put(1, gt1.toString());

	return;
}

/**
	Populates ORC [Common Order] segment from the DiagnosticReport resource
	@param {Object} obj - DiagnosticReport object resource
	@return - nothing, uses global HL7v2 message object
*/
function processCommonOrder(obj) {


	var orc = <ORC><ORC.1><ORC.1.1>NW</ORC.1.1></ORC.1></ORC>;

	// Count ORDER groups
	var groupID = ((obj.resource.DiagnosticReport.id) ? obj.resource.DiagnosticReport.id.value.toString() : "");
	var groupCount = groupID.match(/\d+/);

	if (groupCount == null) {
		groupCount = 1;
		error.push("resource.DiagnosticReport.id element is undefined or not properly formatted, expected: ORC-common-order-n, where n identifies the order group");
	}


	// Placer Order Number
	// The value in ORC.02.01 (Entity Identifier) is the same as the value in OBR.02.01 (Entity Identifier)
	// All values for this field in all ORC and OBR segments are identical.
	// March 12: Moved into DiagnosticReport.identifier verified block 
	//var id = obj.resource.DiagnosticReport.identifier.value.value.toString();
	//if (id) orc['ORC.2']['ORC.2.1'] = id;

	if (obj.resource.DiagnosticReport.identifier) {

		var id = ((obj.resource.DiagnosticReport.identifier.value.value) ? obj.resource.DiagnosticReport.identifier.value.value.toString():"");
		if (id) orc['ORC.2']['ORC.2.1'] = id;

		var namespaceID = ((obj.resource.DiagnosticReport.identifier.system) ? obj.resource.DiagnosticReport.identifier.system.value.toString() : "");
		if (namespaceID) orc['ORC.2']['ORC.2.2'] = namespaceID;

		var universalID = ((obj.resource.DiagnosticReport.identifier.type) ? obj.resource.DiagnosticReport.identifier.type.id.toString() : "");
		if (universalID) orc['ORC.2']['ORC.2.3'] = universalID;
	}


	// Store the Placer Order Number for the OBR segment
	var diagnosticOrderID = ((obj.resource.DiagnosticReport.request) ? obj.resource.DiagnosticReport.request.reference.value.toString() : "");
	if (diagnosticOrderID.indexOf("DiagnosticOrder/") > -1) {
		diagnosticOrderID = diagnosticOrderID.substring("DiagnosticOrder/".length, diagnosticOrderID.length);

		var placedOrderNumber = {id:"", namespaceID:"", universalID:""};

		placedOrderNumber.id = orc['ORC.2']['ORC.2.1'].toString();
		placedOrderNumber.namespaceID = orc['ORC.2']['ORC.2.2'].toString();
		placedOrderNumber.universalID = orc['ORC.2']['ORC.2.3'].toString();
		placedOrderNumberMap.put(diagnosticOrderID, placedOrderNumber);		
	}


	// Verified By Practitioner
	if (obj.resource.DiagnosticReport.contained != null && obj.resource.DiagnosticReport.contained != undefined) {
		var verifiedByID = obj.resource.DiagnosticReport.contained.Practitioner.identifier.value.value.toString();
		if (verifiedByID)
			orc['ORC.11']['ORC.11.1'] = verifiedByID;

		var familyName = ((obj.resource.DiagnosticReport.contained.Practitioner.name) ? obj.resource.DiagnosticReport.contained.Practitioner.name.family.value.toString() : "");
		if (familyName)
			orc['ORC.11']['ORC.11.2'] = familyName;		
	}

	// Process extension for TQ1 [Timing/Quantity] segment
	if (obj.resource.DiagnosticReport.extension.valueTiming != null && obj.resource.DiagnosticReport.extension.valueTiming != undefined) {
		processTimingQuantity(groupCount, obj.resource.DiagnosticReport.extension.valueTiming);
	}

	// Process zero or many DiagnosticReport/codedDiagnosis elements for DG1 [Diagnosis] segment
	var conditions = [];
	if (obj.resource.DiagnosticReport.codedDiagnosis) {

		if (obj.resource.DiagnosticReport.codedDiagnosis.length > 0) {
			var setID = 1;
			obj.resource.DiagnosticReport.codedDiagnosis.forEach(function(item){
				// A maximum of 12 unique ICD-10 codes are permitted per entire OML message
				// (SN) This code checks only codedDiagnosis in a single DiagnosticReport
				if (setID <= 12) conditions.push(processCondition(item, setID++));				 
			});
		} else {
			conditions.push(processCondition(obj.resource.DiagnosticReport.codedDiagnosis, 1)); 
		}
	}

	// Process zero or many result/reference elements for OBX [Observation] segment
	var observationReferences = [];
	if (obj.resource.DiagnosticReport.result) {
		var ref;
		if (obj.resource.DiagnosticReport.result.length > 0) {
			obj.resource.DiagnosticReport.result.forEach(function(item){
				ref = item.reference.value.toString();
				if (ref) observationReferences.push(ref.substring("Observation/".length, ref.length));
			});
		} else {
			ref = ((obj.resource.DiagnosticReport.result.reference != undefined) ? obj.resource.DiagnosticReport.result.reference.value.toString() : "");
			if (ref) observationReferences.push(ref.substring("Observation/".length, ref.length));
		}		
	}


	// ORC.12 [Ordering Provider] is filled when resource.Practitioner is processed (see "Provider field" step)
	var orderingProviderReferenceID = ((obj.resource.DiagnosticReport.performer != undefined) ? obj.resource.DiagnosticReport.performer.reference.value.toString() : "");
	if (orderingProviderReferenceID.indexOf("Practitioner/") > -1) {
		orderingProviderReferenceID = orderingProviderReferenceID.substring("Practitioner/".length, orderingProviderReferenceID.length);
		var objOrderingProvider = practitionerMap.get(orderingProviderReferenceID.toString());

		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.identifier != undefined) {
			var id = objOrderingProvider.resource.Practitioner.identifier.value.value.toString();
			if (id) orc['ORC.12']['ORC.12.1'] = id;

			var assigner = objOrderingProvider.resource.Practitioner.identifier.assigner.display.value.toString();
			if (assigner) orc['ORC.12']['ORC.12.9'] = assigner;
		}

		// Process ordering provider's name, DEX name length limitation does NOT apply
		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.name != undefined) {
			var nameParts = processPersonName(objOrderingProvider.resource.Practitioner.name, false);
			orc['ORC.12']['ORC.12.2'] = nameParts.family;		
			if (nameParts.given1.length > 0) orc['ORC.12']['ORC.12.3'] = nameParts.given1;		
			if (nameParts.given1.length > 0) orc['ORC.12']['ORC.12.4'] = nameParts.given2;				
		}
	}


	// Store the segment to be later compiled into a message
	commonOrderMap.put(groupCount.toString(), orc.toString());

	// Store the array of diagnosis to be later compiled into a message
	if (conditions.length > 0) diagnosisMap.put(groupCount.toString(), conditions);

	// Store the array of Observations to be later compiled into a message
	if (observationReferences.length > 0) observationReferencesMap.put(groupCount.toString(), observationReferences);


	return;
}

/**
	Populates TQ1 [Timing/Quantity] segment with data from the DiagnosticReport extension
	@param {Integer} count - Common Order group count
	@param {Object} valueTiming - valueTiming extension
	@return - nothing, uses global map
*/
function processTimingQuantity(count, valueTiming) {

	// TQ1 [Timing/Quantity] segment
	if (valueTiming != null && valueTiming != undefined) {

		var tq1 = <TQ1><TQ1.1><TQ1.1.1>1</TQ1.1.1></TQ1.1></TQ1>;

		tq1['TQ1.1']['TQ1.1.1'] = count;

		var startDateTime = ((valueTiming.event) ? valueTiming.event.value.toString() : "");
		if (startDateTime)
			tq1['TQ1.7'] = convertJSONDateTime(startDateTime);

		var priorityCode = ((valueTiming.code) ? valueTiming.code.coding.code.value.toString() : "");
		if (priorityCode)
			tq1['TQ1.9']['TQ1.9.1'] = priorityCode;
	}

	// Store the segment to be later compiled into a message
	timingQuantityMap.put(count.toString(), tq1.toString());

	return;
}

/**
	Populates OBR [Observation Request] segment from the DiagnosticReport resource
	@param {Object} obj - DiagnosticOrder object resource
	@return - nothing, uses global HL7v2 message object
*/
function processObservationRequest(obj) {

	var comments = [];

	var obr = <OBR><OBR.1><OBR.1.1>1</OBR.1.1></OBR.1>
				<OBR.2/><OBR.3/>
				<OBR.4><OBR.4.1/><OBR.4.2/><OBR.4.3/><OBR.4.4/><OBR.4.5/></OBR.4>
				<OBR.5/><OBR.6/><OBR.7/><OBR.8/><OBR.9/>
				<OBR.10/><OBR.11/><OBR.12/><OBR.13/><OBR.14/><OBR.15/>
				<OBR.16><OBR.16.1/><OBR.16.2/><OBR.16.3/><OBR.16.4/><OBR.16.5/><OBR.16.6/><OBR.16.7/><OBR.16.8/></OBR.16>
			</OBR>;


	// Count ORDER groups
	var groupID = ((obj.resource.DiagnosticOrder.id) ? obj.resource.DiagnosticOrder.id.value.toString() : "");
	var groupCount = groupID.match(/\d+/);

	if (groupCount == null) {
		groupCount = 1;
		error.push("resource.DiagnosticOrder.id element is undefined or not properly formatted, expected: OBR-observation-request-n, where n identifies the order group");
	}


	// OBR.2 [Placer Order Number] is stored during the DiagnosticReport resource processing	
	var resourceID = obj.resource.DiagnosticOrder.id.value.toString();
	// Extracts the following object - placedOrderNumber = {id:"", namespaceID:"", universalID:""};
	var placedOrderNumber = placedOrderNumberMap.get(resourceID);
	if (placedOrderNumber) {
		if (placedOrderNumber.id) obr['OBR.2']['OBR.2.1'] = placedOrderNumber.id.toString();
		if (placedOrderNumber.namespaceID) obr['OBR.2']['OBR.2.2'] = placedOrderNumber.namespaceID.toStrign();
		if (placedOrderNumber.universalID) obr['OBR.2']['OBR.2.3'] = placedOrderNumber.universalID.toString();
	}


	// Universal Service Identifier	
	var alternateID = obj.resource.DiagnosticOrder.item.code.coding.code.value.toString();
	if (alternateID)
		obr['OBR.4']['OBR.4.4'] = alternateID;

	var alternateText = obj.resource.DiagnosticOrder.item.code.coding.display.value.toString();
	if (alternateText)
		obr['OBR.4']['OBR.4.5'] = alternateText;

	// Observation Date/Time	
	var observationDateTime = obj.resource.DiagnosticOrder.event.dateTime.value.toString();
	if (observationDateTime) {
		// Format : yyyymmddhhmm
		var specimenCollectionDate = convertJSONDateTime(observationDateTime);
		obr['OBR.7'] = specimenCollectionDate.substring(0, 12);
	}

	// Specimen Action Code
	var actionCode = ((obj.resource.DiagnosticOrder.item.specimen) ? obj.resource.DiagnosticOrder.item.specimen.display.value.toString() : "");
	if (actionCode)
		obr['OBR.11'] = actionCode;

	// OBR.16 [Ordering Provider] is filled when resource.Practitioner is processed (see "Provider field" step)
	var orderingProviderReferenceID = ((obj.resource.DiagnosticOrder.event.actor.reference != undefined) ? obj.resource.DiagnosticOrder.event.actor.reference.value.toString() : "");
	if (orderingProviderReferenceID.indexOf("Practitioner/") > -1) {
		orderingProviderReferenceID = orderingProviderReferenceID.substring(13, orderingProviderReferenceID.length);
		var objOrderingProvider = practitionerMap.get(orderingProviderReferenceID.toString());

		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.identifier != undefined) {
			var id = objOrderingProvider.resource.Practitioner.identifier.value.value.toString();
			if (id) obr['OBR.16']['OBR.16.1'] = id;

			var assigner = objOrderingProvider.resource.Practitioner.identifier.assigner.display.value.toString();
			if (assigner) obr['OBR.16']['OBR.16.9'] = assigner;
		}

		// Process ordering provider's name, DEX name length limitation does NOT apply
		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.name != undefined) {
			var nameParts = processPersonName(objOrderingProvider.resource.Practitioner.name, false);
			obr['OBR.16']['OBR.16.2'] = nameParts.family;		
			if (nameParts.given1.length > 0) obr['OBR.16']['OBR.16.3'] = nameParts.given1;		
			if (nameParts.given1.length > 0) obr['OBR.16']['OBR.16.4'] = nameParts.given2;			
		}
	}

	// Store the segment to be later compiled into a message
	observationRequestMap.put(groupCount.toString(), obr.toString());




	// Iterate over all possible comment entries in /text/div/p  (#CS20 certification test case)
	if (obj.resource.DiagnosticOrder.text && obj.resource.DiagnosticOrder.text.div) {	

		var internalCommentsCount = 0; // only 5 NTE segments of 60 characters each
		var reportCommentsCount = 0; // only 2 NTE segments of 60 characters each
		var sourceOfComment = ["L","P","O", ""]; // Optional source of comments

		if (obj.resource.DiagnosticOrder.text.div.p.length && obj.resource.DiagnosticOrder.text.div.p.length > 0) {
			obj.resource.DiagnosticOrder.text.div.p.forEach(function(item){
				// For internal (I) and report (R) comments restriction applies
				if ((item.class.toString() == "I" && internalCommentsCount++ < 5) || 
					(item.class.toString() == "R" && reportCommentsCount++ < 2)) {
						comments.push(getNTESegment(item, countNTE++));

				} else if (sourceOfComment.indexOf(item.class.toString()) > -1) {
						// If the comment is other than internal (I) or report (R) then accept
						comments.push(getNTESegment(item, countNTE++));
				}
			});	
		} else if (obj.resource.DiagnosticOrder.text.div.p) {
			// A single comment from any acceptable source
			sourceOfComment = ["I","R","L","P","O", ""];
			if (sourceOfComment.indexOf(obj.resource.DiagnosticOrder.text.div.p.class.toString()) > -1)
				comments.push(getNTESegment(obj.resource.DiagnosticOrder.text.div.p, countNTE++));			
		}

		// Store the comments array to be later compiled into a message
		observationRequestCommentsMap.put(groupCount.toString(), comments);

	}


	return;
}

/**
	Populates DG1 [Diagnosis] segment from the Condition resource
	@param {Object} obj - Condition object resource
	@return {String} dg1 - DG1 segment serialized to string
*/
function processCondition(obj, setID) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");

	var dg1 = <DG1>
				<DG1.1><DG1.1.1>1</DG1.1.1></DG1.1>
				<DG1.3><DG1.3.1/><DG1.3.2/><DG1.3.3/><DG1.3.4/></DG1.3>
				<DG1.6><DG1.6.1>F</DG1.6.1></DG1.6>
			</DG1>;

	dg1['DG1.1']['DG1.1.1'] = setID;

	// Diagnosis Code
	if (obj.coding) {
		if (obj.coding.length > 0) {
			obj.coding.forEach(function(item){

				var code = ((item.code) ? item.code.value.toString() : "");
				var system = item.system.value.toString().toLowerCase();

				if (system)
					if (system.indexOf("icd-10") > -1) {

					dg1['DG1.3']['DG1.3.1'] = code;

					var text = ((item.display) ? item.display.value.toString() : "");
					dg1['DG1.3']['DG1.3.2'] = text;

					dg1['DG1.3']['DG1.3.3'] = "ICD";
				} else {
					dg1['DG1.3']['DG1.3.4'] = code;
				}
			});
		} else {
			var code = ((obj.coding.code) ? obj.coding.code.value.toString() : "");
			dg1['DG1.3']['DG1.3.1'] = code;
			var text = ((obj.coding.display) ? obj.coding.display.value.toString() : "");
			dg1['DG1.3']['DG1.3.2'] = text;
			dg1['DG1.3']['DG1.3.3'] = "ICD";
		}
	}


	// DG1.6 [Diagnosis Type] - hard-coded, required by HL7v2.5.1, F - Final
	//dg1['DG1.6']['DG1.6.1'] = "F";

	if (coverage == "P" || coverage == "T") {
		if (dg1['DG1.3']['DG1.3.1'].toString() == "" || dg1['DG1.3']['DG1.3.2'].toString() == "" || dg1['DG1.3']['DG1.3.4'].toString() == "") 
			error.push("resource.Condition.code for DG1.3[Diagnosis Code] is required if IN1.47[Coverage Type] is P or T");
	}

	return dg1.toString();

}

/**
	Populates OBX [Observation/Result] segment from the Observation resource
	@param {Object} obj - Observation object resource
	@return {String} obx - OBX segment serialized to string
*/
function processObservation(obj) {

	var obx =	<OBX>
				<OBX.1><OBX.1.1/></OBX.1>
				<OBX.2><OBX.2.1/></OBX.2>
				<OBX.3><OBX.3.1/><OBX.3.2/><OBX.3.3/><OBX.3.4/><OBX.3.5/><OBX.3.6/></OBX.3>
				<OBX.4/>
				<OBX.5><OBX.5.1/></OBX.5>
				<OBX.11><OBX.11.1>P</OBX.11.1></OBX.11>
			</OBX>;


	var id = obj.resource.Observation.id.value.toString();
	obx['OBX.1']['OBX.1.1'] = id.substring("OBX-observation-".length, id.length);

	// Contains a value that reports the results for an AOE
	if (obj.resource.Observation.code) {
		// local code
		var code = obj.resource.Observation.code.coding.code.value.toString();		
		obx['OBX.3']['OBX.3.4'] = code;

		// Local description
		var description = obj.resource.Observation.code.coding.display.value.toString();
		obx['OBX.3']['OBX.3.5'] = description;

		// Code system - Optional
		if (obj.resource.Observation.code.coding.system != undefined) {
			var codeSystem = obj.resource.Observation.code.coding.system.value.toString();
			if (codeSystem.toLowerCase().indexOf("snomed") > -1)
				obx['OBX.3']['OBX.3.6'] = "SCT";
			else if (codeSystem.toLowerCase().indexOf("loinc") > -1)
				obx['OBX.3']['OBX.3.6'] = "LN";
			else 
				delete obx['OBX.3']['OBX.3.6'];
		}

	}


	// If valueString is used, expected that the answer is always a string, therefore OBX.2 [Value Type] is hard-coded to "ST"
	if (obj.resource.Observation.valueString) {
		obx['OBX.2']['OBX.2.1'] = "ST";

		var aoe = obj.resource.Observation.valueString.value.toString();
		if (aoe) obx['OBX.5']['OBX.5.1'] = aoe;		

	// If valueQuantity is used the OBX.2 [Value Type] is hard-coded to "NM"	
	} else if (obj.resource.Observation.valueQuantity) {
		obx['OBX.2']['OBX.2.1'] = "NM";		
		var quantity = obj.resource.Observation.valueQuantity.value.value.toString();
		if (quantity) obx['OBX.5']['OBX.5.1'] = quantity;
	}


	// Observation Result Status - not required by Quest
	// Hard-coded to "P" - Preliminary


	// OBX level comments (Issue #17)
	var observationComments = [];

	// Iterate over all possible comment entries in /text/div/p  (also see Issue #17)
	if (obj.resource.Observation.text && obj.resource.Observation.text.div) {	

		var internalCommentsCount = 0; // only 5 NTE segments of 60 characters each
		var reportCommentsCount = 0; // only 2 NTE segments of 60 characters each
		var sourceOfComment = ["I","R","L","P","O",""]; // Optional source of comment

		if (obj.resource.Observation.text.div.p.length != undefined && obj.resource.Observation.text.div.p.length > 0) {
			// Several comments from any acceptable sources
			obj.resource.Observation.text.div.p.forEach(function(item){
				if (sourceOfComment.indexOf(item.class.toString()) > -1) {
					observationComments.push(getNTESegment(item, countNTE++));
				}
			});	
		} else if (obj.resource.Observation.text.div.p) {
			// A single comment from any acceptable source
			if (sourceOfComment.indexOf(obj.resource.Observation.text.div.p.class.toString()) > -1) {
				observationComments.push(getNTESegment(obj.resource.Observation.text.div.p, countNTE++));
			}			
		}
	}

	observationMap.put(id, obx.toString());
	observationCommentsMap.put(id, observationComments);

	return;
}

// Use fixHL7NodeOrder() to fix a possible node order issue 
// see http://www.mirthcorp.com/community/issues/browse/MIRTH-625

var messages = {request: "", resp: ""};
messages.request = SerializerFactory.getSerializer('HL7V2').fromXML(fixHL7NodeOrder(oml));

logger.debug("SubmitOrder: Destination - " + messages.request);


// Verify for errors 
if (error.length > 0) {

	var issue = {severity: "error", code: "required", details: ""};
	issue.details = "Submit Order request is not sent. OML_O21 is missing required element" + ((error.length == 1) ? "" : "s");
	var outcome = getOperationOutcome(error, issue, messages, channelMap.get("FeedUUID"), globalChannelMap.get("outcomeFormat"));

	// To be sent to original feed submitter
	responseMap.put("Outcome", outcome);

	// Nothing to send to DEX
	channelMap.put("RequestMessage", null);

} else {

	// Temporary save the OML_O21 message for the next destination to process

//// !!!!!!!!!! TEST ONLY !!!!!!!!!!!!!!	
	channelMap.put("RequestMessage", null);
	//var info = {severity: "Information", code: "Information", details: "If you see this then the HL7 message is successfully created"};
	//(AM) operation outcome mapping expects lowercase; code is 'informational' not 'information'
	var info = {severity: "information", code: "informational", details: "If you see this then the HL7 message is successfully created"};
	outcome = getOperationOutcome(error, info, messages, channelMap.get("FeedUUID"), globalChannelMap.get("outcomeFormat"));	
	responseMap.put("Outcome", outcome);
//// !!!!!!!!!! TEST ONLY !!!!!!!!!!!!!!

	// (SN) Enable this line to actually submit the SubmitOrder request
	//channelMap.put("RequestMessage", messages.request);
}

var 	submitOrderSoap = <soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:ord="http://medplus.com/orders">
				   <soapenv:Header/>
				   <soapenv:Body>
				      <ord:submitOrder>
				         <ord:SubmitOrderRequest>
				            <hl7Order>OML_O21</hl7Order>
				         </ord:SubmitOrderRequest>
				      </ord:submitOrder>
				   </soapenv:Body>
				</soapenv:Envelope>;

var oml = channelMap.get("RequestMessage");
if (oml != null && oml.toString().length() > 0) {

	var bytes = new java.lang.String(oml.toString()).getBytes();
	submitOrderSoap.*::Body.*::submitOrder.*::SubmitOrderRequest.hl7Order = FileUtil.encode(bytes).replaceAll('\r\n|\r|\n', '');

} else {
	submitOrderSoap = "";
	return;
}

var sslClient = globalChannelMap.get("sslClientClass");

if (sslClient != null && submitOrderSoap.toString().length > 0) try {

	var error = [];
	var messages = {request: submitOrderSoap.toString(), resp: ""};
	var issue = {severity: "", code: "", details: ""};

	sslClient.setURL( $cfg("submitOrderURL") );
	sslClient.setSOAPRequest(submitOrderSoap);

	// Submit the request
	var httpCode = sslClient.doPost();

	// Get the response with all namespeces preserved
	var soapResp = new XML(sslClient.getSOAPResponse(true));

	if (200 == httpCode) {

		var orderACK = soapResp.*::Body.*::submitOrderResponse.*::Result.hl7OrderAck.toString();
		var ackHL7 = new java.lang.String(FileUtil.decode(orderACK));
		messages.resp = ackHL7;

		var ack = new XML(SerializerFactory.getSerializer('HL7V2').toXML(ackHL7));

		// Check Acknowledgment Code: AA - Accept, AE - Error
		if (ack['MSA']['MSA.1']['MSA.1.1'].toString() == "AA") {
			issue.severity = "information";
			issue.code = "informational";
			issue.details = "SUCCESS";			
		} else {
			// Acknowledgment Code == AE or anything else 
			issue.severity = "error";
			issue.code = "processing";
			issue.details = "Order was not successfully processed";		
			error = ack['MSA']['MSA.6']['MSA.6.2'].toString().split(",");	
		} 

	} else {
		// HTTP code is not 200 OK
		issue.severity = "error";
		issue.code = "processing";
		issue.details = "HTTP Code: " + httpCode;
	}	

} catch(ex) {

	issue.severity = "fatal";
	issue.code = "exception";
	issue.details = "SubmitOrder WS Transformer: " + ex;

} finally {

	// Return submission results
	var outcome = getOperationOutcome(error, issue, messages, channelMap.get("FeedUUID"), globalChannelMap.get("outcomeFormat"));
	responseMap.put("Outcome", outcome);
	logger.debug("Outcome (Submit Order WS): " + outcome);
}

channel.preprocessingScript:
// Modify the message variable below to pre process data

// Delete some characters that may break JSON to XML parsing
message = message.replace(/null/g,'""');
message = message.replace(/&/g,"&amp;"); // ampersand
message = message.replace(/</g,"&lt;");  // less than
message = message.replace(/>/g,"&gt;");  // greater than
message = message.replace(/""""/g,'""'); // quotes around quotes

return message;

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map
return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data

// Format of the OperationOutcome. Supported: "XML" or "JSON"
globalChannelMap.put("outcomeFormat", "XML");

// Prepare SSL Client to access submitOrder Conformance Environment
if (globalMap.containsKey("sslClient") && globalMap.get("sslClient")) 
	try {

		var sslClient = new Packages.org.safemovement.DEXSSLClient();		
		sslClient.setAuthorizationType(org.safemovement.DEXSSLClient.Authorization.BASIC);
		sslClient.setContentType("text/xml;charset=UTF-8");
		sslClient.setUserName($("username"));
		sslClient.setUserPassword($("userpsw"));		
		sslClient.setConnectionType("Close");

		globalChannelMap.put("sslClientClass", sslClient);

	} catch(ex) {
		logger.error("SubmitOrder: Deploy - cannot initialize SSLClient - " + ex);

		// Stop this channel to resolve the problem
		ChannelUtil.stopChannel(channelId);
		throw ("SubmitOrder: Deploy - cannot initialize SSLClient - " + ex);		
	}



return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data

if (globalChannelMap.containsKey("sslClientClass")){
	globalChannelMap.remove("sslClientClass");
}


return;

com.mirth.connect.plugins.javascriptstep.script:
var error = [];				// A list of possible incoming JSON feed validation errors
var hasPatient = false;			// PID
var hasDiagnosticReport = false;	// ORC
var hasDiagnosticOrder = false;	// OBR
var hasCoverage = false;			// IN1
var hasPerson = false;			// GT1
var hasPractitioner = false;		// OBR.16
var conditionCount = 0;

// Feed UUID ~~ RKW The placement of static text in an ID value is pointless, and usually requires stripping out later - such as here. How about not putting it there in the first place?
var uuid = msg.Bundle.id.value.toString().toLowerCase().replace("oml-o21","");

// TODO: Define min length for UUID
if (uuid)
	channelMap.put("FeedUUID", uuid);
else 
	error.push("Feed Bundle id/@value should contain UUID in format OML-O21-xxxxxxxx-...");	


// The Data Exchange verifies that fields are populated depending on the coverage type
var billType = ['T','P','C'];
var coverageType;
msg.Bundle.entry.forEach(function(obj){
	if (obj.resource.Coverage) {
		coverageType = obj.resource.Coverage.type.code.value.toString();
		if (billType.indexOf(coverageType) < 0) error.push("resource.Coverage.type.code.value for IN1.47[Coverage Type] is required and must be T, P or C");
	}
});

// Store IN1.47 [Coverage Type] value for future use
channelMap.put("CoverageType", coverageType);


// Validate incoming JSON field for required resources (aka segments)
msg.Bundle.entry.forEach(function(objRes){

	// Need to set the hasSomeResource only once
	if (objRes.resource.Patient != null && objRes.resource.Patient != undefined) hasPatient = true;
	if (objRes.resource.DiagnosticReport != null && objRes.resource.DiagnosticReport != undefined) hasDiagnosticReport = true;
	if (objRes.resource.DiagnosticOrder != null && objRes.resource.DiagnosticOrder != undefined) hasDiagnosticOrder = true;
	if (objRes.resource.Coverage != null && objRes.resource.Coverage != undefined) hasCoverage = true;
	if (objRes.resource.Person != null && objRes.resource.Person != undefined) hasPerson = true;
	if (objRes.resource.Practitioner != null && objRes.resource.Practitioner != undefined) hasPractitioner = true;
	if (objRes.resource.Condition != null && objRes.resource.Condition != undefined) {
		conditionCount ++;
	}
});


// Validate for required segments
if (!hasPatient) error.push("resource.Patient for PID segment is Required");
if (!hasCoverage) error.push("resource.Coverage for IN1 segment is Required");
if (!hasDiagnosticReport) error.push("resource.DiagnosticReport for ORC segment is Required"); // ~~ RKW - Expecting a diagnostic report - a result observation artifact - in an order? Why? 
if (!hasDiagnosticOrder) error.push("resource.DiagnosticOrder for OBR segment is Required");
if (!hasPractitioner) error.push("resource.Practitioner for OBR.16 [Ordering Provider] field is Required");
if (coverageType == "T" && !hasPerson) error.push("resource.Person to populate GT1 segment is Required for Third-Party Bill type");

// Updated to comply with CS12 Billing certification criteria
if (coverageType == "P" && !hasPerson) error.push("resource.Person to populate GT1 segment is Required for Patient Bill type");
if (conditionCount > 12) error.push("Maximum of 12 resource.Condition with unique ICD-10 codes are permitted. You sent " + conditionCount);  // ~~ RKW - Conditions? Not going to happen, and not in requirements

// Verify for errors 
if (error.length > 0) {

	// OML_O21 message will not be created ~~ RKW. There is this thing called a filter. Manipulation of the destinationSet is hacking, as it makes the channel more opaque.
	destinationSet.removeAll();

	var messages = {request:"", resp:""};
	var issue = {severity: "error", code: "required", details: ""};
	issue.details = "Submit Order request is not sent. OML_O21 is missing required element" + ((error.length == 1) ? "" : "s");

	var outcome = getOperationOutcome(error, issue, messages, channelMap.get("FeedUUID"), globalChannelMap.get("outcomeFormat"));

	// To be sent to the upstream channel
	responseMap.put("Outcome", outcome);
	//logger.debug("SubmitOrder (Source): " + outcome);
}

// OML_O21 template with placeholders for required fields only
var oml_template = "MSH|^~\&|||||||OML^O21^OML_O21|||2.5.1\r" + "PID|1|||||||\r";
var oml = new XML(SerializerFactory.getSerializer('HL7V2').toXML(oml_template));

// MSH segment required fields (may be overwritten by MessageHeader resource)
oml['MSH']['MSH.3'] = $("application");
oml['MSH']['MSH.4'] = $("account");
oml['MSH']['MSH.6'] = $("receivingFacility");

// Overrides
oml['MSH']['MSH.6'] = "MET";
oml['MSH']['MSH.5'] = "PSC";
oml['MSH']['MSH.4'] = "90046003";

// (Quest) Note: All date timestamps are set to Coordinated Universal Time (UTC).
var currentDate = DateUtil.getCurrentDate("yyyyMMddHHmmssZ");
oml['MSH']['MSH.7'] = currentDate;

// Message Control ID is calculated based on date of submission
oml['MSH']['MSH.10'] = currentDate.toString().substring(0,14) + UUIDGenerator.getUUID().toString().substring(0,6);
oml['MSH']['MSH.11'] = $("processingID");


// A list of possible incoming JSON feed validation errors based on IN1.47 [Coverage Type] value
var error = [];

// Global NTE Set ID value
var countNTE = 1; 

// Insurance list to retain primary and secondary
var coverageMap = new Packages.java.util.HashMap(2);

// Guarantor
var guarantorMap = new Packages.java.util.HashMap(1);

// Common Order
var commonOrderMap = new Packages.java.util.HashMap(4);
var placedOrderNumberMap = new Packages.java.util.HashMap(4);

// Timing/Quantity
var timingQuantityMap = new Packages.java.util.HashMap(4);

// Observation Request
var observationRequestMap = new Packages.java.util.HashMap(4);
var observationRequestCommentsMap = new Packages.java.util.HashMap(4);

// Diagnosis
var diagnosisMap = new Packages.java.util.HashMap(12);

// Observation/Result aka AOEs
var observationMap = new Packages.java.util.HashMap(4);
var observationCommentsMap = new Packages.java.util.HashMap(4);
var observationReferencesMap = new Packages.java.util.HashMap(4);

// ORC.12 Ordering Provider
// OBX.16 Ordering Provider
var practitionerMap = new Packages.java.util.HashMap(4);



// Preliminary store referenced resources
for each (obj in msg.Bundle.entry) {

	if (obj.resource.Practitioner != null && obj.resource.Practitioner != undefined) {
logger.error( "Found Practitioner" );	


		// Practitioner is required to complete ORC and OBR segments
		var id = ((obj.resource.Practitioner.id != undefined) ? obj.resource.Practitioner.id.value.toString() : "");
		if (id.length > 0)
			practitionerMap.put(id.toString(), obj);
	}
}

logger.error( "About to iterate: " + obj.resource.toString() );	
// Iterate over all inbound feed resource
for each (obj in msg.Bundle.entry) {

logger.error( "Resource: \n" + JSON.stringify( obj.resource ) );	

	if (obj.resource.MessageHeader != null && obj.resource.MessageHeader != undefined) {
logger.error( "Found MessagerHeader" );			
		// Update MessageHeader segment
		processMessageHeader(obj);

	} else if (obj.resource.Patient != null && obj.resource.Patient != undefined) {
		// Populate PID segment
		processPatient(obj);

	} else if (obj.resource.DiagnosticReport != null && obj.resource.DiagnosticReport != undefined) {
logger.error( "Found Report");	
		// Populate ORC segment
		processCommonOrder(obj);

	} else if (obj.resource.DiagnosticOrder != null && obj.resource.DiagnosticOrder != undefined) {
logger.error( "Found Order");	
		// Populate OBR segment
		processObservationRequest(obj);

	} else if (obj.resource.Coverage != null && obj.resource.Coverage != undefined) {
		// Populate IN1 segment
		processCoverage(obj);

	} else if (obj.resource.Person != null && obj.resource.Person != undefined) {
		// Populate GT1 segment
		processGuarantor(obj);

	} else if (obj.resource.Observation != null && obj.resource.Observation != undefined) {
		// Store Observation resource for OBX segment
		processObservation(obj);
	}

}


// ==================== COMPILE THE OML MESSAGE ====================

// Required primary insurance segment
var segInsurancePrimary = coverageMap.get(1);
if (segInsurancePrimary != null) try {
	oml.appendChild(new XML(segInsurancePrimary));
} catch(ex) {
	logger.error("SubmitOrder-Destination: Error adding primary insurance segment - " + ex);
}
// Optional secondary insurance segment
var segInsuranceSecondary = coverageMap.get(2);
if (segInsuranceSecondary != null) try {
	oml.appendChild(new XML(segInsuranceSecondary));
} catch(ex) {
	logger.error("SubmitOrder-Destination: Error adding secondary insurance segment - " + ex);
} 



// Optional Guarantor segment
var segGuarantor = guarantorMap.get(1);
if (segGuarantor != null) try {
	oml.appendChild(new XML(segGuarantor));
} catch(ex) {
	logger.error("SubmitOrder-Destination: Error adding Guarantor segment - " + ex);	
}



// Required ORDER group
for (var i = 0; i < commonOrderMap.size(); i++) {

	// ORC [Common Order] segment
	var segCommonOrder = commonOrderMap.get((i+1).toString());
	if (segCommonOrder != null) try {
		oml.appendChild(new XML(segCommonOrder));
	} catch(ex) {
		logger.error("SubmitOrder-Destination: Error adding CommonOrder segment - " + ex);
	}

	// TQ1 [Timing/Quantity] segment
	var segTimingQuantity = timingQuantityMap.get((i+1).toString());
	if (segTimingQuantity != null) try {
		oml.appendChild(new XML(segTimingQuantity));
	} catch(ex) {
		logger.error("SubmitOrder-Destination: Error adding Timing/Quantity segment - " + ex);
	}

	// OBR [Observation Request] segment
	var segObservation = observationRequestMap.get((i+1).toString());
	if (segObservation != null) try {
		oml.appendChild(new XML(segObservation));
	} catch(ex) {
		logger.error("SubmitOrder-Destination: Error adding Observation Request segment - " + ex);
	}


	// Observation level NTE [Comments] segments
	var obsComments = observationRequestCommentsMap.get((i+1).toString());
	if (obsComments != null && Array.isArray(obsComments)) {
		for (var kk = 0; kk < obsComments.length; kk++) {
			var nte = obsComments[kk];
			if (nte.toString() != "") try {
				oml.appendChild(new XML(nte.toString()));
			} catch(ex) {
				logger.error("SubmitOrder-Destination: Error adding Observation Comment segment - " + ex);
			}
		}
		obsComments.length = 0;
	}

	// DG1 [Diagnosis] segments
	var conditions = diagnosisMap.get((i+1).toString());
	if (conditions != null && Array.isArray(conditions)) {
		for (var dd = 0; dd < conditions.length; dd++) {
			var dg1 = conditions[dd];
			if (dg1.toString() != "") try {
				oml.appendChild(new XML(dg1.toString()));
			} catch(ex) {
				logger.error("SubmitOrder-Destination: Error adding Diagnosis segment - " + ex);
			}
		}
		conditions.length = 0;		
	}

	// OBX [Observation] segments
	// Get the list of OBX segment references
	var obsReference = observationReferencesMap.get((i+1).toString());
	if (obsReference != null && Array.isArray(obsReference)) {
		for (var rr = 0; rr < obsReference.length; rr++) {
			// Get the actual OBX segments
			var obx = observationMap.get(obsReference[rr]);
			if (obx != null && obx.toString() != "") try {
				oml.appendChild(new XML(obx.toString()));

				// OBX level NTE [Comment] segments
				var observationComments = observationCommentsMap.get(obsReference[rr]);
				if (observationComments != null && Array.isArray(observationComments)) {
					for (var oc = 0; oc < observationComments.length; oc++) {
						var obxNTE = observationComments[oc];
						if (obxNTE != null && obxNTE.length > 0)
							oml.appendChild(new XML(obxNTE.toString()));
					}
				}
				observationComments.length = 0;

			} catch(ex) {
				logger.error("SubmitOrder-Destination: Error adding OBX segment - " + ex);
			}		
		}		
		obsReference.length = 0;		
	}

}


// Clear all temporary maps
coverageMap.clear();
guarantorMap.clear();
commonOrderMap.clear();
placedOrderNumberMap.clear();
timingQuantityMap.clear();
observationRequestMap.clear();
observationRequestCommentsMap.clear();
diagnosisMap.clear();
observationMap.clear();
observationCommentsMap.clear(); 
observationReferencesMap.clear();
practitionerMap.clear();

/**
	Populates MSG segment with data from the MessageHeader resource
	@param {Object} obj - MessageHeader object resource
	@return - nothing, uses global HL7v2 message object
*/
function processMessageHeader(obj) {

	if (obj.resource.MessageHeader.source) {

		// The name of the sending application
		var sendingApplication = ((obj.resource.MessageHeader.source.name) ? obj.resource.MessageHeader.source.name.value.toString() : "");
		if (sendingApplication) oml['MSH']['MSH.3'] = sendingApplication;

		// Primary or secondary account number(s) defined for the placer. ~~ RKW - And the actual account number was less important?
		var sendingFacility = ((obj.resource.MessageHeader.source.endpoint) ? obj.resource.MessageHeader.source.endpoint.value.toString() : "");
		if (sendingFacility) oml['MSH']['MSH.4'] = sendingFacility;

	} 

	if (obj.resource.MessageHeader.destination) {

		// If provided must be hard-coded to "PSC" for PSC hold orders. If the <name> element is not provided or value is other than "PSC" the order is considered to be a basic lab order.
		var receivingApplication = ((obj.resource.MessageHeader.destination.name) ? obj.resource.MessageHeader.destination.name.value.toString() : "");
		if (receivingApplication) oml['MSH']['MSH.5'] = receivingApplication;


		// The receiving facility. The three-letter ID of the facility that performs the test.
		var receivingFacility = ((obj.resource.MessageHeader.destination.endpoint) ? obj.resource.MessageHeader.destination.endpoint.value.toString() : "");
		if (receivingFacility) oml['MSH']['MSH.6'] = receivingFacility;			
	}

	return;
}

/**
	Populates PID segment with data from the Patient resource
	@param {Object} obj - Patient object resource
	@return - nothing, uses global HL7v2 message object
*/
function processPatient(obj) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");
	var hasAddress = false;
	var hasPhone = false;

	var comments = [];

	// Iterate over all patient identifiers
	obj.resource.Patient.identifier.forEach(function(item){

		// PID.3 [Patient Identifier List]
		if (item.use.value.toString() == "official") {
			var id = item.value.value.toString();
			if (id) 
				oml['PID']['PID.3']['PID.3.1'] = id;

			var type = ((item.type && item.type.coding) ? item.type.coding.code.value.toString() : "");
			if (type)
				oml['PID']['PID.3']['PID.3.5'] = type;

			var assigner = ((item.assigner) ? item.assigner.display.value.toString() : "");
			if (assigner)
				oml['PID']['PID.3']['PID.3.4'] = assigner;								

		// PID.2 [Patient ID]	
		} else if (item.use.value.toString() == "secondary") {

			var id = item.value.value.toString();
			if (id) 
				oml['PID']['PID.2']['PID.2.1'] = id;

			var type = ((item.type && item.type.coding) ? item.type.coding.code.value.toString() : "");
			if (type)
				oml['PID']['PID.2']['PID.2.5'] = type;			

		}

	});


	// Parse patient's name with DEX name length limitation applied
	var nameParts = processPersonName(obj.resource.Patient.name, true);
	oml['PID']['PID.5']['PID.5.1'] = nameParts.family;
	if (nameParts.given1) oml['PID']['PID.5']['PID.5.2'] = nameParts.given1;
	if (nameParts.given2) oml['PID']['PID.5']['PID.5.3'] = nameParts.given2;


	// Patient's DOB
	if (obj.resource.Patient.birthDate) {
		var dob = obj.resource.Patient.birthDate.value.toString();
		oml['PID']['PID.7'] = dob.replace(/-/g,"");
	} else {
		error.push("resource.Patient.birthDate for PID.7[DateTime of Birth] is required");
	}

	// Administrative Gender 
	if (obj.resource.Patient.gender) {
		var gender = obj.resource.Patient.gender.value;
		oml['PID']['PID.8'] = convertTable("0001", gender);
	} else {
		error.push("resource.Patient.gender for PID.8[Administrative Sex] is required");		
	}




	// Patient Address
	if (obj.resource.Patient.address) {

		var street = ((obj.resource.Patient.address.line) ? obj.resource.Patient.address.line.value.toString() : "");
		if (street)
			oml['PID']['PID.11']['PID.11.1'] = street;

		var city = ((obj.resource.Patient.address.city) ? obj.resource.Patient.address.city.value.toString() : "");
		if (city)
			oml['PID']['PID.11']['PID.11.3'] = city;	

		var state = ((obj.resource.Patient.address.state) ? obj.resource.Patient.address.state.value.toString() : "");
		if (state)
			oml['PID']['PID.11']['PID.11.4'] = state;

		var postal = ((obj.resource.Patient.address.postalCode) ? obj.resource.Patient.address.postalCode.value.toString() : "");
		if (postal)
			oml['PID']['PID.11']['PID.11.5'] = postal;

		var country = ((obj.resource.Patient.address.country) ? obj.resource.Patient.address.country.value.toString() : "");
		if (country)
			oml['PID']['PID.11']['PID.11.6'] = country;

		// There might be a better definition what is required "patients address".
		hasAddress = (street || city || state || postal);	
	}

	// Patient Phone Number (Home)	
	var phone = ((obj.resource.Patient.telecom) ? obj.resource.Patient.telecom.value.value.toString() : "");
	if (phone) {
		hasPhone = true;
		oml['PID']['PID.13'] = convertPhoneXTN(phone);
	}


	// Validate conditional fields
	if (coverage == "P" || coverage == "T") {
		if (!hasAddress) error.push("resource.Patient.address for PID.11[Patient Address] is required if IN1.47[Coverage Type] is P or T");
		if (!hasPhone) error.push("resource.Patient.telecom for PID.13[Phone Number-Home] is required if IN1.47[Coverage Type] is P or T");
	}


	// Iterate over all possible comment entries in /text/div/p  (#CS20 certification test case)
	if (obj.resource.Patient.text && obj.resource.Patient.text.div) {	

		var internalCommentsCount = 0; //  only 5 NTE segments of 60 characters each
		var reportCommentsCount = 0; // only 2 NTE segments of 60 characters each
		var sourceOfComment = ["L","P","O", ""]; // Optional source of comments

		if (obj.resource.Patient.text.div.p.length && obj.resource.Patient.text.div.p.length > 0) {
			obj.resource.Patient.text.div.p.forEach(function(item){
				// For internal (I) or report (R) comments restriction applies
				if ((item.class.toString() == "I" && internalCommentsCount++ < 5) || 
					(item.class.toString() == "R" && reportCommentsCount++ < 2)) {
						comments.push(getNTESegment(item, countNTE++));
				} else if (sourceOfComment.indexOf(item.class.toString()) > -1) {
					// If the comment is not internal (I) or report (R) then accept
						comments.push(getNTESegment(item, countNTE++));
				} 		
			});	
		} else if (obj.resource.Patient.text.div.p) {
			// A single comment from any acceptable source
			sourceOfComment = ["I","R","L","P","O", ""];
			if (sourceOfComment.indexOf(obj.resource.Patient.text.div.p.class.toString()) > -1)
				comments.push(getNTESegment(obj.resource.Patient.text.div.p, countNTE++));
		}

		for (var i = comments.length - 1; i >= 0; i--) {
			var nte = comments[i];
			if (nte.toString() != "")
				oml['PID'] += new XML(nte.toString());
		}

		comments.length = 0;
	}

	return;
}

/**
	Populates IN1 [Insurance] segments with data from the primary and secondary Coverage resources	
	@param {Object} obj - Coverage object resource
	@return - nothing, uses global HL7v2 message object
*/
function processCoverage(obj) {

	var nameParts = {family: "", given1: "", given2: ""};

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");
	var isContainedPerson = hasPersonAddress = false;
	var isContainedOrganization = hasOrganizationAddress = false;

	var segIns = <IN1>
				<IN1.1>
					<IN1.1.1>1</IN1.1.1>
				</IN1.1>
				<IN1.2/><IN1.3/><IN1.4/><IN1.5/><IN1.6/><IN1.7/><IN1.8/><IN1.9/>
				<IN1.10/><IN1.11/><IN1.12/><IN1.13/><IN1.14/><IN1.15/><IN1.16/><IN1.17/><IN1.18/><IN1.19/>
				<IN1.20/><IN1.21/><IN1.22/><IN1.23/><IN1.24/><IN1.25/><IN1.26/><IN1.27/><IN1.28/><IN1.29/>
				<IN1.30/><IN1.31/><IN1.32/><IN1.33/><IN1.34/><IN1.35/><IN1.36/><IN1.37/><IN1.38/><IN1.39/>
				<IN1.40/><IN1.41/><IN1.42/><IN1.43/><IN1.44/><IN1.45/><IN1.46/><IN1.47/>
			</IN1>;

	// Insurance Set ID
	var coverageType = 1; 
	var setID = obj.resource.Coverage.id.value.toString();
	if (setID.indexOf("2") > -1) {
		segIns['IN1.1'] = "2";
		coverageType = 2; // secondary - "IN1-insurance-2"
	} else {
		segIns['IN1.1'] = "1";
		coverageType = 1; // primary - "IN1-insurance-1"		
	}


	// Insurance Plan ID
	var planID = obj.resource.Coverage.identifier.value.value.toString();
	if (planID)
		segIns['IN1.2'] = planID;


	if (obj.resource.Coverage.contained.length && obj.resource.Coverage.contained.length > 0) {

		// Loop through contained resources
		obj.resource.Coverage.contained.forEach(function(item){

			// Process contained insurance company
			processInsuranceOrganization(segIns, item);

			if (item.Person != null && item.Person != undefined) {

				isContainedPerson = true;

				// Process insured person name with DEX name length limitation applied
				nameParts = processPersonName(item.Person.name, true);

				// Insured's Address
				if (item.Person.address) {
					var street = item.Person.address.line.value.toString();
					if (street)
						segIns['IN1.19']['IN1.19.1'] = street;

					var city = ((item.Person.address.city) ? item.Person.address.city.value.toString() : "");
					if (city)
						segIns['IN1.19']['IN1.19.3'] = city;	

					var state = ((item.Person.address.state) ? item.Person.address.state.value.toString() : "");
					if (state)
						segIns['IN1.19']['IN1.19.4'] = state;

					var postal = ((item.Person.address.postalCode) ? item.Person.address.postalCode.value.toString() : "");
					if (postal)
						segIns['IN1.19']['IN1.19.5'] = postal;

					var country = ((item.Person.address.country) ? item.Person.address.country.value.toString() : "");
					if (country)
						segIns['IN1.19']['IN1.19.6'] = country;

					// There might be a better definition what is required "insureds Address".
					hasPersonAddress = (street || city || state || postal);	
				}
			}
		});

	} else {
		// When only contained  insurance company is present
		processInsuranceOrganization(segIns, obj.resource.Coverage.contained);
	}

	// Insured's Group Employer Name 
	if (obj.resource.Coverage.extension) {
		var emplName = ((obj.resource.Coverage.extension.valueIdentifier) ? obj.resource.Coverage.extension.valueIdentifier.value.value.toString() : "");
		if (emplName)
			segIns['IN1.11'] = emplName;
	}


	// Group Number
	var groupNumber = ((obj.resource.Coverage.group) ? obj.resource.Coverage.group.value.toString() : "");
	if (groupNumber)
		segIns['IN1.8'] = groupNumber;





	// Name Of Insured
	// (SN) March 11, 2017: Fix to downgrade to DSTU3 Coverage resource. Previously checked only planholderReference.
/*	
	var insuredRef;
	if (obj.resource.Coverage.planholderReference) {
		// STU3
		insuredRef = obj.resource.Coverage.planholderReference.reference.value.toString();
	} else if (obj.resource.Coverage.subscriberReference) {
		// DSTU3
		insuredRef = obj.resource.Coverage.subscriberReference.reference.value.toString();
	}
*/

	// Name Of Insured
	// (SN) March 13 2017: Disabled
	//var insuredRef = obj.resource.Coverage.planholderReference.reference.value.toString();

	var insuredRef;
	if (obj.resource.Coverage.planholderReference) {
		// STU3
		insuredRef = obj.resource.Coverage.planholderReference.reference.value.toString();
	} else if (obj.resource.Coverage.subscriberReference) {
		// DSTU3
		insuredRef = obj.resource.Coverage.subscriberReference.reference.value.toString();
	}

	if (insuredRef == "Patient/PID-patient-identification") {
		// Same as the PID segment
		segIns['IN1.16']['IN1.16.1'] = oml['PID']['PID.5']['PID.5.1'].toString();
		if (oml['PID']['PID.5']['PID.5.2'].toString())
			segIns['IN1.16']['IN1.16.2'] = oml['PID']['PID.5']['PID.5.2'].toString();
		if (oml['PID']['PID.5']['PID.5.3'].toString())
			segIns['IN1.16']['IN1.16.3'] = oml['PID']['PID.5']['PID.5.3'].toString();

	} else if (insuredRef == "Person/insured-person") {
		// Taken from the contained Person resource
		segIns['IN1.16']['IN1.16.1'] = nameParts.family;
		if (nameParts.given1.length > 0) segIns['IN1.16']['IN1.16.2'] = nameParts.given1;
		if (nameParts.given2.length > 0) segIns['IN1.16']['IN1.16.3'] = nameParts.given2;		
	}

	// Insured's Relationship To Patient
	if ( obj.resource.Coverage.relationship) {
		var relationship = ((obj.resource.Coverage.relationship.code) ? obj.resource.Coverage.relationship.code.value.toString() : "");
		if (relationship)
			segIns['IN1.17']['IN1.17.1'] = relationship;

		var relationshipDesc = ((obj.resource.Coverage.relationship.display) ? obj.resource.Coverage.relationship.display.value.toString() : "");
		if (relationshipDesc)
			segIns['IN1.17']['IN1.17.2'] = relationshipDesc;
	}

	// Company Plan Code
	var planCode = (( obj.resource.Coverage.subPlan) ? obj.resource.Coverage.subPlan.value.toString() : "");
	if (planCode)
		segIns['IN1.35'] = planCode;

	// Policy Number
	var policyNumber = ((obj.resource.Coverage.plan) ? obj.resource.Coverage.plan.value.toString() : "");
	if (policyNumber)
		segIns['IN1.36'] = policyNumber;

	// Coverage Type
	segIns['IN1.47'] = channelMap.get("CoverageType");


	if (coverage == "T") {
		if (segIns['IN1.4'].toString() == "") error.push("resource.Coverage.contained.Organization.name for IN1.4[Insurance Company Name] is required if IN1.47[Coverage Type] is T");
		if (segIns['IN1.8'].toString() == "") error.push("resource.Coverage.group for IN1.8[Group Number] is required if IN1.47[Coverage Type] is T");
		if (segIns['IN1.16']['IN1.16.1'].toString() == "") error.push("resource.Coverage.contained.Person.name for IN1.16[Name Of Insured] is required if IN1.47[Coverage Type] is T");
		if (segIns['IN1.17']['IN1.17.1'].toString() == "") error.push("resource.Coverage.relationship.code for IN1.17[Insured Relationship To Patient] is required if IN1.47[Coverage Type] is T");
		if (isContainedPerson && !hasPersonAddress) error.push("resource.Coverage.contained.Person.address for IN1.19[Insured Address] is required if IN1.47[Coverage Type] is T");
		if (segIns['IN1.36'].toString() == "") error.push("resource.Coverage.plan for IN1.36[Policy Number] is required if IN1.47[Coverage Type] is T");		
	}


	// Store the segment to be later compiled into a message
	coverageMap.put(coverageType, segIns.toString());

	return;
}


/**
	Populates IN1.3 [Insurance Company ID] field with data from the contained Organization resource
	@param {Object} obj - Organization object resource
	@return - nothing, uses global HL7v2 message object
*/
function processInsuranceOrganization(segIns, item) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");
	var hasAddress = false;

	if (item.Organization != null && item.Organization != undefined) {

		isContainedOrganization = true;

		// Insurance Company ID
		var companyID = ((item.Organization.identifier) ? item.Organization.identifier.value.value.toString() : "");
		if (companyID) 
			segIns['IN1.3'] = companyID;

		// Insurance Company Name
		var companyName = ((item.Organization.name) ? item.Organization.name.value.toString() : "");
		if (companyName)
			segIns['IN1.4'] = companyName;

		// Insurance Company Address
		if (item.Organization.address) {
			var street = ((item.Organization.address.line) ? item.Organization.address.line.value.toString() : "");
			if (street)
				segIns['IN1.5']['IN1.5.1'] = street;

			var city = ((item.Organization.address.city) ? item.Organization.address.city.value.toString() : "");
			if (city)
				segIns['IN1.5']['IN1.5.3'] = city;

			var state = ((item.Organization.address.state) ? item.Organization.address.state.value.toString() : "");
			if (state)
				segIns['IN1.5']['IN1.5.4'] = state;

			var postal = ((item.Organization.address.postalCode) ? item.Organization.address.postalCode.value.toString() : "");
			if (postal)
				segIns['IN1.5']['IN1.5.5'] = postal;

			var country = ((item.Organization.address.country) ? item.Organization.address.country.value.toString() : "");
			if (country)
				segIns['IN1.5']['IN1.5.6'] = country;

			// There might be a better definition what is required "Insurance Company Address"
			hasOrganizationAddress = (street || city || state || postal);						
		}		

		// Insurance Co Phone Number
		var phone = ((item.Organization.telecom) ? item.Organization.telecom.value.value.toString() : "");
		if (phone) {
			hasPhone = true;
			segIns['IN1.7'] = convertPhoneXTN(phone);
		}
	}

	// Validate conditional fields
	if (coverage == "T") {		
		if (isContainedOrganization && !hasOrganizationAddress) error.push("resource.Coverage.contained.Organization.address for IN1.5[Insurance Company Address] is required if IN1.47[Coverage Type] is T");
	}


	return;
}

/**
	Creates and populates GT1 [Guarantor] segment from the Person resource
	@param {Object} obj - Coverage object resource
	@return - nothing, uses global HL7v2 message object
*/
function processGuarantor(obj) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");
	var hasAddress = false;
	var hasPhone = false;

	var gt1 = <GT1>
				<GT1.1><GT1.1.1>1</GT1.1.1></GT1.1>
				<GT1.2/>
				<GT1.3><GT1.3.1/><GT1.3.2/></GT1.3>
			</GT1>;

	// Parse guarantor's name with DEX name length limitation applied
	var nameParts = processPersonName(obj.resource.Person.name, true);
	gt1['GT1.3']['GT1.3.1'] = nameParts.family;
	if (nameParts.given1.length > 0) gt1['GT1.3']['GT1.3.2'] = nameParts.given1;
	if (nameParts.given2.length > 0) gt1['GT1.3']['GT1.3.3'] = nameParts.given2;	


	// Guarantor Address
	if (obj.resource.Person.address) {
		var street = ((obj.resource.Person.address.line) ? obj.resource.Person.address.line.value.toString() : "");
		if (street)
			gt1['GT1.5']['GT1.5.1'] = street;

		var city = ((obj.resource.Person.address.city) ? obj.resource.Person.address.city.value.toString() : "");
		if (city)
			gt1['GT1.5']['GT1.5.3'] = city;	

		var state = ((obj.resource.Person.address.state) ? obj.resource.Person.address.state.value.toString() : "");
		if (state)
			gt1['GT1.5']['GT1.5.4'] = state;

		var postal = ((obj.resource.Person.address.postalCode) ? obj.resource.Person.address.postalCode.value.toString() : "");
		if (postal)
			gt1['GT1.5']['GT1.5.5'] = postal;

		var country = ((obj.resource.Person.address.country) ? obj.resource.Person.address.country.value.toString() : "");
		if (country)
			gt1['GT1.5']['GT1.5.6'] = country;

		// There might be a better definition what is required "guarantor address".
		hasAddress = (street || city || state || postal);			
	}


	// Guarantor Phon Number - Home
	var phone = ((obj.resource.Person.telecom) ? obj.resource.Person.telecom.value.value.toString() : "");
	if (phone) {
		hasPhone = true;
		gt1['GT1.6'] = convertPhoneXTN(phone);
	}


	if (coverage == "P" || coverage == "T") {
		if (gt1['GT1.3']['GT1.3.1'].toString() == "") error.push("resource.Person.name for GT1.3[Guarantor Name] is required if IN1.47[Coverage Type] is P or T");
		if (!hasAddress) error.push("resource.Person.address for GT1.5[Guarantor Address] is required if IN1.47[Coverage Type] is P or T");
		if (!hasPhone) error.push("resource.Person.telecom for GT1.6[Guarantor Phone Number-Home] is required if IN1.47[Coverage Type] is P or T");		
	}

	// Store the segment to be later compiled into a message
	guarantorMap.put(1, gt1.toString());

	return;
}

/**
	Populates ORC [Common Order] segment from the DiagnosticReport resource
	@param {Object} obj - DiagnosticReport object resource
	@return - nothing, uses global HL7v2 message object
*/
function processCommonOrder(obj) {


	var orc = <ORC><ORC.1><ORC.1.1>NW</ORC.1.1></ORC.1></ORC>;

	// Count ORDER groups
	var groupID = ((obj.resource.DiagnosticReport.id) ? obj.resource.DiagnosticReport.id.value.toString() : "");
	var groupCount = groupID.match(/\d+/);

	logger.error( "Group Count: " + groupCount );
	logger.error( "Group ID: " + groupID );

	if (groupCount == null) {
		groupCount = 1;
		error.push("resource.DiagnosticReport.id element is undefined or not properly formatted, expected: ORC-common-order-n, where n identifies the order group");
	}


	// Placer Order Number
	// The value in ORC.02.01 (Entity Identifier) is the same as the value in OBR.02.01 (Entity Identifier)
	// All values for this field in all ORC and OBR segments are identical.
	// March 12: Moved into DiagnosticReport.identifier verified block 
	//var id = obj.resource.DiagnosticReport.identifier.value.value.toString();
	//if (id) orc['ORC.2']['ORC.2.1'] = id;

	if (obj.resource.DiagnosticReport.identifier) {

		var id = ((obj.resource.DiagnosticReport.identifier.value.value) ? obj.resource.DiagnosticReport.identifier.value.value.toString():"");
		if (id) orc['ORC.2']['ORC.2.1'] = id;

		var namespaceID = ((obj.resource.DiagnosticReport.identifier.system) ? obj.resource.DiagnosticReport.identifier.system.value.toString() : "");
		if (namespaceID) orc['ORC.2']['ORC.2.2'] = namespaceID;

		var universalID = ((obj.resource.DiagnosticReport.identifier.type) ? obj.resource.DiagnosticReport.identifier.type.id.toString() : "");
		if (universalID) orc['ORC.2']['ORC.2.3'] = universalID;
	}
	else
	{
		logger.error( "Missing identifier in in Diagnostic Report:" + groupID )
	}


	// Store the Placer Order Number for the OBR segment
	var diagnosticOrderID = ((obj.resource.DiagnosticReport.request) ? obj.resource.DiagnosticReport.request.reference.value.toString() : "");
	if (diagnosticOrderID.indexOf("DiagnosticOrder/") > -1) {
		diagnosticOrderID = diagnosticOrderID.substring("DiagnosticOrder/".length, diagnosticOrderID.length);

		var placedOrderNumber = {id:"", namespaceID:"", universalID:""};

		placedOrderNumber.id = orc['ORC.2']['ORC.2.1'].toString();
		placedOrderNumber.namespaceID = orc['ORC.2']['ORC.2.2'].toString();
		placedOrderNumber.universalID = orc['ORC.2']['ORC.2.3'].toString();
		placedOrderNumberMap.put(diagnosticOrderID, placedOrderNumber);		
	}
	else
	{
		logger.error( "No Diagnostic Order Found" );
	}


	// Verified By Practitioner
	if (obj.resource.DiagnosticReport.contained != null && obj.resource.DiagnosticReport.contained != undefined) {
		var verifiedByID = obj.resource.DiagnosticReport.contained.Practitioner.identifier.value.value.toString();
		if (verifiedByID)
			orc['ORC.11']['ORC.11.1'] = verifiedByID;

		var familyName = ((obj.resource.DiagnosticReport.contained.Practitioner.name) ? obj.resource.DiagnosticReport.contained.Practitioner.name.family.value.toString() : "");
		if (familyName)
			orc['ORC.11']['ORC.11.2'] = familyName;		
	}

	// Process extension for TQ1 [Timing/Quantity] segment
	if (obj.resource.DiagnosticReport.extension.valueTiming != null && obj.resource.DiagnosticReport.extension.valueTiming != undefined) {
		processTimingQuantity(groupCount, obj.resource.DiagnosticReport.extension.valueTiming);
	}

	// Process zero or many DiagnosticReport/codedDiagnosis elements for DG1 [Diagnosis] segment
	var conditions = [];
	if (obj.resource.DiagnosticReport.codedDiagnosis) {

		if (obj.resource.DiagnosticReport.codedDiagnosis.length > 0) {
			var setID = 1;
			obj.resource.DiagnosticReport.codedDiagnosis.forEach(function(item){
				// A maximum of 12 unique ICD-10 codes are permitted per entire OML message
				// (SN) This code checks only codedDiagnosis in a single DiagnosticReport
				if (setID <= 12) conditions.push(processCondition(item, setID++));				 
			});
		} else {
			conditions.push(processCondition(obj.resource.DiagnosticReport.codedDiagnosis, 1)); 
		}
	}

	// Process zero or many result/reference elements for OBX [Observation] segment
	var observationReferences = [];
	if (obj.resource.DiagnosticReport.result) {
		var ref;
		if (obj.resource.DiagnosticReport.result.length > 0) {
			obj.resource.DiagnosticReport.result.forEach(function(item){
				ref = item.reference.value.toString();
				if (ref) observationReferences.push(ref.substring("Observation/".length, ref.length));
			});
		} else {
			ref = ((obj.resource.DiagnosticReport.result.reference != undefined) ? obj.resource.DiagnosticReport.result.reference.value.toString() : "");
			if (ref) observationReferences.push(ref.substring("Observation/".length, ref.length));
		}		
	}


	// ORC.12 [Ordering Provider] is filled when resource.Practitioner is processed (see "Provider field" step)
	var orderingProviderReferenceID = ((obj.resource.DiagnosticReport.performer != undefined) ? obj.resource.DiagnosticReport.performer.reference.value.toString() : "");
	if (orderingProviderReferenceID.indexOf("Practitioner/") > -1) {
		orderingProviderReferenceID = orderingProviderReferenceID.substring("Practitioner/".length, orderingProviderReferenceID.length);
		var objOrderingProvider = practitionerMap.get(orderingProviderReferenceID.toString());

		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.identifier != undefined) {
			var id = objOrderingProvider.resource.Practitioner.identifier.value.value.toString();
			if (id) orc['ORC.12']['ORC.12.1'] = id;

			var assigner = objOrderingProvider.resource.Practitioner.identifier.assigner.display.value.toString();
			if (assigner) orc['ORC.12']['ORC.12.9'] = assigner;
		}

		// Process ordering provider's name, DEX name length limitation does NOT apply
		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.name != undefined) {
			var nameParts = processPersonName(objOrderingProvider.resource.Practitioner.name, false);
			orc['ORC.12']['ORC.12.2'] = nameParts.family;		
			if (nameParts.given1.length > 0) orc['ORC.12']['ORC.12.3'] = nameParts.given1;		
			if (nameParts.given1.length > 0) orc['ORC.12']['ORC.12.4'] = nameParts.given2;				
		}
	}


	// Store the segment to be later compiled into a message
	commonOrderMap.put(groupCount.toString(), orc.toString());

	// Store the array of diagnosis to be later compiled into a message
	if (conditions.length > 0) diagnosisMap.put(groupCount.toString(), conditions);

	// Store the array of Observations to be later compiled into a message
	if (observationReferences.length > 0) observationReferencesMap.put(groupCount.toString(), observationReferences);


	return;
}

/**
	Populates TQ1 [Timing/Quantity] segment with data from the DiagnosticReport extension
	@param {Integer} count - Common Order group count
	@param {Object} valueTiming - valueTiming extension
	@return - nothing, uses global map
*/
function processTimingQuantity(count, valueTiming) {

	// TQ1 [Timing/Quantity] segment
	if (valueTiming != null && valueTiming != undefined) {

		var tq1 = <TQ1><TQ1.1><TQ1.1.1>1</TQ1.1.1></TQ1.1></TQ1>;

		tq1['TQ1.1']['TQ1.1.1'] = count;

		var startDateTime = ((valueTiming.event) ? valueTiming.event.value.toString() : "");
		if (startDateTime)
			tq1['TQ1.7'] = convertJSONDateTime(startDateTime);

		var priorityCode = ((valueTiming.code) ? valueTiming.code.coding.code.value.toString() : "");
		if (priorityCode)
			tq1['TQ1.9']['TQ1.9.1'] = priorityCode;
	}

	// Store the segment to be later compiled into a message
	timingQuantityMap.put(count.toString(), tq1.toString());

	return;
}

/**
	Populates OBR [Observation Request] segment from the DiagnosticReport resource
	@param {Object} obj - DiagnosticOrder object resource
	@return - nothing, uses global HL7v2 message object
*/
function processObservationRequest(obj) {

	var comments = [];

	var obr = <OBR><OBR.1><OBR.1.1>1</OBR.1.1></OBR.1>
				<OBR.2/><OBR.3/>
				<OBR.4><OBR.4.1/><OBR.4.2/><OBR.4.3/><OBR.4.4/><OBR.4.5/></OBR.4>
				<OBR.5/><OBR.6/><OBR.7/><OBR.8/><OBR.9/>
				<OBR.10/><OBR.11/><OBR.12/><OBR.13/><OBR.14/><OBR.15/>
				<OBR.16><OBR.16.1/><OBR.16.2/><OBR.16.3/><OBR.16.4/><OBR.16.5/><OBR.16.6/><OBR.16.7/><OBR.16.8/></OBR.16>
			</OBR>;


	// Count ORDER groups
	var groupID = ((obj.resource.DiagnosticOrder.id) ? obj.resource.DiagnosticOrder.id.value.toString() : "");
	var groupCount = groupID.match(/\d+/);

	if (groupCount == null) {
		groupCount = 1;
		error.push("resource.DiagnosticOrder.id element is undefined or not properly formatted, expected: OBR-observation-request-n, where n identifies the order group");
	}


	// OBR.2 [Placer Order Number] is stored during the DiagnosticReport resource processing	
	var resourceID = obj.resource.DiagnosticOrder.id.value.toString();
	// Extracts the following object - placedOrderNumber = {id:"", namespaceID:"", universalID:""};
	var placedOrderNumber = placedOrderNumberMap.get(resourceID);
	if (placedOrderNumber) {
		if (placedOrderNumber.id) obr['OBR.2']['OBR.2.1'] = placedOrderNumber.id.toString();
		if (placedOrderNumber.namespaceID) obr['OBR.2']['OBR.2.2'] = placedOrderNumber.namespaceID.toString();
		if (placedOrderNumber.universalID) obr['OBR.2']['OBR.2.3'] = placedOrderNumber.universalID.toString();
	}


	// Universal Service Identifier	
	var alternateID = obj.resource.DiagnosticOrder.item.code.coding.code.value.toString();
	if (alternateID)
		obr['OBR.4']['OBR.4.4'] = alternateID;

	var alternateText = obj.resource.DiagnosticOrder.item.code.coding.display.value.toString();
	if (alternateText)
		obr['OBR.4']['OBR.4.5'] = alternateText;

	// Observation Date/Time	
	var observationDateTime = obj.resource.DiagnosticOrder.event.dateTime.value.toString();
	if (observationDateTime) {
		// Format : yyyymmddhhmm
		var specimenCollectionDate = convertJSONDateTime(observationDateTime);
		obr['OBR.7'] = specimenCollectionDate.substring(0, 12);
	}

	// Specimen Action Code
	var actionCode = ((obj.resource.DiagnosticOrder.item.specimen) ? obj.resource.DiagnosticOrder.item.specimen.display.value.toString() : "");
	if (actionCode)
		obr['OBR.11'] = actionCode;

	// OBR.16 [Ordering Provider] is filled when resource.Practitioner is processed (see "Provider field" step)
	var orderingProviderReferenceID = ((obj.resource.DiagnosticOrder.event.actor.reference != undefined) ? obj.resource.DiagnosticOrder.event.actor.reference.value.toString() : "");
	if (orderingProviderReferenceID.indexOf("Practitioner/") > -1) {
		orderingProviderReferenceID = orderingProviderReferenceID.substring(13, orderingProviderReferenceID.length);
		var objOrderingProvider = practitionerMap.get(orderingProviderReferenceID.toString());

		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.identifier != undefined) {
			var id = objOrderingProvider.resource.Practitioner.identifier.value.value.toString();
			if (id) obr['OBR.16']['OBR.16.1'] = id;

			var assigner = objOrderingProvider.resource.Practitioner.identifier.assigner.display.value.toString();
			if (assigner) obr['OBR.16']['OBR.16.9'] = assigner;
		}

		// Process ordering provider's name, DEX name length limitation does NOT apply
		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.name != undefined) {
			var nameParts = processPersonName(objOrderingProvider.resource.Practitioner.name, false);
			obr['OBR.16']['OBR.16.2'] = nameParts.family;		
			if (nameParts.given1.length > 0) obr['OBR.16']['OBR.16.3'] = nameParts.given1;		
			if (nameParts.given1.length > 0) obr['OBR.16']['OBR.16.4'] = nameParts.given2;			
		}
	}

	// Store the segment to be later compiled into a message
	observationRequestMap.put(groupCount.toString(), obr.toString());




	// Iterate over all possible comment entries in /text/div/p  (#CS20 certification test case)
	if (obj.resource.DiagnosticOrder.text && obj.resource.DiagnosticOrder.text.div) {	

		var internalCommentsCount = 0; // only 5 NTE segments of 60 characters each
		var reportCommentsCount = 0; // only 2 NTE segments of 60 characters each
		var sourceOfComment = ["L","P","O", ""]; // Optional source of comments

		if (obj.resource.DiagnosticOrder.text.div.p.length && obj.resource.DiagnosticOrder.text.div.p.length > 0) {
			obj.resource.DiagnosticOrder.text.div.p.forEach(function(item){
				// For internal (I) and report (R) comments restriction applies
				if ((item.class.toString() == "I" && internalCommentsCount++ < 5) || 
					(item.class.toString() == "R" && reportCommentsCount++ < 2)) {
						comments.push(getNTESegment(item, countNTE++));

				} else if (sourceOfComment.indexOf(item.class.toString()) > -1) {
						// If the comment is other than internal (I) or report (R) then accept
						comments.push(getNTESegment(item, countNTE++));
				}
			});	
		} else if (obj.resource.DiagnosticOrder.text.div.p) {
			// A single comment from any acceptable source
			sourceOfComment = ["I","R","L","P","O", ""];
			if (sourceOfComment.indexOf(obj.resource.DiagnosticOrder.text.div.p.class.toString()) > -1)
				comments.push(getNTESegment(obj.resource.DiagnosticOrder.text.div.p, countNTE++));			
		}

		// Store the comments array to be later compiled into a message
		observationRequestCommentsMap.put(groupCount.toString(), comments);

	}


	return;
}

/**
	Populates DG1 [Diagnosis] segment from the Condition resource
	@param {Object} obj - Condition object resource
	@return {String} dg1 - DG1 segment serialized to string
*/
function processCondition(obj, setID) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");

	var dg1 = <DG1>
				<DG1.1><DG1.1.1>1</DG1.1.1></DG1.1>
				<DG1.3><DG1.3.1/><DG1.3.2/><DG1.3.3/><DG1.3.4/></DG1.3>
				<DG1.6><DG1.6.1>F</DG1.6.1></DG1.6>
			</DG1>;

	dg1['DG1.1']['DG1.1.1'] = setID;

	// Diagnosis Code
	if (obj.coding) {
		if (obj.coding.length > 0) {
			obj.coding.forEach(function(item){

				var code = ((item.code) ? item.code.value.toString() : "");
				var system = item.system.value.toString().toLowerCase();

				if (system)
					if (system.indexOf("icd-10") > -1) {

					dg1['DG1.3']['DG1.3.1'] = code;

					var text = ((item.display) ? item.display.value.toString() : "");
					dg1['DG1.3']['DG1.3.2'] = text;

					dg1['DG1.3']['DG1.3.3'] = "ICD";
				} else {
					dg1['DG1.3']['DG1.3.4'] = code;
				}
			});
		} else {
			var code = ((obj.coding.code) ? obj.coding.code.value.toString() : "");
			dg1['DG1.3']['DG1.3.1'] = code;
			var text = ((obj.coding.display) ? obj.coding.display.value.toString() : "");
			dg1['DG1.3']['DG1.3.2'] = text;
			dg1['DG1.3']['DG1.3.3'] = "ICD";
		}
	}


	// DG1.6 [Diagnosis Type] - hard-coded, required by HL7v2.5.1, F - Final
	//dg1['DG1.6']['DG1.6.1'] = "F";

	if (coverage == "P" || coverage == "T") {
		if (dg1['DG1.3']['DG1.3.1'].toString() == "" || dg1['DG1.3']['DG1.3.2'].toString() == "" || dg1['DG1.3']['DG1.3.4'].toString() == "") 
			error.push("resource.Condition.code for DG1.3[Diagnosis Code] is required if IN1.47[Coverage Type] is P or T");
	}

	return dg1.toString();

}

/**
	Populates OBX [Observation/Result] segment from the Observation resource
	@param {Object} obj - Observation object resource
	@return {String} obx - OBX segment serialized to string
*/
function processObservation(obj) {

	var obx =	<OBX>
				<OBX.1><OBX.1.1/></OBX.1>
				<OBX.2><OBX.2.1/></OBX.2>
				<OBX.3><OBX.3.1/><OBX.3.2/><OBX.3.3/><OBX.3.4/><OBX.3.5/><OBX.3.6/></OBX.3>
				<OBX.4/>
				<OBX.5><OBX.5.1/></OBX.5>
				<OBX.11><OBX.11.1>P</OBX.11.1></OBX.11>
			</OBX>;


	var id = obj.resource.Observation.id.value.toString();
	obx['OBX.1']['OBX.1.1'] = id.substring("OBX-observation-".length, id.length);

	// Contains a value that reports the results for an AOE
	if (obj.resource.Observation.code) {
		// local code
		var code = obj.resource.Observation.code.coding.code.value.toString();		
		obx['OBX.3']['OBX.3.4'] = code;

		// Local description
		var description = obj.resource.Observation.code.coding.display.value.toString();
		obx['OBX.3']['OBX.3.5'] = description;

		// Code system - Optional
		if (obj.resource.Observation.code.coding.system != undefined) {
			var codeSystem = obj.resource.Observation.code.coding.system.value.toString();
			if (codeSystem.toLowerCase().indexOf("snomed") > -1)
				obx['OBX.3']['OBX.3.6'] = "SCT";
			else if (codeSystem.toLowerCase().indexOf("loinc") > -1)
				obx['OBX.3']['OBX.3.6'] = "LN";
			else 
				delete obx['OBX.3']['OBX.3.6'];
		}

	}


	// If valueString is used, expected that the answer is always a string, therefore OBX.2 [Value Type] is hard-coded to "ST"
	if (obj.resource.Observation.valueString) {
		obx['OBX.2']['OBX.2.1'] = "ST";

		var aoe = obj.resource.Observation.valueString.value.toString();
		if (aoe) obx['OBX.5']['OBX.5.1'] = aoe;		

	// If valueQuantity is used the OBX.2 [Value Type] is hard-coded to "NM"	
	} else if (obj.resource.Observation.valueQuantity) {
		obx['OBX.2']['OBX.2.1'] = "NM";		
		var quantity = obj.resource.Observation.valueQuantity.value.value.toString();
		if (quantity) obx['OBX.5']['OBX.5.1'] = quantity;
	}


	// Observation Result Status - not required by Quest
	// Hard-coded to "P" - Preliminary


	// OBX level comments (Issue #17)
	var observationComments = [];

	// Iterate over all possible comment entries in /text/div/p  (also see Issue #17)
	if (obj.resource.Observation.text && obj.resource.Observation.text.div) {	

		var internalCommentsCount = 0; // only 5 NTE segments of 60 characters each
		var reportCommentsCount = 0; // only 2 NTE segments of 60 characters each
		var sourceOfComment = ["I","R","L","P","O",""]; // Optional source of comment

		if (obj.resource.Observation.text.div.p.length != undefined && obj.resource.Observation.text.div.p.length > 0) {
			// Several comments from any acceptable sources
			obj.resource.Observation.text.div.p.forEach(function(item){
				if (sourceOfComment.indexOf(item.class.toString()) > -1) {
					observationComments.push(getNTESegment(item, countNTE++));
				}
			});	
		} else if (obj.resource.Observation.text.div.p) {
			// A single comment from any acceptable source
			if (sourceOfComment.indexOf(obj.resource.Observation.text.div.p.class.toString()) > -1) {
				observationComments.push(getNTESegment(obj.resource.Observation.text.div.p, countNTE++));
			}			
		}
	}

	observationMap.put(id, obx.toString());
	observationCommentsMap.put(id, observationComments);

	return;
}

// Use fixHL7NodeOrder() to fix a possible node order issue 
// see http://www.mirthcorp.com/community/issues/browse/MIRTH-625

var messages = {request: "", resp: ""};
messages.request = SerializerFactory.getSerializer('HL7V2').fromXML(fixHL7NodeOrder(oml));

channelMap.put("omlHL7", messages.request); // testing
channelMap.put("error", error); // testing

logger.debug("SubmitOrder: Destination - " + messages.request);


// Verify for errors 
if (error.length > 0) {

	var issue = {severity: "error", code: "required", details: ""};
	issue.details = "Submit Order request is not sent. OML_O21 is missing required element" + ((error.length == 1) ? "" : "s");
	var outcome = getOperationOutcome(error, issue, messages, channelMap.get("FeedUUID"), globalChannelMap.get("outcomeFormat"));

	// To be sent to original feed submitter
	responseMap.put("Outcome", outcome);

	// Nothing to send to DEX
	channelMap.put("RequestMessage", null);

} else {

	// Temporary save the OML_O21 message for the next destination to process

//// !!!!!!!!!! TEST ONLY !!!!!!!!!!!!!!	
	channelMap.put("RequestMessage", null);
	//var info = {severity: "Information", code: "Information", details: "If you see this then the HL7 message is successfully created"};
	//(AM) operation outcome mapping expects lowercase; code is 'informational' not 'information'
	var info = {severity: "information", code: "informational", details: "If you see this then the HL7 message is successfully created"};
	outcome = getOperationOutcome(error, info, messages, channelMap.get("FeedUUID"), globalChannelMap.get("outcomeFormat"));	
	responseMap.put("Outcome", outcome);

	logger.info("sent done");
//// !!!!!!!!!! TEST ONLY !!!!!!!!!!!!!!

	// (SN) Enable this line to actually submit the SubmitOrder request
	channelMap.put("RequestMessage", messages.request);
}

var 	submitOrderSoap = <soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:ord="http://medplus.com/orders">
				   <soapenv:Header/>
				   <soapenv:Body>
				      <ord:submitOrder>
				         <ord:SubmitOrderRequest>
				            <hl7Order>OML_O21</hl7Order>
				         </ord:SubmitOrderRequest>
				      </ord:submitOrder>
				   </soapenv:Body>
				</soapenv:Envelope>;

var oml = channelMap.get("RequestMessage");
if (oml != null && oml.toString().length() > 0) {

	var bytes = new java.lang.String(oml.toString()).getBytes();
	submitOrderSoap.*::Body.*::submitOrder.*::SubmitOrderRequest.hl7Order = FileUtil.encode(bytes).replaceAll('\r\n|\r|\n', '');

} else {
	submitOrderSoap = "";
	return;
}

var sslClient = globalChannelMap.get("sslClientClass");

if (sslClient != null && submitOrderSoap.toString().length > 0) try {
	var error = [];
	var messages = {request: submitOrderSoap.toString(), resp: ""};
	var issue = {severity: "", code: "", details: ""};
	sslClient.setURL( $cfg("submitOrderURL") );
	sslClient.setSOAPRequest(submitOrderSoap);
	// Submit the request
	var httpCode = sslClient.doPost();

logger.debug ("httpCode is " + httpCode);
	// Get the response with all namespeces preserved
	var soapResp = new XML(sslClient.getSOAPResponse(true));
logger.debug ("soapResp is " + soapResp);
	if (200 == httpCode) {

		var orderACK = soapResp.*::Body.*::submitOrderResponse.*::Result.hl7OrderAck.toString();
		var ackHL7 = new java.lang.String(FileUtil.decode(orderACK));
		messages.resp = ackHL7;

		var ack = new XML(SerializerFactory.getSerializer('HL7V2').toXML(ackHL7));
		channelMap.put("HL7ACK", ackHL7);
		// Check Acknowledgment Code: AA - Accept, AE - Error
		if (ack['MSA']['MSA.1']['MSA.1.1'].toString() == "AA") {
			issue.severity = "information";
			issue.code = "informational";
			issue.details = "SUCCESS";			
		} else {
			// Acknowledgment Code == AE or anything else 
			issue.severity = "error";
			issue.code = "processing";
			issue.details = "Order was not successfully processed";		
			error = ack['MSA']['MSA.6']['MSA.6.2'].toString().split(",");	
		} 

	} else {
		// HTTP code is not 200 OK
		issue.severity = "error";
		issue.code = "processing";
		issue.details = "HTTP Code: " + httpCode;
	}	

} catch(ex) {

	issue.severity = "fatal";
	issue.code = "exception";
	issue.details = "SubmitOrder WS Transformer: " + ex;

} finally {

	// Return submission results
	var outcome = getOperationOutcome(error, issue, messages, channelMap.get("FeedUUID"), globalChannelMap.get("outcomeFormat"));
	responseMap.put("Outcome", outcome);
//	logger.debug("Outcome (Submit Order WS): " + outcome);
}

channel.preprocessingScript:
// Modify the message variable below to pre process data

// Delete some characters that may break JSON to XML parsing
message = message.replace(/null/g,'""');
message = message.replace(/&/g,"&amp;"); // ampersand
message = message.replace(/</g,"&lt;");  // less than
message = message.replace(/>/g,"&gt;");  // greater than
message = message.replace(/""""/g,'""'); // quotes around quotes

return message;

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map
return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data

// Format of the OperationOutcome. Supported: "XML" or "JSON"
globalChannelMap.put("outcomeFormat", "XML");

// Prepare SSL Client to access submitOrder Conformance Environment
if (globalMap.containsKey("sslClient") && globalMap.get("sslClient")) 
	try {

		var sslClient = new Packages.org.safemovement.DEXSSLClient();		
		sslClient.setAuthorizationType(org.safemovement.DEXSSLClient.Authorization.BASIC);
		sslClient.setContentType("text/xml;charset=UTF-8");
		sslClient.setUserName($("username"));
		sslClient.setUserPassword($("userpsw"));		
		sslClient.setConnectionType("Close");

		globalChannelMap.put("sslClientClass", sslClient);

	} catch(ex) {
		logger.error("SubmitOrder: Deploy - cannot initialize SSLClient - " + ex);

		// Stop this channel to resolve the problem
		ChannelUtil.stopChannel(channelId);
		throw ("SubmitOrder: Deploy - cannot initialize SSLClient - " + ex);		
	}



return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data

if (globalChannelMap.containsKey("sslClientClass")){
	globalChannelMap.remove("sslClientClass");
}


return;

