var count = globalChannelMap.get("isMoreCount");
if (count <= 1) {
	responseMap.put("getResultsResponse", resultsResponse1);
	globalChannelMap.put("isMoreCount", 2);
} else if (count == 2) {
	responseMap.put("getResultsResponse", resultsResponse2);
	globalChannelMap.put("isMoreCount", 3);
} else if (count >= 3) {
	responseMap.put("getResultsResponse", emptyResultsResponse);
	globalChannelMap.put("isMoreCount", 4);
}

