step.script:
// OML_O21 template with placeholders for required fields only
var oml_template = "MSH|^~\&|||||||OML^O21^OML_O21|||2.5.1\r" + "PID|1|||||||\r";
var oml = new XML(SerializerFactory.getSerializer('HL7V2').toXML(oml_template));

// MSH segment required fields (may be overwritten by MessageHeader resource)
oml['MSH']['MSH.3'] = $("application");
oml['MSH']['MSH.4'] = $("account");
oml['MSH']['MSH.6'] = $("receivingFacility");

// Overrides
oml['MSH']['MSH.6'] = "MET";
oml['MSH']['MSH.5'] = "PSC";
oml['MSH']['MSH.4'] = "90046003";

// (Quest) Note: All date timestamps are set to Coordinated Universal Time (UTC).
var currentDate = DateUtil.getCurrentDate("yyyyMMddHHmmssZ");
oml['MSH']['MSH.7'] = currentDate;

// Message Control ID is calculated based on date of submission
oml['MSH']['MSH.10'] = currentDate.toString().substring(0,14) + UUIDGenerator.getUUID().toString().substring(0,6);
oml['MSH']['MSH.11'] = $("processingID");


// A list of possible incoming JSON feed validation errors based on IN1.47 [Coverage Type] value
var error = [];

entry.string:
// OML_O21 template with placeholders for required fields only
var oml_template = "MSH|^~\&|||||||OML^O21^OML_O21|||2.5.1\r" + "PID|1|||||||\r";
var oml = new XML(SerializerFactory.getSerializer('HL7V2').toXML(oml_template));

// MSH segment required fields (may be overwritten by MessageHeader resource)
oml['MSH']['MSH.3'] = $("application");
oml['MSH']['MSH.4'] = $("account");
oml['MSH']['MSH.6'] = $("receivingFacility");

// Overrides
oml['MSH']['MSH.6'] = "MET";
oml['MSH']['MSH.5'] = "PSC";
oml['MSH']['MSH.4'] = "90046003";

// (Quest) Note: All date timestamps are set to Coordinated Universal Time (UTC).
var currentDate = DateUtil.getCurrentDate("yyyyMMddHHmmssZ");
oml['MSH']['MSH.7'] = currentDate;

// Message Control ID is calculated based on date of submission
oml['MSH']['MSH.10'] = currentDate.toString().substring(0,14) + UUIDGenerator.getUUID().toString().substring(0,6);
oml['MSH']['MSH.11'] = $("processingID");


