step.script:
/**
	Populates ORC [Common Order] segment from the DiagnosticReport resource
	@param {Object} obj - DiagnosticReport object resource
	@return - nothing, uses global HL7v2 message object
*/
function processCommonOrder(obj) {


	var orc = <ORC><ORC.1><ORC.1.1>NW</ORC.1.1></ORC.1></ORC>;

	// Count ORDER groups
	var groupID = ((obj.resource.DiagnosticReport.id) ? obj.resource.DiagnosticReport.id.value.toString() : "");
	var groupCount = groupID.match(/\d+/);

	logger.error( "Group Count: " + groupCount );
	logger.error( "Group ID: " + groupID );

	if (groupCount == null) {
		groupCount = 1;
		error.push("resource.DiagnosticReport.id element is undefined or not properly formatted, expected: ORC-common-order-n, where n identifies the order group");
	}


	// Placer Order Number
	// The value in ORC.02.01 (Entity Identifier) is the same as the value in OBR.02.01 (Entity Identifier)
	// All values for this field in all ORC and OBR segments are identical.
	// March 12: Moved into DiagnosticReport.identifier verified block 
	//var id = obj.resource.DiagnosticReport.identifier.value.value.toString();
	//if (id) orc['ORC.2']['ORC.2.1'] = id;

	if (obj.resource.DiagnosticReport.identifier) {

		var id = ((obj.resource.DiagnosticReport.identifier.value.value) ? obj.resource.DiagnosticReport.identifier.value.value.toString():"");
		if (id) orc['ORC.2']['ORC.2.1'] = id;
		logger.error( "Placer Number Order: " + id );

		var namespaceID = ((obj.resource.DiagnosticReport.identifier.system) ? obj.resource.DiagnosticReport.identifier.system.value.toString() : "");
		if (namespaceID) orc['ORC.2']['ORC.2.2'] = namespaceID;

		var universalID = ((obj.resource.DiagnosticReport.identifier.type) ? obj.resource.DiagnosticReport.identifier.type.id.toString() : "");
		if (universalID) orc['ORC.2']['ORC.2.3'] = universalID;
	}
	else
	{
		logger.error( "Missing identifier in in Diagnostic Report:" + groupID )
	}


	// Store the Placer Order Number for the OBR segment
	var diagnosticOrderID = ((obj.resource.DiagnosticReport.request) ? obj.resource.DiagnosticReport.request.reference.value.toString() : "");
	if (diagnosticOrderID.indexOf("DiagnosticOrder/") > -1) {
		diagnosticOrderID = diagnosticOrderID.substring("DiagnosticOrder/".length, diagnosticOrderID.length);

		logger.error( "DiagnosticOrderID: " + diagnosticOrderID );

		var placedOrderNumber = {id:"", namespaceID:"", universalID:""};

		placedOrderNumber.id = orc['ORC.2']['ORC.2.1'].toString();
		placedOrderNumber.namespaceID = orc['ORC.2']['ORC.2.2'].toString();
		placedOrderNumber.universalID = orc['ORC.2']['ORC.2.3'].toString();
		placedOrderNumberMap.put(diagnosticOrderID, placedOrderNumber);		
	}
	else
	{
		logger.error( "No Diagnostic Order Found" );
	}


	// Verified By Practitioner
	if (obj.resource.DiagnosticReport.contained != null && obj.resource.DiagnosticReport.contained != undefined) {
		var verifiedByID = obj.resource.DiagnosticReport.contained.Practitioner.identifier.value.value.toString();
		if (verifiedByID)
			orc['ORC.11']['ORC.11.1'] = verifiedByID;

		var familyName = ((obj.resource.DiagnosticReport.contained.Practitioner.name) ? obj.resource.DiagnosticReport.contained.Practitioner.name.family.value.toString() : "");
		if (familyName)
			orc['ORC.11']['ORC.11.2'] = familyName;		
	}

	// Process extension for TQ1 [Timing/Quantity] segment
	if (obj.resource.DiagnosticReport.extension.valueTiming != null && obj.resource.DiagnosticReport.extension.valueTiming != undefined) {
		processTimingQuantity(groupCount, obj.resource.DiagnosticReport.extension.valueTiming);
	}

	// Process zero or many DiagnosticReport/codedDiagnosis elements for DG1 [Diagnosis] segment
	var conditions = [];
	if (obj.resource.DiagnosticReport.codedDiagnosis) {

		if (obj.resource.DiagnosticReport.codedDiagnosis.length > 0) {
			var setID = 1;
			obj.resource.DiagnosticReport.codedDiagnosis.forEach(function(item){
				// A maximum of 12 unique ICD-10 codes are permitted per entire OML message
				// (SN) This code checks only codedDiagnosis in a single DiagnosticReport
				if (setID <= 12) conditions.push(processCondition(item, setID++));				 
			});
		} else {
			conditions.push(processCondition(obj.resource.DiagnosticReport.codedDiagnosis, 1)); 
		}
	}

	// Process zero or many result/reference elements for OBX [Observation] segment
	var observationReferences = [];
	if (obj.resource.DiagnosticReport.result) {
		var ref;
		if (obj.resource.DiagnosticReport.result.length > 0) {
			obj.resource.DiagnosticReport.result.forEach(function(item){
				ref = item.reference.value.toString();
				if (ref) observationReferences.push(ref.substring("Observation/".length, ref.length));
			});
		} else {
			ref = ((obj.resource.DiagnosticReport.result.reference != undefined) ? obj.resource.DiagnosticReport.result.reference.value.toString() : "");
			if (ref) observationReferences.push(ref.substring("Observation/".length, ref.length));
		}		
	}


	// ORC.12 [Ordering Provider] is filled when resource.Practitioner is processed (see "Provider field" step)
	var orderingProviderReferenceID = ((obj.resource.DiagnosticReport.performer != undefined) ? obj.resource.DiagnosticReport.performer.reference.value.toString() : "");
	if (orderingProviderReferenceID.indexOf("Practitioner/") > -1) {
		orderingProviderReferenceID = orderingProviderReferenceID.substring("Practitioner/".length, orderingProviderReferenceID.length);
		var objOrderingProvider = practitionerMap.get(orderingProviderReferenceID.toString());

		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.identifier != undefined) {
			var id = objOrderingProvider.resource.Practitioner.identifier.value.value.toString();
			if (id) orc['ORC.12']['ORC.12.1'] = id;

			var assigner = objOrderingProvider.resource.Practitioner.identifier.assigner.display.value.toString();
			if (assigner) orc['ORC.12']['ORC.12.9'] = assigner;
		}

		// Process ordering provider's name, DEX name length limitation does NOT apply
		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.name != undefined) {
			var nameParts = processPersonName(objOrderingProvider.resource.Practitioner.name, false);
			orc['ORC.12']['ORC.12.2'] = nameParts.family;		
			if (nameParts.given1.length > 0) orc['ORC.12']['ORC.12.3'] = nameParts.given1;		
			if (nameParts.given1.length > 0) orc['ORC.12']['ORC.12.4'] = nameParts.given2;				
		}
	}


	// Store the segment to be later compiled into a message
	commonOrderMap.put(groupCount.toString(), orc.toString());

	// Store the array of diagnosis to be later compiled into a message
	if (conditions.length > 0) diagnosisMap.put(groupCount.toString(), conditions);

	// Store the array of Observations to be later compiled into a message
	if (observationReferences.length > 0) observationReferencesMap.put(groupCount.toString(), observationReferences);


	return;
}

entry.string:
/**
	Populates ORC [Common Order] segment from the DiagnosticReport resource
	@param {Object} obj - DiagnosticReport object resource
	@return - nothing, uses global HL7v2 message object
*/
function processCommonOrder(obj) {


	var orc = <ORC><ORC.1><ORC.1.1>NW</ORC.1.1></ORC.1></ORC>;

	// Count ORDER groups
	var groupID = ((obj.resource.DiagnosticReport.id) ? obj.resource.DiagnosticReport.id.value.toString() : "");
	var groupCount = groupID.match(/\d+/);

	logger.error( "Group Count: " + groupCount );
	logger.error( "Group ID: " + groupID );

	if (groupCount == null) {
		groupCount = 1;
		error.push("resource.DiagnosticReport.id element is undefined or not properly formatted, expected: ORC-common-order-n, where n identifies the order group");
	}


	// Placer Order Number
	// The value in ORC.02.01 (Entity Identifier) is the same as the value in OBR.02.01 (Entity Identifier)
	// All values for this field in all ORC and OBR segments are identical.
	// March 12: Moved into DiagnosticReport.identifier verified block 
	//var id = obj.resource.DiagnosticReport.identifier.value.value.toString();
	//if (id) orc['ORC.2']['ORC.2.1'] = id;

	if (obj.resource.DiagnosticReport.identifier) {

		var id = ((obj.resource.DiagnosticReport.identifier.value.value) ? obj.resource.DiagnosticReport.identifier.value.value.toString():"");
		if (id) orc['ORC.2']['ORC.2.1'] = id;
		logger.error( "Placer Number Order: " + id );

		var namespaceID = ((obj.resource.DiagnosticReport.identifier.system) ? obj.resource.DiagnosticReport.identifier.system.value.toString() : "");
		if (namespaceID) orc['ORC.2']['ORC.2.2'] = namespaceID;

		var universalID = ((obj.resource.DiagnosticReport.identifier.type) ? obj.resource.DiagnosticReport.identifier.type.id.toString() : "");
		if (universalID) orc['ORC.2']['ORC.2.3'] = universalID;
	}
	else
	{
		logger.error( "Missing identifier in in Diagnostic Report:" + groupID )
	}


	// Store the Placer Order Number for the OBR segment
	var diagnosticOrderID = ((obj.resource.DiagnosticReport.request) ? obj.resource.DiagnosticReport.request.reference.value.toString() : "");
	if (diagnosticOrderID.indexOf("DiagnosticOrder/") > -1) {
		diagnosticOrderID = diagnosticOrderID.substring("DiagnosticOrder/".length, diagnosticOrderID.length);

		logger.error( "DiagnosticOrderID: " + diagnosticOrderID );

		var placedOrderNumber = {id:"", namespaceID:"", universalID:""};

		placedOrderNumber.id = orc['ORC.2']['ORC.2.1'].toString();
		placedOrderNumber.namespaceID = orc['ORC.2']['ORC.2.2'].toString();
		placedOrderNumber.universalID = orc['ORC.2']['ORC.2.3'].toString();
		placedOrderNumberMap.put(diagnosticOrderID, placedOrderNumber);		
	}
	else
	{
		logger.error( "No Diagnostic Order Found" );
	}


	// Verified By Practitioner
	if (obj.resource.DiagnosticReport.contained != null && obj.resource.DiagnosticReport.contained != undefined) {
		var verifiedByID = obj.resource.DiagnosticReport.contained.Practitioner.identifier.value.value.toString();
		if (verifiedByID)
			orc['ORC.11']['ORC.11.1'] = verifiedByID;

		var familyName = ((obj.resource.DiagnosticReport.contained.Practitioner.name) ? obj.resource.DiagnosticReport.contained.Practitioner.name.family.value.toString() : "");
		if (familyName)
			orc['ORC.11']['ORC.11.2'] = familyName;		
	}

	// Process extension for TQ1 [Timing/Quantity] segment
	if (obj.resource.DiagnosticReport.extension.valueTiming != null && obj.resource.DiagnosticReport.extension.valueTiming != undefined) {
		processTimingQuantity(groupCount, obj.resource.DiagnosticReport.extension.valueTiming);
	}

	// Process zero or many DiagnosticReport/codedDiagnosis elements for DG1 [Diagnosis] segment
	var conditions = [];
	if (obj.resource.DiagnosticReport.codedDiagnosis) {

		if (obj.resource.DiagnosticReport.codedDiagnosis.length > 0) {
			var setID = 1;
			obj.resource.DiagnosticReport.codedDiagnosis.forEach(function(item){
				// A maximum of 12 unique ICD-10 codes are permitted per entire OML message
				// (SN) This code checks only codedDiagnosis in a single DiagnosticReport
				if (setID <= 12) conditions.push(processCondition(item, setID++));				 
			});
		} else {
			conditions.push(processCondition(obj.resource.DiagnosticReport.codedDiagnosis, 1)); 
		}
	}

	// Process zero or many result/reference elements for OBX [Observation] segment
	var observationReferences = [];
	if (obj.resource.DiagnosticReport.result) {
		var ref;
		if (obj.resource.DiagnosticReport.result.length > 0) {
			obj.resource.DiagnosticReport.result.forEach(function(item){
				ref = item.reference.value.toString();
				if (ref) observationReferences.push(ref.substring("Observation/".length, ref.length));
			});
		} else {
			ref = ((obj.resource.DiagnosticReport.result.reference != undefined) ? obj.resource.DiagnosticReport.result.reference.value.toString() : "");
			if (ref) observationReferences.push(ref.substring("Observation/".length, ref.length));
		}		
	}


	// ORC.12 [Ordering Provider] is filled when resource.Practitioner is processed (see "Provider field" step)
	var orderingProviderReferenceID = ((obj.resource.DiagnosticReport.performer != undefined) ? obj.resource.DiagnosticReport.performer.reference.value.toString() : "");
	if (orderingProviderReferenceID.indexOf("Practitioner/") > -1) {
		orderingProviderReferenceID = orderingProviderReferenceID.substring("Practitioner/".length, orderingProviderReferenceID.length);
		var objOrderingProvider = practitionerMap.get(orderingProviderReferenceID.toString());

		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.identifier != undefined) {
			var id = objOrderingProvider.resource.Practitioner.identifier.value.value.toString();
			if (id) orc['ORC.12']['ORC.12.1'] = id;

			var assigner = objOrderingProvider.resource.Practitioner.identifier.assigner.display.value.toString();
			if (assigner) orc['ORC.12']['ORC.12.9'] = assigner;
		}

		// Process ordering provider's name, DEX name length limitation does NOT apply
		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.name != undefined) {
			var nameParts = processPersonName(objOrderingProvider.resource.Practitioner.name, false);
			orc['ORC.12']['ORC.12.2'] = nameParts.family;		
			if (nameParts.given1.length > 0) orc['ORC.12']['ORC.12.3'] = nameParts.given1;		
			if (nameParts.given1.length > 0) orc['ORC.12']['ORC.12.4'] = nameParts.given2;				
		}
	}


	// Store the segment to be later compiled into a message
	commonOrderMap.put(groupCount.toString(), orc.toString());

	// Store the array of diagnosis to be later compiled into a message
	if (conditions.length > 0) diagnosisMap.put(groupCount.toString(), conditions);

	// Store the array of Observations to be later compiled into a message
	if (observationReferences.length > 0) observationReferencesMap.put(groupCount.toString(), observationReferences);


	return;
}

step.script:
/**
	Populates TQ1 [Timing/Quantity] segment with data from the DiagnosticReport extension
	@param {Integer} count - Common Order group count
	@param {Object} valueTiming - valueTiming extension
	@return - nothing, uses global map
*/
function processTimingQuantity(count, valueTiming) {

	// TQ1 [Timing/Quantity] segment
	if (valueTiming != null && valueTiming != undefined) {

		var tq1 = <TQ1><TQ1.1><TQ1.1.1>1</TQ1.1.1></TQ1.1></TQ1>;

		tq1['TQ1.1']['TQ1.1.1'] = count;

		var startDateTime = ((valueTiming.event) ? valueTiming.event.value.toString() : "");
		if (startDateTime)
			tq1['TQ1.7'] = convertJSONDateTime(startDateTime);

		var priorityCode = ((valueTiming.code) ? valueTiming.code.coding.code.value.toString() : "");
		if (priorityCode)
			tq1['TQ1.9']['TQ1.9.1'] = priorityCode;
	}

	// Store the segment to be later compiled into a message
	timingQuantityMap.put(count.toString(), tq1.toString());

	return;
}

entry.string:
/**
	Populates TQ1 [Timing/Quantity] segment with data from the DiagnosticReport extension
	@param {Integer} count - Common Order group count
	@param {Object} valueTiming - valueTiming extension
	@return - nothing, uses global map
*/
function processTimingQuantity(count, valueTiming) {

	// TQ1 [Timing/Quantity] segment
	if (valueTiming != null && valueTiming != undefined) {

		var tq1 = <TQ1><TQ1.1><TQ1.1.1>1</TQ1.1.1></TQ1.1></TQ1>;

		tq1['TQ1.1']['TQ1.1.1'] = count;

		var startDateTime = ((valueTiming.event) ? valueTiming.event.value.toString() : "");
		if (startDateTime)
			tq1['TQ1.7'] = convertJSONDateTime(startDateTime);

		var priorityCode = ((valueTiming.code) ? valueTiming.code.coding.code.value.toString() : "");
		if (priorityCode)
			tq1['TQ1.9']['TQ1.9.1'] = priorityCode;
	}

	// Store the segment to be later compiled into a message
	timingQuantityMap.put(count.toString(), tq1.toString());

	return;
}

step.script:
/**
	Populates OBR [Observation Request] segment from the DiagnosticReport resource
	@param {Object} obj - DiagnosticOrder object resource
	@return - nothing, uses global HL7v2 message object
*/
function processObservationRequest(obj) {

	var comments = [];

	var obr = <OBR><OBR.1><OBR.1.1>1</OBR.1.1></OBR.1>
				<OBR.2/><OBR.3/>
				<OBR.4><OBR.4.1/><OBR.4.2/><OBR.4.3/><OBR.4.4/><OBR.4.5/></OBR.4>
				<OBR.5/><OBR.6/><OBR.7/><OBR.8/><OBR.9/>
				<OBR.10/><OBR.11/><OBR.12/><OBR.13/><OBR.14/><OBR.15/>
				<OBR.16><OBR.16.1/><OBR.16.2/><OBR.16.3/><OBR.16.4/><OBR.16.5/><OBR.16.6/><OBR.16.7/><OBR.16.8/></OBR.16>
			</OBR>;


	// Count ORDER groups
	var groupID = ((obj.resource.DiagnosticOrder.id) ? obj.resource.DiagnosticOrder.id.value.toString() : "");
	var groupCount = groupID.match(/\d+/);

	if (groupCount == null) {
		groupCount = 1;
		error.push("resource.DiagnosticOrder.id element is undefined or not properly formatted, expected: OBR-observation-request-n, where n identifies the order group");
	}


	// OBR.2 [Placer Order Number] is stored during the DiagnosticReport resource processing	
	var resourceID = obj.resource.DiagnosticOrder.id.value.toString();
	// Extracts the following object - placedOrderNumber = {id:"", namespaceID:"", universalID:""};
	var placedOrderNumber = placedOrderNumberMap.get(resourceID);
	if (placedOrderNumber) {
		if (placedOrderNumber.id) obr['OBR.2']['OBR.2.1'] = placedOrderNumber.id.toString();
		if (placedOrderNumber.namespaceID) obr['OBR.2']['OBR.2.2'] = placedOrderNumber.namespaceID.toStrign();
		if (placedOrderNumber.universalID) obr['OBR.2']['OBR.2.3'] = placedOrderNumber.universalID.toString();
	}


	// Universal Service Identifier	
	var alternateID = obj.resource.DiagnosticOrder.item.code.coding.code.value.toString();
	if (alternateID)
		obr['OBR.4']['OBR.4.4'] = alternateID;

	var alternateText = obj.resource.DiagnosticOrder.item.code.coding.display.value.toString();
	if (alternateText)
		obr['OBR.4']['OBR.4.5'] = alternateText;

	// Observation Date/Time	
	var observationDateTime = obj.resource.DiagnosticOrder.event.dateTime.value.toString();
	if (observationDateTime) {
		// Format : yyyymmddhhmm
		var specimenCollectionDate = convertJSONDateTime(observationDateTime);
		obr['OBR.7'] = specimenCollectionDate.substring(0, 12);
	}

	// Specimen Action Code
	var actionCode = ((obj.resource.DiagnosticOrder.item.specimen) ? obj.resource.DiagnosticOrder.item.specimen.display.value.toString() : "");
	if (actionCode)
		obr['OBR.11'] = actionCode;

	// OBR.16 [Ordering Provider] is filled when resource.Practitioner is processed (see "Provider field" step)
	var orderingProviderReferenceID = ((obj.resource.DiagnosticOrder.event.actor.reference != undefined) ? obj.resource.DiagnosticOrder.event.actor.reference.value.toString() : "");
	if (orderingProviderReferenceID.indexOf("Practitioner/") > -1) {
		orderingProviderReferenceID = orderingProviderReferenceID.substring(13, orderingProviderReferenceID.length);
		var objOrderingProvider = practitionerMap.get(orderingProviderReferenceID.toString());

		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.identifier != undefined) {
			var id = objOrderingProvider.resource.Practitioner.identifier.value.value.toString();
			if (id) obr['OBR.16']['OBR.16.1'] = id;

			var assigner = objOrderingProvider.resource.Practitioner.identifier.assigner.display.value.toString();
			if (assigner) obr['OBR.16']['OBR.16.9'] = assigner;
		}

		// Process ordering provider's name, DEX name length limitation does NOT apply
		if (objOrderingProvider != null && objOrderingProvider.resource.Practitioner.name != undefined) {
			var nameParts = processPersonName(objOrderingProvider.resource.Practitioner.name, false);
			obr['OBR.16']['OBR.16.2'] = nameParts.family;		
			if (nameParts.given1.length > 0) obr['OBR.16']['OBR.16.3'] = nameParts.given1;		
			if (nameParts.given1.length > 0) obr['OBR.16']['OBR.16.4'] = nameParts.given2;			
		}
	}

	// Store the segment to be later compiled into a message
	observationRequestMap.put(groupCount.toString(), obr.toString());




	// Iterate over all possible comment entries in /text/div/p  (#CS20 certification test case)
	if (obj.resource.DiagnosticOrder.text && obj.resource.DiagnosticOrder.text.div) {	

		var internalCommentsCount = 0; // only 5 NTE segments of 60 characters each
		var reportCommentsCount = 0; // only 2 NTE segments of 60 characters each
		var sourceOfComment = ["L","P","O", ""]; // Optional source of comments

		if (obj.resource.DiagnosticOrder.text.div.p.length && obj.resource.DiagnosticOrder.text.div.p.length > 0) {
			obj.resource.DiagnosticOrder.text.div.p.forEach(function(item){
				// For internal (I) and report (R) comments restriction applies
				if ((item.class.toString() == "I" && internalCommentsCount++ < 5) || 
					(item.class.toString() == "R" && reportCommentsCount++ < 2)) {
						comments.push(getNTESegment(item, countNTE++));

				} else if (sourceOfComment.indexOf(item.class.toString()) > -1) {
						// If the comment is other than internal (I) or report (R) then accept
						comments.push(getNTESegment(item, countNTE++));
				}
			});	
		} else if (obj.resource.DiagnosticOrder.text.div.p) {
			// A single comment from any acceptable source
			sourceOfComment = ["I","R","L","P","O", ""];
			if (sourceOfComment.indexOf(obj.resource.DiagnosticOrder.text.div.p.class.toString()) > -1)
				comments.push(getNTESegment(obj.resource.DiagnosticOrder.text.div.p, countNTE++));			
		}

		// Store the comments array to be later compiled into a message
		observationRequestCommentsMap.put(groupCount.toString(), comments);

	}


	return;
}

entry.string:
/**
	Populates OBR [Observation Request] segment from the DiagnosticReport resource
	@param {Object} obj - DiagnosticOrder object resource
	@return - nothing, uses global HL7v2 message object
*/
function processObservationRequest(obj) {

	var comments = [];

	var obr = <OBR><OBR.1><OBR.1.1>1</OBR.1.1></OBR.1>
				<OBR.2/><OBR.3/>
				<OBR.4><OBR.4.1/><OBR.4.2/><OBR.4.3/><OBR.4.4/><OBR.4.5/></OBR.4>
				<OBR.5/><OBR.6/><OBR.7/><OBR.8/><OBR.9/>
				<OBR.10/><OBR.11/><OBR.12/><OBR.13/><OBR.14/><OBR.15/>
				<OBR.16><OBR.16.1/><OBR.16.2/><OBR.16.3/><OBR.16.4/><OBR.16.5/><OBR.16.6/><OBR.16.7/><OBR.16.8/></OBR.16>
			</OBR>;


	// Count ORDER groups
	var groupID = ((obj.resource.DiagnosticOrder.id) ? obj.resource.DiagnosticOrder.id.value.toString() : "");
	var groupCount = groupID.match(/\d+/);

	if (groupCount == null) {
		groupCount = 1;
		error.push("resource.DiagnosticOrder.id element is undefined or not properly formatted, expected: OBR-observation-request-n, where n identifies the order group");
	}


	// OBR.2 [Placer Order Number] is stored during the DiagnosticReport resource processing	
	var resourceID = obj.resource.DiagnosticOrder.id.value.toString();
	// Extracts the following object - placedOrderNumber = {id:"", namespaceID:"", universalID:""};
	var placedOrderNumber = placedOrderNumberMap.get(resourceID);
	if (placedOrderNumber) {
		if (placedOrderNumber.id) obr['OBR.2']['OBR.2.1'] = placedOrderNumber.id.toString();
		if (placedOrderNumber.namespaceID) obr['OBR.2']['OBR.2.2'] = placedOrderNumber.namespaceID.toStrign();
		if (placedOrderNumber.universalID) obr['OBR.2']['OBR.2.3'] = placedOrderNumber.universalID.toString();
	}


	// Universal Service Identifier	
