step.script:
/**
	Populates DG1 [Diagnosis] segment from the Condition resource
	@param {Object} obj - Condition object resource
	@return {String} dg1 - DG1 segment serialized to string
*/
function processCondition(obj, setID) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");

	var dg1 = <DG1>
				<DG1.1><DG1.1.1>1</DG1.1.1></DG1.1>
				<DG1.3><DG1.3.1/><DG1.3.2/><DG1.3.3/><DG1.3.4/></DG1.3>
				<DG1.6><DG1.6.1>F</DG1.6.1></DG1.6>
			</DG1>;

	dg1['DG1.1']['DG1.1.1'] = setID;

	// Diagnosis Code
	if (obj.coding) {
		if (obj.coding.length > 0) {
			obj.coding.forEach(function(item){

				var code = ((item.code) ? item.code.value.toString() : "");
				var system = item.system.value.toString().toLowerCase();

				if (system)
					if (system.indexOf("icd-10") > -1) {

					dg1['DG1.3']['DG1.3.1'] = code;

					var text = ((item.display) ? item.display.value.toString() : "");
					dg1['DG1.3']['DG1.3.2'] = text;

					dg1['DG1.3']['DG1.3.3'] = "ICD";
				} else {
					dg1['DG1.3']['DG1.3.4'] = code;
				}
			});
		} else {
			var code = ((obj.coding.code) ? obj.coding.code.value.toString() : "");
			dg1['DG1.3']['DG1.3.1'] = code;
			var text = ((obj.coding.display) ? obj.coding.display.value.toString() : "");
			dg1['DG1.3']['DG1.3.2'] = text;
			dg1['DG1.3']['DG1.3.3'] = "ICD";
		}
	}


	// DG1.6 [Diagnosis Type] - hard-coded, required by HL7v2.5.1, F - Final
	//dg1['DG1.6']['DG1.6.1'] = "F";

	if (coverage == "P" || coverage == "T") {
		if (dg1['DG1.3']['DG1.3.1'].toString() == "" || dg1['DG1.3']['DG1.3.2'].toString() == "" || dg1['DG1.3']['DG1.3.4'].toString() == "") 
			error.push("resource.Condition.code for DG1.3[Diagnosis Code] is required if IN1.47[Coverage Type] is P or T");
	}

	return dg1.toString();

}

entry.string:
/**
	Populates DG1 [Diagnosis] segment from the Condition resource
	@param {Object} obj - Condition object resource
	@return {String} dg1 - DG1 segment serialized to string
*/
function processCondition(obj, setID) {

	// Retrieve stored coverage type (T, P or C)
	var coverage = channelMap.get("CoverageType");

	var dg1 = <DG1>
				<DG1.1><DG1.1.1>1</DG1.1.1></DG1.1>
				<DG1.3><DG1.3.1/><DG1.3.2/><DG1.3.3/><DG1.3.4/></DG1.3>
				<DG1.6><DG1.6.1>F</DG1.6.1></DG1.6>
			</DG1>;

	dg1['DG1.1']['DG1.1.1'] = setID;

	// Diagnosis Code
	if (obj.coding) {
		if (obj.coding.length > 0) {
			obj.coding.forEach(function(item){

				var code = ((item.code) ? item.code.value.toString() : "");
				var system = item.system.value.toString().toLowerCase();

				if (system)
					if (system.indexOf("icd-10") > -1) {

					dg1['DG1.3']['DG1.3.1'] = code;

					var text = ((item.display) ? item.display.value.toString() : "");
					dg1['DG1.3']['DG1.3.2'] = text;

					dg1['DG1.3']['DG1.3.3'] = "ICD";
				} else {
					dg1['DG1.3']['DG1.3.4'] = code;
				}
			});
		} else {
			var code = ((obj.coding.code) ? obj.coding.code.value.toString() : "");
			dg1['DG1.3']['DG1.3.1'] = code;
			var text = ((obj.coding.display) ? obj.coding.display.value.toString() : "");
			dg1['DG1.3']['DG1.3.2'] = text;
			dg1['DG1.3']['DG1.3.3'] = "ICD";
		}
	}


	// DG1.6 [Diagnosis Type] - hard-coded, required by HL7v2.5.1, F - Final
	//dg1['DG1.6']['DG1.6.1'] = "F";

	if (coverage == "P" || coverage == "T") {
		if (dg1['DG1.3']['DG1.3.1'].toString() == "" || dg1['DG1.3']['DG1.3.2'].toString() == "" || dg1['DG1.3']['DG1.3.4'].toString() == "") 
			error.push("resource.Condition.code for DG1.3[Diagnosis Code] is required if IN1.47[Coverage Type] is P or T");
	}

	return dg1.toString();

}

step.script:
/**
	Populates OBX [Observation/Result] segment from the Observation resource
	@param {Object} obj - Observation object resource
	@return {String} obx - OBX segment serialized to string
*/
function processObservation(obj) {

	var obx =	<OBX>
				<OBX.1><OBX.1.1/></OBX.1>
				<OBX.2><OBX.2.1/></OBX.2>
				<OBX.3><OBX.3.1/><OBX.3.2/><OBX.3.3/><OBX.3.4/><OBX.3.5/><OBX.3.6/></OBX.3>
				<OBX.4/>
				<OBX.5><OBX.5.1/></OBX.5>
				<OBX.11><OBX.11.1>P</OBX.11.1></OBX.11>
			</OBX>;


	var id = obj.resource.Observation.id.value.toString();
	obx['OBX.1']['OBX.1.1'] = id.substring("OBX-observation-".length, id.length);

	// Contains a value that reports the results for an AOE
	if (obj.resource.Observation.code) {
		// local code
		var code = obj.resource.Observation.code.coding.code.value.toString();		
		obx['OBX.3']['OBX.3.4'] = code;

		// Local description
		var description = obj.resource.Observation.code.coding.display.value.toString();
		obx['OBX.3']['OBX.3.5'] = description;

		// Code system - Optional
		if (obj.resource.Observation.code.coding.system != undefined) {
			var codeSystem = obj.resource.Observation.code.coding.system.value.toString();
			if (codeSystem.toLowerCase().indexOf("snomed") > -1)
				obx['OBX.3']['OBX.3.6'] = "SCT";
			else if (codeSystem.toLowerCase().indexOf("loinc") > -1)
				obx['OBX.3']['OBX.3.6'] = "LN";
			else 
				delete obx['OBX.3']['OBX.3.6'];
		}

	}


	// If valueString is used, expected that the answer is always a string, therefore OBX.2 [Value Type] is hard-coded to "ST"
	if (obj.resource.Observation.valueString) {
		obx['OBX.2']['OBX.2.1'] = "ST";

		var aoe = obj.resource.Observation.valueString.value.toString();
		if (aoe) obx['OBX.5']['OBX.5.1'] = aoe;		

	// If valueQuantity is used the OBX.2 [Value Type] is hard-coded to "NM"	
	} else if (obj.resource.Observation.valueQuantity) {
		obx['OBX.2']['OBX.2.1'] = "NM";		
		var quantity = obj.resource.Observation.valueQuantity.value.value.toString();
		if (quantity) obx['OBX.5']['OBX.5.1'] = quantity;
	}


	// Observation Result Status - not required by Quest
	// Hard-coded to "P" - Preliminary


	// OBX level comments (Issue #17)
	var observationComments = [];

	// Iterate over all possible comment entries in /text/div/p  (also see Issue #17)
	if (obj.resource.Observation.text && obj.resource.Observation.text.div) {	

		var internalCommentsCount = 0; // only 5 NTE segments of 60 characters each
		var reportCommentsCount = 0; // only 2 NTE segments of 60 characters each
		var sourceOfComment = ["I","R","L","P","O",""]; // Optional source of comment

		if (obj.resource.Observation.text.div.p.length != undefined && obj.resource.Observation.text.div.p.length > 0) {
			// Several comments from any acceptable sources
			obj.resource.Observation.text.div.p.forEach(function(item){
				if (sourceOfComment.indexOf(item.class.toString()) > -1) {
					observationComments.push(getNTESegment(item, countNTE++));
				}
			});	
		} else if (obj.resource.Observation.text.div.p) {
			// A single comment from any acceptable source
			if (sourceOfComment.indexOf(obj.resource.Observation.text.div.p.class.toString()) > -1) {
				observationComments.push(getNTESegment(obj.resource.Observation.text.div.p, countNTE++));
			}			
		}
	}

	observationMap.put(id, obx.toString());
	observationCommentsMap.put(id, observationComments);

	return;
}

