channel.preprocessingScript:
// Modify the message variable below to pre process data

// Delete some characters that may break JSON to XML parsing
message = message.replace(/null/g,'""');
message = message.replace(/&/g,"&amp;"); // ampersand
message = message.replace(/</g,"&lt;");  // less than
message = message.replace(/>/g,"&gt;");  // greater than
message = message.replace(/""""/g,'""'); // quotes around quotes

return message;

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map
return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data

// Format of the OperationOutcome. Supported: "XML" or "JSON"
globalChannelMap.put("outcomeFormat", "XML");

// Prepare SSL Client to access submitOrder Conformance Environment
if (globalMap.containsKey("sslClient") && globalMap.get("sslClient")) 
	try {

		var sslClient = new Packages.org.safemovement.DEXSSLClient();		
		sslClient.setAuthorizationType(org.safemovement.DEXSSLClient.Authorization.BASIC);
		sslClient.setContentType("text/xml;charset=UTF-8");
		sslClient.setUserName($("username"));
		sslClient.setUserPassword($("userpsw"));		
		sslClient.setConnectionType("Close");

		globalChannelMap.put("sslClientClass", sslClient);

	} catch(ex) {
		logger.error("SubmitOrder: Deploy - cannot initialize SSLClient - " + ex);

		// Stop this channel to resolve the problem
		ChannelUtil.stopChannel(channelId);
		throw ("SubmitOrder: Deploy - cannot initialize SSLClient - " + ex);		
	}



return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data

if (globalChannelMap.containsKey("sslClientClass")){
	globalChannelMap.remove("sslClientClass");
}


return;

codeTemplate.code:
/**
	Converts FHIR code to HL7v2 code as defined by the conversion table according to the HL7v2 specification

	@param {String} table - HL7v2 Table code as per specification
	@param {String] value - value to convert
	@return {String} return code according to conversion table
*/
function convertTable(table, value) {

	switch(table) {

		// Table 0001 - Administrative Sex
		case "0001":
			if (value.toString() == "male") return "M"
			else if (value.toString() == "female") return "F"
			else if (value.toString() == "other") return "O"
			else return "U";
			break;
	}

	return "";
}

/**
	Converts date, time and time zone from HL7v2 to ISO 8601 (JSON) format.
	Possible input string format: yyyymmddhhmm[ss[.s[s[s[s]]]]][+/-zzzz]

	@param {String} date - datetime to be converted
	@return {String} return JSON representation of the datetime
*/
function convertHL7DateTime(datetime) {

/*
	Format: yyyymmddhhmm[ss[.s[s[s[s]]]]][+/-zzzz]
	The date and time elements are populated as follows:
	yyyy = Required
	mm = Required if yyyy is not 0000.
	dd = Required if yyyy is not 0000.
	hh = Required if yyyy is not 0000.
	mm = Required if yyyy is not 0000.
	[ss[.s[s[s[s]]]]] = Conditional. Optional if yyyy is not 0000.
	[+/-zzzz] = Optional
*/


	if (datetime == null || datetime.length == 0 || typeof datetime === "undefined") return "";

	try {
		var strDateTime = new Packages.java.lang.StringBuffer(datetime.toString());

		// Extract time zone
		var index;
		var symbol;
		var timeZone;

		if (strDateTime.indexOf("-") > 0) {
			index = strDateTime.indexOf("-");
		} else if (strDateTime.indexOf("+") > 0) {
			index = strDateTime.indexOf("+");
		} else
			index = -1;

		if (index > 0) {		
			symbol = strDateTime.substring(index, index+1);
			timeZone = new Packages.java.lang.StringBuffer(strDateTime.substring(index+1));
			if (timeZone.length() > 2)
				timeZone.insert(2,":");
			strDateTime.delete(index, strDateTime.length());		
		} else {
			symbol = "";
			timeZone = "";
		}

		// Extract decimal fraction of a second
		var fraction = strDateTime.indexOf(".");
		var milliseconds;
		if (fraction > 0) {
			milliseconds = strDateTime.substring(fraction, strDateTime.length());
			strDateTime.delete(fraction, strDateTime.length());
		} else {
			milliseconds = "";
		}

		// Convert the main part
		var jsonDateTime = new Packages.java.lang.StringBuffer(strDateTime);

		// yyyyMMdd
		if (strDateTime.length() >= "yyyyMMdd".length) {
			jsonDateTime.insert(4,"-");
			jsonDateTime.insert(7,"-");
		}

		// yyyyMMddhh
		if (strDateTime.length() >= "yyyyMMddhh".length) {
			jsonDateTime.insert(10,"T");
		}

		// yyyyMMddhhmm
		if (strDateTime.length() >= "yyyyMMddhhmm".length) {
			jsonDateTime.insert(13,":");

		}

		// yyyymmddhhmmss
		if (strDateTime.length() >= "yyyymmddhhmmss".length) {
			jsonDateTime.insert(16,":");		
		}

	} catch(ex) {
		jsonDateTime = "0000-00-00"
		milliseconds = "";
		symbol = "";
		timeZone = "";
		logger.error("convertHL7DateTime(" + datetime + ") exception: " + ex);	
	} finally {
		return jsonDateTime.toString() + milliseconds.toString() + symbol.toString() + timeZone.toString();
	}
}

/**
	Converts date, time and time zone from ISO 8601 (JSON) to HL7v2 format.
	Possible input string format: yyyy-mm-ddThh:mm:ss.nnnnnn+|-hh:mm

	@param {String} date - datetime to be converted
	@return {String} return HL7v2 representation of the datetime
*/
function convertJSONDateTime(datetime) {

/*
	Format: yyyy-mm-dd[Thh:mm[:ss[.nnnnnn[+|-hh:mm]]]]
*/

	var date;
	var time;

	var arrDateTime = datetime.split("T");

	if (arrDateTime.length == 1) {
		date = arrDateTime[0];
		time = "";
	} else if (arrDateTime.length == 2) {
		date = arrDateTime[0];
		time = arrDateTime[1];
	} else 
		return "";

	date = date.replace(/\-/g, "");
	time = time.replace(/\:/g, "");

	return date + time;

}

/**
	Inserts hyphens into the phone number if it is 7 digits in length

	@param {String} phone - initial string to convert
	@return {String} return phone number
*/
function convertPhone(phone) {

	try {
		var strPhone = new Packages.java.lang.StringBuffer(phone.toString());	

		if (strPhone.length() == 7) {			
			strPhone.insert(3,"-");
			strPhone.insert(6,"-");
			return strPhone.toString();			
		} else 
			return phone.toString();

	} catch(ex) {
		return phone.toString();		
	} 
}

/**
	Converts a string in XML format into its JSON representation.
	This function requires org.json.jar to be deployed to the /custom-lib folder on Mirth

	@param {String} input - A string in XML format
	@return {String} return a string representing initial XML feed but in JSON format
*/
function convertXMLtoJSON(input) {
	var strJSON = Packages.org.json.XML.toJSONObject(input.toString());
	return strJSON.toString(2);	
}

/**
	Verifies and converts HL7v2 administrative gender value to FHIR value set defined by
	http://hl7.org/fhir/administrative-gender

	@param {String} code - HL7v2 PID.8 [Administrative Sex] code
	@return {String} return FHIR value set code
*/
function getGenderValue(code) {

	var gender;

	switch(code) {
		case "M":
			gender = "male";
			break;
		case "F":
			gender = "female";
		 	break;
		case "O":
		case "A":
		case "N":
			gender = "other";
			break; 	
		default:
			gender = "unknown";		 	
	}

	return gender;	
}

/**
	Converts phone number string with caret dividers into XTN data type.
	Supported formats: 1-314-8727127 or 314-8727127
	Returned formats: ^^PH^^1^314^8727127 or ^^PH^^^314^8727127 or ^^PH^^^^8727127

	@param {String} phone - Phone string in one of the supported format
	@return {String} return Phone string as per XTN data type
*/
function convertPhoneXTN(phone) {

	phone = phone.replace(/\+/g,"");

	// XTN data type example: ^^PH^^^734^6777777

	//	    916-3000		\d{3}-\d{4}			^^PH^^^^9163000
	//	    9163000		\d{7}				^^PH^^^^9163000
	//   972-9163000		\d{3}-\d{7}			^^PH^^^972^9163000
	//   972-916-3000  		\d{3}-\d{3}-\d{4}		^^PH^^^972^9163000
	// 1-972-9163000 		\d{1}-\d{3}-\d{7}		^^PH^^1^972^9163000
	// 1-972-916-3000 		\d{1}-\d{3}-\d{3}-\d{4}	^^PH^^1^972^9163000

	var arrPhone = phone.split("-");
	if (arrPhone.length <= 1) return "^^^^^^^^" + phone;

	// 1-972-9163000
	if (phone.match(/\d{1}-\d{3}-\d{7}/) != null) {
		// ^^PH^^1^734^6777777
		return "^^PH^^" + arrPhone[0] + "^" + arrPhone[1] + "^" + arrPhone[2];

	// 1-972-916-3000	
	} else if (phone.match(/\d{1}-\d{3}-\d{3}-\d{4}/) != null) {
		// ^^PH^^1^734^6777777
		return "^^PH^^" + arrPhone[0] + "^" + arrPhone[1] + "^" + arrPhone[2] + arrPhone[3];

	// 972-9163000	
	} else if (phone.match(/\d{3}-\d{7}/) != null) {
		// ^^PH^^^734^6777777
		return "^^PH^^^" + arrPhone[0] + "^" + arrPhone[1];

	// 972-916-3000
	} else if (phone.match(/\d{3}-\d{3}-\d{4}/) != null) {
		// ^^PH^^^972^9163000
		return "^^PH^^^" + arrPhone[0] + "^" + arrPhone[1] + arrPhone[2];

	// 9163000
	} else if (phone.match(/\d{7}/) != null) {
		// ^^PH^^^^9163000
		return "^^PH^^^^" + arrPhone[0];

	// 916-3000
	} else if (phone.match(/\d{3}-\d{4}/) != null) {
		// ^^PH^^^^9163000
		return "^^PH^^^^" + arrPhone[0] + arrPhone[1];

	} else
		return "^^^^^^^^" + phone; 

}

/**
	Creates OperationOutcome resource to inform about validation errors

	@param {Array} error - A list of errors or comments
	@param {Object} issue - An object with severity, code and details associated with outcome
	@param {Object} messages - An object with inbounb and outbound messages to be logged
	@param {String} uuid - A string with uuid to pair request and response FHIR feeds	
	@param {String} format - A resource output format (xml | json)	
	@return {String} outcome - OperationOutcome resource in required format
*/
function getOperationOutcome(error, issue, messages, uuid, format) {

	// Expected issue object format:
	// var issue = {severity: "", code: "", details: ""};

	// Expected messages object format:
	// var messages = {request: "", resp: ""};

	// Namespaces are required to validate against FHIR XML Schema
	if (format.toUpperCase() != "JSON") 
		default xml namespace = "http://hl7.org/fhir";

	var outcome = <OperationOutcome>
				<id value="responsestatus"/>
				<extension url="request">
					<valueBase64Binary value=""/>
				</extension>
				<extension url="response">
					<valueBase64Binary value=""/>
				</extension>
				<issue>
					<severity value=""/>
					<code value=""/>
					<details>
						<text value=""/>
					</details>
				</issue>
			</OperationOutcome>;

	if (uuid != null && uuid != undefined && uuid.toString() != "") 
		outcome.id.@value = "responsestatus" + uuid;

	outcome.issue.severity.@value = issue.severity;
	outcome.issue.code.@value = issue.code;
	outcome.issue.details.text.@value = issue.details;

	// xhtml namespace is intentionally removed
	var text = <text>
			    	<status value="generated"/>
			    	<div/>
			  </text>;

	text.div.* = new XMLList();
	error.forEach(function(n){
	  text.div.* += new XML("<p>" + n +"</p>");
	});

	outcome.id += text;

	// If request or response messages are available, encode them
	if (messages.request != undefined && messages.request.toString() != "") 
		outcome.extension.(@url == "request")[0].valueBase64Binary.@value = getBase64Encoded(messages.request.toString());	
	else
		delete outcome.extension.(@url == "request")[0];


	if (messages.resp != undefined && messages.resp.toString() != "") 	
		outcome.extension.(@url == "response")[0].valueBase64Binary.@value = getBase64Encoded(messages.resp.toString());
	else
		delete outcome.extension.(@url == "response")[0];

	// Convert to a requested format
	if (format.toUpperCase() == "JSON") {
		return convertXMLtoJSON(outcome.toString());		
	} else {
		// Namespace is required to validate FHIR XML
		outcome = outcome.toString().replace('<div>', '<div  xmlns="http://www.w3.org/1999/xhtml">');
		return outcome.toString();
	}
}

/**
	Returns a new E4X node where the order of all siblings and descendants have been fixed as per the Mirth HL7 dot notation convention.
	@param {String} node - The node to be fixed
	@return {String} return - E4X node
*/
/*
	Author: Nick Rupley
	Date Modified: 4/18/2012
*/
function fixHL7NodeOrder(node) {
	// Create output node
	var newNode = new XML();
	// In case the node is an XMLList of multiple siblings, loop through each sibling
	for each (sibling in node) {
		// Create new sibling node
		var newSibling = new XML('<'+sibling.name().toString()+'/>');
		// Iterate through each child node
		for each (child in sibling.children())
			// If the child has its own children, then recursively fix the node order of the child
			if (child.hasComplexContent())
				newSibling.appendChild(fixHL7NodeOrder(child));
			// If the child doesn't have its own children, then just add the child to the new sibling node
			else
				newSibling.appendChild(child);
		// After recursively fixing all of the child nodes, now we'll fix the current node
		newNode += sortHL7Node(newSibling);
	}
	// Return the fixed node
	return newNode;
}

// Helper function for fixHL7NodeOrder
function sortHL7Node(node) {
	// If the node has no children, then there's nothing to sort
	if (node.hasSimpleContent())
		return node;
	// Create new output node
	var newNode = new XML('<'+node.name().toString()+'/>');
	// Iterate through each child in the node
	for each (child in node.children()) {
		// If the child has a QName, then we can sort on it
		if (child.name()) {
			// Get the current "index" of the child. Id est, if the QName is PID.3.1, then the index is 1
			curChildIndex = parseInt(child.name().toString().substring(child.name().toString().lastIndexOf('.')+1),10);
			// Boolean placeholder
			var inserted = false;
			// Iterate through each child currently in the NEW node
			for (var i = 0; i <= newNode.children().length()-1; i++) {
				// Get the index of the child of the new node
				loopChildIndex = parseInt(newNode.child(i).name().toString().substring(newNode.child(i).name().toString().lastIndexOf('.')+1),10);
				// If the child we want to insert has a lower index then the current child of the new node, then we're going to insert the child 
				// right before the current newNode child
				if (curChildIndex < loopChildIndex) {
					// Insert the child
					newNode.insertChildBefore(newNode.children()[i],child);
					// Set our flag, indicating that an insertion was made
					inserted = true;
					// No need to continue iteration
					break;
				}
			}
			// If no insertion was made, then the index of the child we want to insert is greater than or equal to all of the
			// indices of the children that have already been inserted in newNode. So, we'll just append the child to the end.
			if (!inserted)
				newNode.appendChild(child);
		}
	}
	// Return the sorted HL7 node
	return newNode;
}

/**
	Encodes given string into a Base64 string.

	@param {String} strData - String to be encoded
	@return {String} return Base64 encoded string
*/
function getBase64Encoded(strData) {

	try {

		if (null == strData) strData = '';
		var byteData = new Packages.java.lang.String(strData).getBytes();
		return FileUtil.encode(byteData).replaceAll('\r\n|\r|\n', '');

	} catch(err) {
		logger.error(err);
		return "";
	}
}

/**
	Retrieve code description for a given HL7v2 table (to be used for ORC, OBR and OBX segments)

	@param {String} table - HL7v2 Table code as per specification
	@param {String] value - value to convert
	@return {String} return code description
*/
function getHL7v2CodeDescription(table, value) {

	switch(table) {

		// Table 0038 - Order Status
		case "0038":
			if ("CM" === value.toString()) return "Order complete"
			else if ("IP" === value.toString()) return "Order incomplete, or contains pending items"
			else if ("CA" === value.toString()) return "Order was canceled"
			else if ("DC" === value.toString()) return "Order was discontinued"
			else if ("ER" === value.toString()) return "Error, order not found"
			else if ("HD" === value.toString()) return "Order is on hold"
			else if ("RP" === value.toString()) return "Order has been replaced"
			else if ("SC" === value.toString()) return "In process, scheduled"
			else if ("" === value.toString()) return ""
			else return "";
			break;

		// Table 0123 - Result Status
		case "0123":
			if ("F" === value.toString()) return "Final results"
			else if ("A" === value.toString()) return "Partial results"
			else if ("C" === value.toString()) return "Correction to results"
			else if ("I" === value.toString()) return "No results available. The specimen was received, but the procedure is incomplete."
			else if ("O" === value.toString()) return "Specimen not yet received"
			else if ("P" === value.toString()) return "Preliminary results"
			else if ("R" === value.toString()) return "Results stored"
			else if ("S" === value.toString()) return "No results available. The procedure is scheduled, but has not been performed."
			else if ("X" === value.toString()) return "No results available. The order was canceled."
			else return "";		
			break;

		// Table 0085 - Observation Result Status
		case "0085":
			if ("F" === value.toString()) return "Final results; can only be changed with a corrected result."
			else if ("D" === value.toString()) return "Deletes the OBX record"
			else if ("I" === value.toString()) return "Specimen in lab; results pending."
			else if ("N" === value.toString()) return "Not asked; used to affirmatively document that the observation identified in the OBX was not sought when the universal service ID in OBR.04 implies that it would be sought."
			else if ("O" === value.toString()) return "Order detail description only (no result)"
			else if ("P" === value.toString()) return "Preliminary results"
			else if ("R" === value.toString()) return "Results entered but not verified"
			else if ("X" === value.toString()) return "Results cannot be obtained for this observation"
			else if ("U" === value.toString()) return "Results status change to final without retransmitting results already sent as preliminary. For example, radiology changes status from preliminary to final."
			else if ("W" === value.toString()) return "Post original as wrong. For example, transmitted for wrong patient."
			else return "";
			break;
	}

	return "";
}

/**
	Builds NTE segment with the comment entry from a single /text/div/p resource element
	Expected comment entry object is {"class":"","content":""}

	// TODO: may require a flag to NOT restrict comment line longer than 60 chars as required for the Order message

	@param {String} entry - JSON object with comment entries
	@param {String} count - total NTE segment count i.e., NTE.1 [Set ID]	
	@return {String} return NTE segment string in XML format
*/
function getNTESegment(entry, count) {

	if (entry != undefined) {

		var nte = <NTE>
					<NTE.1>
						<NTE.1.1></NTE.1.1>
					</NTE.1>
					<NTE.2>
						<NTE.2.1></NTE.2.1>
					</NTE.2>
					<NTE.3>
						<NTE.3.1></NTE.3.1>
					</NTE.3>
				</NTE>;

		// NTE.1 [Set ID]		
		nte['NTE.1']['NTE.1.1'] = count;

		// NTE.2 [Source of Comment]
		nte['NTE.2']['NTE.2.1'] = entry.class.toString();

		// NTE.3 [Comment]
		// Comment shall be no longer than 60 characters for the OML_O21 Submit Order message
		if (entry.content.toString().length > 60)
			entry.content = entry.content.toString().substring(0,60);

		nte['NTE.3']['NTE.3.1'] = entry.content.toString();

		return nte.toString();

	} else 
		return "";

}

/**
	Add the HTTP response status code description to the HTTP code

	@param {Integer} code - HTTP response status code
	@return {String} return HTTP response status code and its description
*/
function getHttpCodeDescription(code) {

	var text;

	switch(code) {
		case 200:
			text = "OK";
			break;
		case 400:
			text = "The request was not properly constructed";
			break;
		case 401:
			text = "The user credentials submitted are not authorized";
			break;
		case 403:
			text = "The user name submitted does not have permission to access the Results RESTful web service";
			break;
		case 404:
			text = "The user credentials submitted are not authorized";
			break;
		case 500:
			text = "An internal server error occurred";
			break;	        
		default:
			text = "This response status code is undefined in the DEX spec";
	}

	return (code + " - " + text);

}

/**
	Process Person.name element with zero or multiple given names with possible DEX name length limitation

	@param {Object} item - JSON representation of /resource/Person/name node
	@param {Boolean} lengthLimit - If person's name limitation applies according to DEX spec (true - apply, false - does not)	
	@return {Object} return name parts object with family and given names
*/
function processPersonName(item, lengthLimit) {

	var nameParts = {family: "", given1: "", given2: ""};

	if (item.family != null && item.family != undefined) {

		// Last name of the person
		var family = item.family.value.toString();

		// If Quest's length limit applies
		if (lengthLimit) {
			// The last name can be a maximum of 20 characters. (p.201)
			if (family.length > 20) 
				family = family.substring(0, 20);
			// The entire patient name cannot exceed 24 characters (p.201)
			var limit = 24 - family.length;
		}
		nameParts.family = family;
	}

	if (item.given == null || item.given == undefined) return nameParts;

	if (item.given.length != undefined) {

		var count = 0;
		item.given.forEach(function(entry){
			var given = entry.value.toString();

			if (given.length > 0) {					
				if (0 == count) {
					// The first name is limited to the difference between the number of characters in the last name and 24.				
					if (given.length > limit && lengthLimit) given = given.substring(0, limit);
					nameParts.given1 = given;
					limit -= given.length;
				} else {
					// if the limit is exceeded, a minimum of the first character of the middle initial is always returned in the result message.
					if (given.length > limit && lengthLimit) given = given.substring(0, 1);
					nameParts.given2 = given;
				}
			}
			count++;
		});			
	} else {
		var given = item.given.value.toString();
		if (given.length > limit && lengthLimit) given = given.substring(0, limit);
		nameParts.given1 = given;
	}

	return nameParts;
}

/**
	Strip non valid XML characters from a given text string.
	CAUTION: This is not a full-fledged function for XML input sanitization 

	@param {String} text - text string that may contain illegal XML characters
	@return {String} return text string with illegal XML characters replaced
*/
function stripNonValidXMLChars(text) {

	if (text == null || text == undefined || text.toString().length == 0) return "";

	//text = comment.replace(/&/g,"&amp;");// ampersand - (SN) temporary disabled
	text = text.replace(/</g,"&lt;"); // less than
	text = text.replace(/>/g,"&gt;"); // greater than
	text = text.replace(/'/g,"&apos;"); // single quotes
	text = text.replace(/"/g,"&apos;"); // double quotes

	// (SN) Issue #6, #11: unexpected "%" in field
	text = text.replace(/\|%/g, "|");

	return text;
}

/**
	Verifies if the submitted Lab test code is in the list of STD lab tests

	@param {String} code - Lab test code
	@return {String} return true if Lab test code in the list of STD codes, false otherwise
*/
function isSTDLabTest(code) {

	// Check for LOINC codes
	switch(code) {
		// 86631, 86632 Chlamydia trachomatis Antibodies 
		case "1833274":
		case "1832970":
		case "1833579":
		case "44079-2":
		// 87591 Neisseria gonorrhoeae
		case "43305-2":
		// 86704	Hepatitis A
		case "13951-9":
		// 87340	Hepatitis B
		case "1203841":
		case "2193342":
		case "10900-9":
		case "13952-7":
		// 81001	Hepatitis C (Urinalysis, Complete)
		case "13955-0":
		case "48159-8":
		// 86695	Herpes Simplex Virus 1 (IgG)
		case "1207706":
		// 86696	Herpes Simplex Virus 2 (IgG)
		case "1208621":
		// 86592	VDRL, Serum (Syphilis)
		case "1239118":
		// 86701	HIV-1 Antibody
		case "29893-5":
		// 86702	HIV-2 Antibody
		case "30361-0":
		// 87389	HIV-1/2 Antig. and Antib.,4th Gen
		case "56888-1":
			return true;
			break;
	}

	return false;

}
